INSERT INTO `articles` VALUES (1, '玉米锈病', 18, 'para', '病状： 普通锈病可发生在玉米植株上的各个部位，但主要发生在叶片上。');
INSERT INTO `articles` VALUES (1, '玉米锈病', 19, 'para', '在受害部位初形成乳白色、淡黄色，后变黄褐色乃至红褐色的夏孢子堆，夏\n\n\n\n');
INSERT INTO `articles` VALUES (1, '玉米锈病', 20, 'para', '孢子堆在叶两面散生或聚生，椭圆或长椭圆形，隆起，表皮破裂散出锈粉状');
INSERT INTO `articles` VALUES (1, '玉米锈病', 21, 'para', '夏孢子，呈黄褐色至红褐色。后期在叶两面形成冬孢子堆，长椭圆形，初埋，后突破表皮呈黑色，长1~ 2毫米，有时多个冬孢子堆汇合连片，使叶片提早枯死。');
