INSERT INTO `articles` VALUES (1, '玉米锈病', 26, 'para', '孔。冬孢子长椭圆形或椭圆形，栗褐色，顶端圆，少数扁平，表面光滑，具');
INSERT INTO `articles` VALUES (1, '玉米锈病', 27, 'para', '1个隔膜，隔膜处稍缢缩，大小28~46微米×14~25微米。柄淡黄色至淡褐色，');
