INSERT INTO `articles` VALUES (1, '玉米锈病', 32, 'para', '在广西、云南、贵州等南方各省、自治区，由于冬季气温较高，夏孢子可以');
INSERT INTO `articles` VALUES (1, '玉米锈病', 33, 'para', '在当地越冬，并成为当地第二年的初侵染菌源，但在甘肃、陕西、河北、山');
INSERT INTO `articles` VALUES (1, '玉米锈病', 34, 'para', '东等北方省份，由于冬季寒冷，夏孢子和冬孢子能否安全越冬尚存在争议，');
INSERT INTO `articles` VALUES (1, '玉米锈病', 35, 'para', '且也未发现酢浆草与玉米锈病的初侵染存在联系。总之，在北方玉米锈病发\n\n\n\n');
INSERT INTO `articles` VALUES (1, '玉米锈病', 36, 'para', '生的初侵染菌源主要是南方玉米锈病菌的夏孢子随季风和气流传播而来的。');
INSERT INTO `articles` VALUES (1, '玉米锈病', 37, 'para', '普遍锈病在相对较低的气温(16~23℃)和经常降雨、相对湿度较高(100%)的');
INSERT INTO `articles` VALUES (1, '玉米锈病', 38, 'para', '条件下，易于发生和流行。在我国西南山区玉米锈病正是在这样的条件下普');
INSERT INTO `articles` VALUES (1, '玉米锈病', 39, 'para', '遍发生的。据国外报道，玉为普通锈病的夏孢子堆和冬孢子堆阶段也可发生');
INSERT INTO `articles` VALUES (1, '玉米锈病', 40, 'para', '于大刍草上。实践证明，偏施氮肥有利于玉米锈病的发生。不同玉米品种和');
INSERT INTO `articles` VALUES (1, '玉米锈病', 41, 'para', '品系对玉米锈病存在明显的抗性差异，马齿型较抗病，甜质型玉米则抗病性');
INSERT INTO `articles` VALUES (1, '玉米锈病', 42, 'para', '较差，生育期短的早熟品种发病较重。');
INSERT INTO `articles` VALUES (1, '玉米锈病', 43, 'para', '防治：玉米锈病是一种气流传播的大区域发生和流行的病害、防治上必\n\n\n\n');
INSERT INTO `articles` VALUES (1, '玉米锈病', 44, 'para', '须采取以抗病品种为主、以栽培防病和药剂防治为辅的综合防治措施。病重');
INSERT INTO `articles` VALUES (1, '玉米锈病', 45, 'para', '地区应更换抗病品种；适时播种，合理密植，避免偏施氮肥，搭配使用磷钾');
INSERT INTO `articles` VALUES (1, '玉米锈病', 46, 'para', '肥。发病初期要及时喷药防治，有效药剂有65%代森锌500倍液，50 %代森铵');
INSERT INTO `articles` VALUES (1, '玉米锈病', 47, 'para', '水剂800~ 1000倍液，0.2波美度的石硫合剂，25%粉锈宁可湿性粉剂1000~');
INSERT INTO `articles` VALUES (1, '玉米锈病', 48, 'para', '1500倍液。');
INSERT INTO `articles` VALUES (2, '白粉病', 0, 'title', '白粉病');
INSERT INTO `articles` VALUES (2, '白粉病', 0, 'summary', '白粉病，自幼苗到抽穗均可发病。主要危害叶片，也危害茎和穗子。在叶片上开始产生黄色小点，而后扩大发展成圆形或椭圆形病斑，表面生有白色粉状霉层。一般情况下部叶片比上部叶片多，叶片背面比正面多。霉斑早期单独分散，后联合成一个大霉斑，甚至可以覆盖全叶，严重影响光合作用，使正常新陈代谢受到干扰，造成早衰，产量受到损失。');
INSERT INTO `articles` VALUES (2, '白粉病', 1, 'level-3', '病害分布状况');
INSERT INTO `articles` VALUES (2, '白粉病', 2, 'para', '麦类白粉病近年来在江苏、浙江、湖北、河南、山东、贵州、四川等地发生较为普遍，为害日趋严重。由麦类白粉菌(Erysiphe graminis D.Cf.sp. tritici E.Marchal.)引起，主要为害小麦、大麦、黑麦、燕麦等\n[1] \n。');
INSERT INTO `articles` VALUES (2, '白粉病', 3, 'level-3', '发病规律');
INSERT INTO `articles` VALUES (2, '白粉病', 4, 'para', '病菌在河南省平原地区不能越夏，但子囊孢子或分生孢子可在海拔500米以上山区的自生麦苗或夏麦上发生为害，秋后通过气流传到平原麦苗上引起发病。');
INSERT INTO `articles` VALUES (2, '白粉病', 5, 'para', '粉病流行的条件主要有两个，一是大面积种植感病品种，二是适宜的环境条件。一般在小麦密度偏大、施氮肥过量的情况下，麦株旺长，植株衰弱，田间湿度大或者发生了倒伏的麦田，发病往往较重。该病一般在3月底至4月初出现发病中心，4月中旬后随气温逐渐回升，病株率迅速增加，在适宜的条件下导致大流行。');
INSERT INTO `articles` VALUES (2, '白粉病', 6, 'level-2', '苗木与花卉');
INSERT INTO `articles` VALUES (2, '白粉病', 7, 'level-3', '症状与危害');
INSERT INTO `articles` VALUES (2, '白粉病', 8, 'para', '白粉病发生在叶、嫩茎、花柄及花蕾、花瓣等部位，初期为黄绿色不规则小斑，边缘不明显。随后病斑不断扩大，表面生出白粉斑，最后该处长出无数黑点。染病部位变成灰色，连片覆盖其表面，边缘不清晰，呈污白色或淡灰白色。受害严重时叶片皱缩变小，嫩梢扭曲畸形，花芽不开。\n[2] \n');
INSERT INTO `articles` VALUES (2, '白粉病', 9, 'level-3', '防治方法');
INSERT INTO `articles` VALUES (2, '白粉病', 10, 'para', 'a、农业防治');
INSERT INTO `articles` VALUES (2, '白粉病', 11, 'para', '（1）选择抗病品种\n\n\n\n\n得白粉病的瓜叶\n\n');
INSERT INTO `articles` VALUES (2, '白粉病', 12, 'para', '（2）在购入苗木时要严格剔除染病株，杜绝病源。');
INSERT INTO `articles` VALUES (2, '白粉病', 13, 'para', '（3）进行扩繁时，要剪取无病虫插枝或根蘖作为无性繁殖材料。');
INSERT INTO `articles` VALUES (2, '白粉病', 14, 'para', '（4）苗木出圃时，要进行施药防治，严防带病苗木传入新区。');
INSERT INTO `articles` VALUES (2, '白粉病', 15, 'para', '（5）轮作与非寄主花木轮作2至3年，以减少病源。彻底清洁苗圃，扫除枯枝落叶，剪去病虫枝集中销毁。生长期间及时摘除染病枝叶，彻底清除落叶，剪去病虫枝和中下部过密枝，集中销毁。不宜种植过密，棚室加强通风换气，以降低湿度。及时排除田间和花盆积水，浇水不宜多，从盆边浇水，不使茎叶淋水，减少病菌传播和发病机会。增施磷钾肥，少施氮肥，使植株生长健壮，多施充分腐熟的有机肥，以增强植株的抗病性。');
INSERT INTO `articles` VALUES (2, '白粉病', 16, 'para', '（6）预防大棚内花木发病大棚育苗种植前，彻底清除棚内所有植物，清扫棚室，用药物熏烟等手段严格消毒。严防病苗入室，棚内尽量种植单一花木品种，避免混植，以防交叉传染。早春露地花木萌芽前，彻底销毁棚内病株后，才能开棚，以防病菌孢子传播到棚外。');
INSERT INTO `articles` VALUES (2, '白粉病', 17, 'para', 'b、药剂防治：');
INSERT INTO `articles` VALUES (2, '白粉病', 18, 'para', '（1） 越冬期用波美3至5度的石硫合剂稀释液喷或涂枝干。注意，瓜叶菊等易受药害的花卉不能施用。地面喷硫磺粉，一般每70平方米使用25克至30克，消灭越冬菌源。');
INSERT INTO `articles` VALUES (2, '白粉病', 19, 'para', '（2）生长期在发病前可喷保护剂，发病后宜喷内吸剂，根据发病症状，花木生长和气候情况及农药的特性，间隔5至20天施药一次，连施2至 5次。一季花木，一种内吸剂只能施1至2次。要经常更换农药种类，避免病菌产生抗药性。');
INSERT INTO `articles` VALUES (2, '白粉病', 20, 'para', '（3）病害盛发时，可喷15%粉锈宁1000倍液、2%抗霉菌素水剂200倍液、10%多抗霉素1000至1500倍液。传统药物因反复使用产生使病菌产生抗体，效果锐减，故提倡交替使用。另外也可用白酒（酒精含量35%）1000倍液，每3至6天喷一次，连续喷3至6次，冲洗叶片到无白粉为止。');
INSERT INTO `articles` VALUES (2, '白粉病', 21, 'para', '（4）种植在大棚、温室等保护地内的花木，除了喷上述各种药剂外，还可施熏烟剂和粉尘剂。种植前或结束后要进行空棚消毒，每100平方米用硫磺粉250克、木屑500克，均匀混合，分成10堆，用烧红的煤球或木炭点燃，不能用明火燃烧，关棚一昼夜。在熏蒸过程中要严防火灾。花木生长期熏烟易发生药害，应慎用。植物生长期在发病前用45%百菌清烟剂。');
INSERT INTO `articles` VALUES (2, '白粉病', 22, 'para', '（5）喷布高脂膜（乳剂）200倍液\n[3] \n对于预防白粉病发生和治疗初期白粉病斑都具有良好效果。');
INSERT INTO `articles` VALUES (2, '白粉病', 23, 'level-2', '甜瓜');
INSERT INTO `articles` VALUES (2, '白粉病', 24, 'para', '1、危害\n[4] \n与症状');
INSERT INTO `articles` VALUES (2, '白粉病', 25, 'para', '主要为害甜瓜叶片。初发生时叶片产生黄色小点，而后扩大发展成圆形或椭圆形病斑，表面生有白色粉状霉层，为病菌的分生孢子梗和分生孢子，后病斑变黄褐色为病菌的闭囊壳。一般情况下部叶片比上部叶片多，叶片背面比正面多。霉斑早期单独分散，后联合成一个大霉斑，甚至可以覆盖全叶，严重影响光合作用，使正常新陈代谢受到干扰，造成早衰，产量受到损失。');
INSERT INTO `articles` VALUES (2, '白粉病', 26, 'para', '2、防治方法');
INSERT INTO `articles` VALUES (2, '白粉病', 27, 'para', '化学防治：');
INSERT INTO `articles` VALUES (2, '白粉病', 28, 'para', '（1）每20-30ml乙嘧酚醚菌酯升级精准复配(控白)加水15公斤。每7天喷药一次，喷药2～3次。该药在白粉病单独发生地块或同时轻微发生霜霉病的地块采用。 注意在配药时不能和其它任何农药和化肥混配，配药要用清水，渠水要经过沉淀后才可配药。');
INSERT INTO `articles` VALUES (2, '白粉病', 29, 'para', '（3）80%硫磺400倍加72%苯噻霜脲氰600倍喷雾,，150克硫磺加100克苯噻霜脲氰加水60公斤，同时防治白粉病和霜霉病。');
INSERT INTO `articles` VALUES (2, '白粉病', 30, 'para', '（4）三唑类杀菌剂，如腈菌唑、氟菌唑等\n[5] \n。');
INSERT INTO `articles` VALUES (2, '白粉病', 31, 'level-2', '黄瓜的防治');
INSERT INTO `articles` VALUES (2, '白粉病', 32, 'para', '黄瓜白粉病白粉病，俗称白毛，系常发性病害，是黄瓜中后期的主要病害之一\n[6] \n。');
INSERT INTO `articles` VALUES (2, '白粉病', 33, 'level-3', '症状与诊断');
INSERT INTO `articles` VALUES (2, '白粉病', 34, 'para', '先在下部叶片正面或背面长出小圆形白粉状霉斑，逐渐扩大，厚密，不久连成一片。发病后期整个叶片布满白粉，菌丝老熟后变灰白色，最后叶片呈黄褐色干枯。茎和叶柄上也产生与叶片类似病斑，密生白粉霉斑。在秋天，有时在病班上产生黄褐色小粒点，后变黑色，即有性世代的子囊壳。此病在叶片布满白粉，发病初期霉层下部表皮仍保持绿色，与其它叶部病害容易区别。');
INSERT INTO `articles` VALUES (2, '白粉病', 35, 'level-3', '发病规律');
INSERT INTO `articles` VALUES (2, '白粉病', 36, 'para', '以闭囊壳随病残体留在地面上越冬。南部地区和温室中病菌以菌丝体在病株活体上越冬。子囊孢子和分生孢子主要以气流传播，萌发产生出蚜管直接侵入寄主体内。田间相对湿度大，温度在16—24℃， 此病易流行。密度过大，光照不足，氮肥过多，徒长苗易发病。');
INSERT INTO `articles` VALUES (2, '白粉病', 37, 'level-3', '防治措施');
INSERT INTO `articles` VALUES (2, '白粉病', 38, 'para', '（1）栽培抗病品种。一般抗霜霉病的品种也抗白粉病，栽培上可选择叶片厚实的品种。');
INSERT INTO `articles` VALUES (2, '白粉病', 39, 'para', '（2）种子处理：播种前先在阳光下晒种2-3天天，以杀灭表皮杂菌，随后用用55℃温水浸种15分钟，温度下降至常温后继续浸种4-6小时， 再做消毒处理，播种前用新高脂膜拌种，驱避地下病虫，加强呼吸强度，提高种子发芽率。');
INSERT INTO `articles` VALUES (2, '白粉病', 40, 'para', '（3）加强栽培及肥水管理，增施磷、钾肥，随时保持土壤湿润，在开花前、幼果期、果实膨大期各喷洒壮瓜蒂灵使瓜蒂增粗，强化营养输送量，增强植株抗逆性，促进瓜体快速发育，使瓜型漂亮，汁多味美。');
INSERT INTO `articles` VALUES (2, '白粉病', 41, 'para', '（4）物理防治：当发现中心病株时，要及时喷洒药剂加新高脂膜形成保护膜，隔离病源菌，防止病菌借风雨再次传播感染。');
INSERT INTO `articles` VALUES (3, '柑桔黄龙病', 0, 'title', '柑桔黄龙病');
INSERT INTO `articles` VALUES (3, '柑桔黄龙病', 0, 'summary', '柑桔黄龙病又称黄梢病、黄枯病、青果病。该病70年代在广东爆发流行，80年代传入闽南、福州一带，对柑桔造成极大危害，管理差、抛荒的柑桔园发生较重，如任其蔓延，将给我国柑桔产业造成死树毁园的致命打击。');
INSERT INTO `articles` VALUES (3, '柑桔黄龙病', 1, 'level-2', '病源');
INSERT INTO `articles` VALUES (3, '柑桔黄龙病', 2, 'para', '革兰氏阴性细菌，学名Rickettsiae-like organism gloeosporioides Penz 简称RLO。病原是Proteobacteria 纲中韧皮部杆菌属，其形态与螺原体及菌原体不同。');
INSERT INTO `articles` VALUES (3, '柑桔黄龙病', 3, 'para', '在寄主筛管细胞和薄壁细胞内分布不均匀，故同一株树各枝梢发病时间也呈现不同。病菌只侵染柑桔属、金柑属和枳属植物。');
INSERT INTO `articles` VALUES (3, '柑桔黄龙病', 4, 'para', '\n\n\n\n');
INSERT INTO `articles` VALUES (3, '柑桔黄龙病', 5, 'level-2', '病害类型');
INSERT INTO `articles` VALUES (3, '柑桔黄龙病', 6, 'para', '细菌');
INSERT INTO `articles` VALUES (3, '柑桔黄龙病', 7, 'para', '亚洲菌系：Liberobacter asiatum (亚洲柑桔黄龙病）');
INSERT INTO `articles` VALUES (3, '柑桔黄龙病', 8, 'para', '非洲菌系：Liberobacter africanum(非洲柑桔黄龙病）');
INSERT INTO `articles` VALUES (3, '柑桔黄龙病', 9, 'level-2', '主要危害作物');
INSERT INTO `articles` VALUES (3, '柑桔黄龙病', 10, 'para', '柑桔属、金柑属和枳属植物，柑、橘、橙、柠檬和柚类均可感病。尤其以椪柑、蕉柑、福橘、茶枝柑等品种的耐病力弱，感病后衰退快。橙类耐病力较强。感病不受树龄大小的限制，但幼龄树比老龄树根容易感病。');
INSERT INTO `articles` VALUES (3, '柑桔黄龙病', 11, 'level-2', '主要危害部位');
INSERT INTO `articles` VALUES (3, '柑桔黄龙病', 12, 'para', '得了黄龙病的柑橘全株系统性病害, 根、树干、枝、叶、花、果都有病原, 特别是根部, 药剂很难达到, 尚未找到好的给药方法和途径, 也没有研制出特效的农药。');
INSERT INTO `articles` VALUES (3, '柑桔黄龙病', 13, 'level-2', '危害症状');
INSERT INTO `articles` VALUES (3, '柑桔黄龙病', 14, 'para', '柑橘类植物感染黄龙病菌后并不立即显症，存在潜伏期。潜伏期的长短因品种、树龄、健康状况、种植环境等而异但相比其他病原体，黄龙病菌的潜伏期较长。');
INSERT INTO `articles` VALUES (3, '柑桔黄龙病', 15, 'para', '果实的典型症状是着色异常、果小、畸形。染病沙糖橘等宽皮柑橘的果实常表现为果蒂和果肩周围先褪绿转色，其它部位仍然为青绿色，且因蜡质形成受阻而无光泽。果农称之为“红鼻子果”或“红肩果”。染病甜橙树的果实常表现为青果，病果的果汁味酸、淡，果实中心柱不正。');
INSERT INTO `articles` VALUES (3, '柑桔黄龙病', 16, 'para', '叶片的典型症状是黄化、变小。据病程长短，叶片黄化有两种主要类型，一种是斑驳型黄化，一种是均匀黄化。染病早期，叶片常显现为斑驳型黄化，待病菌散播到全树后，新梢抽生的叶片一般为均匀黄化。');
INSERT INTO `articles` VALUES (3, '柑桔黄龙病', 17, 'para', '病根表皮易脱离、腐烂等，黄龙病菌优先定殖在根中且分布较为均匀，这也是为什么仅仅砍除病枝并不能有效控制黄龙病的原因所在。');
INSERT INTO `articles` VALUES (3, '柑桔黄龙病', 18, 'level-2', '传播途径');
INSERT INTO `articles` VALUES (3, '柑桔黄龙病', 19, 'para', '该病通过苗木、接穗和木虱传播。发生与气候条件、栽培管理及品种有关。5月下旬开始发病，8－9月最严重。春、夏季多雨，秋季干旱时发病重；施肥不足，低洼果园排水不良，树冠郁闭，发病重。');
INSERT INTO `articles` VALUES (3, '柑桔黄龙病', 20, 'level-2', '发病条件');
INSERT INTO `articles` VALUES (3, '柑桔黄龙病', 21, 'para', '黄龙病是不会通过土壤、流水、大风、修剪、其他昆虫及动物来传播感染。所以果园只有没有木虱，那么黄龙病就不会传播开来。同一品种中，幼龄树较老龄树抗病，4－8年生的树发病重。不同品种抗病力为芦柑、福桔<甜橙类<温州蜜柑<柚类<杂柑类 <金柑类<枳壳。地理条件也有关系，平地、河谷、溪边果园发病重于山垅果园重于山地果园。');
INSERT INTO `articles` VALUES (3, '柑桔黄龙病', 22, 'level-2', '防治方法');
INSERT INTO `articles` VALUES (3, '柑桔黄龙病', 23, 'para', '1 、建立认识：黄龙病为第一重要病害、柑桔木虱为第一重要害虫。');
INSERT INTO `articles` VALUES (3, '柑桔黄龙病', 24, 'para', '2、先喷药，后抹梢、锯病树。采果后先喷有效药剂防木虱, 药后再锯断病树（对锯断的病树桩要用大剂量除草剂灌注处理，之后加盖黑色薄膜，防止其再生）,避免将病树上带病原菌的木虱成虫驱赶到健康树上,造成人为传播。当前生产上普遍是先砍树后喷药杀木虱或根本就不杀木虱。');
INSERT INTO `articles` VALUES (3, '柑桔黄龙病', 25, 'para', '木虱控制方法是化学防治。喷施矿物油，亲水颗粒薄膜悬浮液吡虫啉、涕灭威等可以较好地扑杀木虱；喷施保幼激素类似物吡丙醚、喷施牧荆、菖蒲等植物的萃取物可以防治亚洲木虱。');
INSERT INTO `articles` VALUES (3, '柑桔黄龙病', 26, 'para', '越冬后的木虱成虫要防好: 柑桔木虱以成虫越冬,系决定当年种群数量多少重要因素之一, 也是带病原菌较多的时期, 所以早春第一次药一定要加入防木虱成虫的农药。');
INSERT INTO `articles` VALUES (3, '柑桔黄龙病', 27, 'para', '很多果农在防治木虱的时间选取上漏掉了两个最重要的关键点：晚秋梢、冬梢期以及春梢萌芽前的越冬期，前者是木虱发生高峰期，后者则是木虱年虫口密度最低、喷药防治效果最好的时期。');
INSERT INTO `articles` VALUES (3, '柑桔黄龙病', 28, 'para', '在春梢萌芽前，原本是一年中防治木虱最好的时机（实验证明此时若杀死一头木虱成虫，相当于夏、秋梢期杀死20～200头成虫），但此时木虱极难见到，且其他害虫数量也少，因此果农容易忽视。');
INSERT INTO `articles` VALUES (3, '柑桔黄龙病', 29, 'para', '第一个关键期:砍病树前黄龙病树前必须先喷药杀灭病树上的木虱成虫和若虫,避免将带病木虱成虫驱赶到健康树上。可选用对温度不敏感的杀虫杀螨剂26%甲氰菊酯+水胺硫磷乳油1000倍液或22%甲氰菊酯十三唑磷乳油1000倍液。');
INSERT INTO `articles` VALUES (3, '柑桔黄龙病', 30, 'para', '第二个关键期:春梢萌芽前。杀灭越冬后的木虱成虫,减少当年的虫口基数。可选用低温期可用的杀虫杀螨剂22%甲氰菊酯+三唑磷乳油1000倍液。');
INSERT INTO `articles` VALUES (3, '柑桔黄龙病', 31, 'para', '第三个关键期:春梢期。选用既能有效防治柑桔木虱成虫、若虫,又能防治蚜虫、粉虱的药剂。可选用20%异丙威十唑虫琳乳油1000倍液或2.5%增效联苯菊酯1500一2000倍液。');
INSERT INTO `articles` VALUES (3, '柑桔黄龙病', 32, 'para', '第四个关键期:夏梢期。在4月下旬至5月上中旬结合防治第一代矢尖蚊时使用能有效防治木虱的杀虫剂。可选用51.5%毒死蜱+高效氯氰菊酯乳油800倍液。');
INSERT INTO `articles` VALUES (3, '柑桔黄龙病', 33, 'para', '第五个关键期:秋梢期。秋梢期选用既能有效防治柑桔木虱成虫、若虫,又能有效防治柑桔潜叶蛾、锈壁虱的药剂,可选用10%毗虫琳可湿性粉剂1000倍+20%丁硫克百威乳油1500倍液。');
INSERT INTO `articles` VALUES (3, '柑桔黄龙病', 34, 'para', '第六个关键期:晚秋梢期。9月上、中旬抽出的晚秋梢或9月下旬至1月抽出的冬梢,在抹梢前必须先喷药,可选用20%异丙威+吡虫琳乳油1000倍液或2.5%增效联苯菊酯1500-2000倍。');
INSERT INTO `articles` VALUES (3, '柑桔黄龙病', 35, 'para', '喷药方法二：');
INSERT INTO `articles` VALUES (3, '柑桔黄龙病', 36, 'para', '①在春梢萌发 2 ～ 5 mm，使用 40% 毒死蜱800 倍或敌介灵、介虱灵乳油 1 000 倍或 40% 丙溴磷 1 000 倍 + 螨危 5 000 倍 +10% 苯醚甲环唑 1 500倍防治越冬木虱成虫、红蜘蛛和疮痂病 1 ～2 次（第1 次 3 月上旬末，第 2 次 4 月中旬末）。');
INSERT INTO `articles` VALUES (3, '柑桔黄龙病', 37, 'para', '②夏梢期，使用 40% 毒死蜱 1 500 倍或 40%杀扑磷 1 000 倍或 1.8% 阿维菌素 1 500 倍 + 螨危5 000 倍 +10% 苯醚甲环唑 1 500 倍防治红蜘蛛、木虱、蚧壳虫、蝽象、潜叶甲、沙皮病和炭疽病等病虫 2 ～ 3 次（第 1 次 5 月下旬，第 2 次 6 月中旬，第 3 次 7 月上旬）。');
INSERT INTO `articles` VALUES (3, '柑桔黄龙病', 38, 'para', '③秋梢期，使用螨危 5 000 倍 +1.8% 阿维菌素1 500 倍或 10% 吡虫啉 1 500 倍或 20% 单甲脒 800倍 +10% 苯醚甲环唑 1 500 倍防治红蜘蛛、木虱、锈壁虱、沙皮病和炭疽病等病虫 1 ～ 2 次（第 1 次8 月下旬，第 2 次 9 月下旬）。');
INSERT INTO `articles` VALUES (3, '柑桔黄龙病', 39, 'para', '喷药参考：');
INSERT INTO `articles` VALUES (3, '柑桔黄龙病', 40, 'para', '分别在春梢萌发前、春梢期、夏梢期（5月上旬）、秋梢期（8月）、晚秋梢、冬梢期（9月下旬~11月）(喷2次，9月初1次，10月初1次)、砍病树前（12月）喷药。');
INSERT INTO `articles` VALUES (3, '柑桔黄龙病', 41, 'para', '所用药剂：2006年：1）春梢萌发前:22%奥杀螨 1000 倍+螨泯灭 1500 倍：防治越冬木虱成虫、红蜘蛛、粉虱等；2）春梢期：花蕾露白期用26%虫螨毙 1000倍防治春梢期木虱、花蕾蛆，兼治蚜虫、粉虱；3）夏梢期（5月上旬）：51.5%农敌落 1000倍防治夏梢期木虱、');
INSERT INTO `articles` VALUES (3, '柑桔黄龙病', 42, 'para', '第一代矢尖蚧幼蚧兼治粉虱、蚜虫等；4）秋梢期（8月）：用10%吡虫啉 WP1000倍+19%锈潜净 1500倍防治秋梢期木虱、锈壁虱、潜叶蛾兼治粉虱、蝽象等；');
INSERT INTO `articles` VALUES (3, '柑桔黄龙病', 43, 'para', '5）晚秋梢、冬梢期（9 月下旬—11 月）：喷 2 次，9 月初 1');
INSERT INTO `articles` VALUES (3, '柑桔黄龙病', 44, 'para', '次：用4.5%赛得 1000倍防治晚秋梢期木虱兼治粉虱等。10月初1次：用4.5%赛得 1000倍+70%炔螨特 2000倍防治冬梢期木虱、红蜘蛛兼治粉虱等；');
INSERT INTO `articles` VALUES (3, '柑桔黄龙病', 45, 'para', '6）砍病树前（12 月—1 月）26%虫螨毙 1000 倍+机油乳剂150倍防治越冬木虱、红蜘蛛、粉虱、介壳虫等。');
INSERT INTO `articles` VALUES (3, '柑桔黄龙病', 46, 'para', '2007 年：1）春梢萌发前:22%奥杀螨 1000 倍+螨泯灭1500倍：防治越冬木虱成虫、红蜘蛛、粉虱等；');
INSERT INTO `articles` VALUES (3, '柑桔黄龙病', 47, 'para', '敌1000倍防治夏梢期木虱、第一代矢尖蚧幼蚧兼治粉虱、蚜虫等；4）秋梢期（8月）：用10%吡虫啉 WP1000 倍+19%锈潜净 1500 倍防治秋梢期木虱、锈壁虱、潜叶蛾兼治粉虱、蝽象等；5）晚秋梢、冬梢期（9月下旬—11月）：喷2次，9月初1次：用25%哒虱威 1000 倍防治晚秋梢期木虱兼治粉虱等。10 月初 1 次：用 2.5%增效联苯菊酯 2000倍+70%炔螨特 2000倍防治冬梢期木虱、红蜘蛛兼治粉虱等；6）砍病树前（12月~1月）26%虫螨毙1000倍+机油乳剂150倍防治越冬木虱、红蜘蛛、粉虱、介壳虫等。');
INSERT INTO `articles` VALUES (3, '柑桔黄龙病', 48, 'para', '2）春梢期：花蕾露白期用 26%虫螨毙 1000 倍防治春梢期木虱、花蕾蛆，兼治蚜虫、粉虱；3）夏梢期（5月上旬）：51.5%农敌落 1000倍防治夏梢期木虱、第一代矢尖蚧幼蚧兼治粉虱、蚜虫、实蝇等；4）秋梢期（8月）：用16%铁骑 2000倍+19%锈潜净 1500倍防治秋梢期木虱、锈壁虱、潜叶蛾兼治粉虱、蝽象等；5）晚秋梢、冬梢期（9月下旬—11月）：喷2次，9月初1次：用 2.5%高效氟氯氰菊酯EW 2000倍防治晚秋梢期木虱、粉虱等。10 月初 1 次：用 2.5%增效联苯菊酯 2000 倍+70%炔螨特 2000 倍防治冬梢期木虱、红蜘蛛兼治粉虱等；6）砍病树前（12月~1月）26%虫螨毙1000 倍+机油乳剂 150 倍防治越冬木虱、红蜘蛛、粉虱、介壳虫等.');
INSERT INTO `articles` VALUES (3, '柑桔黄龙病', 49, 'para', '有效防木虱药物：');
INSERT INTO `articles` VALUES (3, '柑桔黄龙病', 50, 'para', '经过防治木虱试验表明，有机磷农药与拟除虫菊酯类农药的混剂对木虱成虫的防效最好，特别是22%奥杀螨 （甲氰菊酯+三唑磷）、26%虫螨毙乳油（甲氰菊酯+水胺硫磷）、51.5%毒·高氯乳油、50%丙溴磷+甲氰菊酯乳油220mg/L、260mg/L、515mg/L、500mg/L的防效都在92%以上。');
INSERT INTO `articles` VALUES (3, '柑桔黄龙病', 51, 'para', '春梢、秋梢期木虱防治要选用有效药剂, 如: 10%吡虫啉可湿粉、4.5%赛得乳油、20%甲氰菊酯乳油、22%甲氰·唑磷乳油。');
INSERT INTO `articles` VALUES (3, '柑桔黄龙病', 52, 'para', '木虱成虫已对以前常用的敌敌畏、乐果、乙酰甲胺磷、辛硫磷等低残留有机磷农药产生严重的抗药性。');
INSERT INTO `articles` VALUES (3, '柑桔黄龙病', 53, 'para', '10%吡虫啉 1g/L 对木虱成虫药后1 天、3 天 的防效分别为78.3%、86.6%。');
INSERT INTO `articles` VALUES (3, '柑桔黄龙病', 54, 'para', '对柑桔蚜虫有特效、生产上拟大量推广使用的啶虫咪对木虱成虫、若虫的防效都很差。3%啶虫脒 1ml/L 对若虫药后1 天、3 天 的防效只有8.7%和37.0%; 对成虫药后1 天、3 天 的防效分别为-15.4%和31.1%。');
INSERT INTO `articles` VALUES (3, '柑桔黄龙病', 55, 'para', '管理意识：');
INSERT INTO `articles` VALUES (3, '柑桔黄龙病', 56, 'para', '专家以大面积实验证明，科学防治黄龙病可以基本控制黄龙病。');
INSERT INTO `articles` VALUES (3, '柑桔黄龙病', 57, 'para', '加强栽培管理, 增施有机肥。加强对介壳虫、潜叶蛾、粉虱的防治工作时可对木虱起到兼治作用; 增施有机肥, 增强树势, 可增加树体对黄龙病菌的抵抗力, 延缓发病和衰退。');
INSERT INTO `articles` VALUES (3, '柑桔黄龙病', 58, 'para', '适当高密度种植, 增加前期产量, 延长果园寿命。');
INSERT INTO `articles` VALUES (3, '柑桔黄龙病', 59, 'para', '果园附近不要种黄皮、九里香等芸香科植物, 这些植物一般一年四季都不喷药, 但又都是木虱的寄主,特别是秋季、冬季木虱成虫数量多。');
INSERT INTO `articles` VALUES (3, '柑桔黄龙病', 60, 'para', '④据广东柑桔黄龙病治理研究中心\n[1] \n最新研究发现，黄龙病病因是从树根感染病菌开始发病，治理应从根部治理，而不是传统的叶茎治理，以微生物有机菌药剂从根部治理柑桔类黄龙病是用生理解毒、生理防治和物理治理的综合方法来进行治理，“以菌治菌”抑制和杀灭土壤有害病菌优化土质，其目的是让果树吸收有益的微生物，增加肥效，促进新根生长，让树木根系更加发达，树木细胞管状更加通畅，恢复病株养份，使果树黄叶转绿、健康新稍生长。');
INSERT INTO `articles` VALUES (3, '柑桔黄龙病', 61, 'para', '⑤加强果园管理，增强树势，提高柑桔产量，争取尽早收回投资。');
INSERT INTO `articles` VALUES (3, '柑桔黄龙病', 62, 'level-2', '黄龙病症状');
INSERT INTO `articles` VALUES (3, '柑桔黄龙病', 63, 'para', '黄龙病在枝、叶、花、果及根部均可显症，尤以秋梢症状最明显。发病初期，部分新梢叶片黄化，出现“黄梢”，黄梢最初出现在树冠顶部，后渐扩展，经1－2年后全株发病，春梢症状多出现在叶片转绿后，先在叶脉基部转黄后部分叶肉褪绿，叶脉逐渐黄化，叶片现不规则黄绿斑块，且有淀粉积累现象。夏梢症状多在嫩叶期不转绿均匀黄化，叶片硬化失去光泽，似缺氮状；有的叶脉呈绿色，叶肉黄化，呈细网状，似缺铁症状；有的叶上出现不规则，边缘不明显的绿斑。老枝上的老叶也可表现黄化，多从中脉和侧脉开始变黄，叶肉变厚、硬化，叶表无光泽，叶脉肿大，有些肿大的叶脉背面破裂，似缺硼状。芦柑的叶片初期表现花叶症状。新梢上的叶片黄化不久即脱落，老枝上的病叶多在未完全变黄以前脱落。发病中期，即新梢生长后期，叶片叶脉及沿脉附近的组织变绿色；叶肉变黄，黄化轻微的似缺锰状，严重黄化的，似缺锌状。后期，新梢抽出困难，叶片症状较中期严重，大部分落叶。枝条由顶端向下枯死，病枝木质部局部或全部变为橙玫瑰色，最后全株死亡。病树翌年春季提前开花，花小畸形，结果少，果实畸形，果小似乒乓球，着色不均，果蒂附近着色，俗称“红鼻子果”，果实味酸、品质极差，是识别黄龙病的最主要典型症状。根部症状主要表现为根的腐烂，其严重程度与地上枝梢相对称。枝叶发病初期，根多不腐烂，叶片黄化脱落时，须根及支根开始腐烂，后期蔓延到侧根和主根，皮层破碎，与木质部分离');
INSERT INTO `articles` VALUES (3, '柑桔黄龙病', 64, 'level-2', '地理分布');
INSERT INTO `articles` VALUES (3, '柑桔黄龙病', 65, 'para', '我国主要病区为广东、广西和福建，在四川、云南、贵州、湖南、江西、浙江等省的局部地区有发生，台湾省的立枯病亦为黄龙病。在泰国、越南、印度、菲律宾、美国、马来西亚、印度尼西亚也为害严重，非洲的青果病也属黄龙病。');
INSERT INTO `articles` VALUES (4, '番茄斑点病', 0, 'title', '番茄斑点病');
INSERT INTO `articles` VALUES (4, '番茄斑点病', 0, 'summary', '主要为害叶片。初生绿褐色水浸状小斑点，后扩大，周缘黑褐色，中间灰褐色，大小2～3毫米，病斑圆形或近圆形，病斑周围形成不规则形黄化区，后期病斑中间穿孔，叶片黄化枯死或脱落。');
INSERT INTO `articles` VALUES (4, '番茄斑点病', 1, 'level-2', '病原形态特征');
INSERT INTO `articles` VALUES (4, '番茄斑点病', 2, 'para', '番茄匍柄霉（Stemphylium lycopersici(Enjoji)Yamamoto），属半知菌亚门真菌。分生孢子梗大小140×6～7微米，膨大部位直径8～10微米；分生孢子短锥状，具横隔膜1～8个，若干\n\n\n\n个纵隔膜，通常在3条主横隔处缢缩。光滑或具微瘤，大小50～74×16～23微米。\n[2] \n');
INSERT INTO `articles` VALUES (4, '番茄斑点病', 3, 'level-2', '传播途径');
INSERT INTO `articles` VALUES (4, '番茄斑点病', 4, 'para', '病菌以菌丝和分生孢子随病残体在土壤中越冬，翌年条件适宜时进行初侵染，发病后病部产生分生孢子借风雨传播，进行再侵染。');
INSERT INTO `articles` VALUES (4, '番茄斑点病', 5, 'level-2', '发病条件');
INSERT INTO `articles` VALUES (4, '番茄斑点病', 6, 'para', '中国露地和棚室均有发生，气温20～25℃及连续连阴雨后的多湿条件易发病。\n[2] \n');
INSERT INTO `articles` VALUES (4, '番茄斑点病', 7, 'level-2', '防治方法');
INSERT INTO `articles` VALUES (4, '番茄斑点病', 8, 'para', '基本方法');
INSERT INTO `articles` VALUES (4, '番茄斑点病', 9, 'para', '（1）采用配方施肥技术，施用酵素菌沤制的堆肥或充分腐熟的有机肥，合理灌溉，避免浇水过量，棚室浇水改在上午，适时放风，防止湿度过高。');
INSERT INTO `articles` VALUES (4, '番茄斑点病', 10, 'para', '（2）注意田间卫生，及时收集病残物烧毁。\n[3] \n');
INSERT INTO `articles` VALUES (4, '番茄斑点病', 11, 'para', '药剂方法');
INSERT INTO `articles` VALUES (4, '番茄斑点病', 12, 'para', '（1）发病初期喷洒30%碱式硫酸铜（绿得保）悬浮剂400倍液或50%琥胶肥酸铜可湿性粉剂500倍液、14%络氨铜水剂300倍液、77%可杀得可湿性粉剂500倍液、56%靠山水分散微颗粒剂800倍液、47%加瑞农可湿性粉剂800～900倍液，隔10天左右1次，每677m2喷对好的药液65升。采收前3天停止用药。');
INSERT INTO `articles` VALUES (4, '番茄斑点病', 13, 'para', '（2）发病初期开始喷洒36%甲基硫菌灵悬浮剂或50%混杀浮剂500倍液，40%多·硫悬浮剂600倍液、50%复方甲基硫菌灵可湿性粉剂800~900倍液、50%多菌灵可湿性粉剂1000倍液加75%百菌清可湿性粉剂1000倍液，隔10天左右1次，连续防治2~3次。\n[2-3] \n');
INSERT INTO `articles` VALUES (5, '玉米灰斑病', 0, 'title', '玉米灰斑病');
INSERT INTO `articles` VALUES (5, '玉米灰斑病', 0, 'summary', '玉米灰斑病又称尾孢叶斑病、玉米霉斑病，除侵染玉米外，还可侵染高梁、香茅、须芒草等多种禾本科植物。玉米灰斑病是近年上升很快、为害较严重的病害之一。主要为害叶片。初在叶面上形成无明显边缘的椭圆形至矩圆形灰色至浅褐色病斑，后期变为褐色。病斑多限于平行叶脉之间，大小4～20×2～5(mm)。湿度大时，病斑背面生出灰色霉状物，即病菌分生孢子梗和分生孢子。');
INSERT INTO `articles` VALUES (5, '玉米灰斑病', 1, 'level-2', '病原菌及形态特征');
INSERT INTO `articles` VALUES (5, '玉米灰斑病', 2, 'para', '病原真菌是玉米尾孢，高粱尾孢Cercospora zeae maydis Tehon&Daniels Cercospora sorghi Ell.et Ev。\n[1] \n分生孢子梗密生，浅褐色，顶部屈曲，大小60～180×4～6（um）；分生孢子无色，大小40～120×3～\n\n\n\n\n玉米灰斑病\n\n4.5（um）。分生孢于倒棍棒形，下端较直或稍弯曲，无色，1～8个隔膜，多数3～5个隔膜，孢脐明显，顶端较细稍钝，有的顶端较尖。分生孢子梗单生或丛生，一般3～10个，暗褐色，粗细一致，1～4个隔膜，多数1-2个隔膜，常呈曲膝状，1-3个膝状节，上着生分生孢子，具孢痕，分生孢子梗无分枝。\n[2] \n');
INSERT INTO `articles` VALUES (5, '玉米灰斑病', 3, 'level-2', '病害症状特点');
INSERT INTO `articles` VALUES (5, '玉米灰斑病', 4, 'para', '玉米灰斑病病菌主要为害叶片。初始病斑在透射光下呈针尖状褪绿的黄色小斑点，1周后，感病品种上形成长矩形病斑，长1-6 cm，宽0.2～0.4 cm，宽度受叶脉限制，多数沿玉米叶脉扩展。病斑交界处清晰。病斑中央灰色，边缘具褐色坏死线，叶片两面均可产生灰色霉层，以叶背面居多。 湿度大时，病斑背面生出灰色霉状物，即病菌分生孢子梗和分生孢子。发病严重年份，植株叶片枯死早衰.\n[1] \n\n[3] \n');
INSERT INTO `articles` VALUES (5, '玉米灰斑病', 5, 'level-2', '传播途径');
INSERT INTO `articles` VALUES (5, '玉米灰斑病', 6, 'para', '病菌主要以子座或菌丝随病残体越冬，成为翌年初侵染源。以后病斑上产生分生孢子进行重复侵染，不断扩展蔓延。玉米灰斑病是一个空气传播的病害。病原菌在本地病残体越冬。分生孢子从植株的下部向上部传播， 然后在株间传播\n[1] \n\n[3] \n。');
INSERT INTO `articles` VALUES (5, '玉米灰斑病', 7, 'level-2', '发病条件及风险估计');
INSERT INTO `articles` VALUES (5, '玉米灰斑病', 8, 'para', '在北方地区，一般7～8月多雨的年份易发病。病害传播很快， 一个病害侵染循环周期大约十天。高温多雨，相对湿度高的天数多的季节发病严重。 种植密度高不透风湿度会加快病害的传播, 增大风险。玉米连茬则病害的风险高。种植感病品种增加风险。如果病害在玉米生长的早期发生，后期的病害流行的风险增加。个别地块可引致大量叶片干枯。品种间抗病性有差异。\n[1] \n\n[3] \n');
INSERT INTO `articles` VALUES (5, '玉米灰斑病', 9, 'para', '1、品种和生育期 玉米品种之间存在抗性差异，感病品种易发病。在导致玉米灰斑病发生流行因素中，玉米本身的抗病性是主要因素。在同样环境条件和同样存在着大量病菌的情况下，不同品种的发病程度都不相同，同一品种在不同生育期，发病程度也不同，苗期基本不发病，拔节抽雄期开始发病，灌浆期暴发为害。');
INSERT INTO `articles` VALUES (5, '玉米灰斑病', 10, 'para', '2、环境条件　灰斑病的发生受气候条件影响较为明显，苗期低温多雨，成株期高温高湿，长期阴雨连绵，适宜病害的发生和流行。病害多在温暖潮湿，雾日较多，连年大面积种植感病品种的地区发生。植株叶片的生理年龄影响病害发展，病害多从下部叶片开始发生，逐叶向上发展。');
INSERT INTO `articles` VALUES (5, '玉米灰斑病', 11, 'para', '3、栽培管理 在栽培管理措施中，关系最为密切的是播种节令、种植密度和施肥管理。免耕或少耕的田块由于病残体积累发病严重，播种时间偏迟，栽培密度过大，使玉米植株过于茂密荫蔽，不施底肥和磷钾肥，偏施氮肥，后期脱肥、管理粗放的地块植株抗病力弱均有利病害发生发展。');
INSERT INTO `articles` VALUES (5, '玉米灰斑病', 12, 'level-2', '防治方法');
INSERT INTO `articles` VALUES (5, '玉米灰斑病', 13, 'para', '（1）选用抗病或耐病的品种。');
INSERT INTO `articles` VALUES (5, '玉米灰斑病', 14, 'para', '（2）收获后及时清除病残体。');
INSERT INTO `articles` VALUES (5, '玉米灰斑病', 15, 'para', '（3）进行大面积轮作。');
INSERT INTO `articles` VALUES (5, '玉米灰斑病', 16, 'para', '（4）加强田间管理，雨后及时排水，防止湿气滞留。');
INSERT INTO `articles` VALUES (5, '玉米灰斑病', 17, 'para', '（5）药剂防治。（勘发病初期喷洒75%百菌清可湿性粉剂500倍液或50%多菌灵可湿性粉剂600倍液、40%克瘟散乳油800～900倍液、50%苯菌灵可湿性粉剂1500倍液、25%苯菌灵乳油800倍液、20%三唑酮乳油1000倍液。\n[3] \n在北方地区化学防治最有效的防治时期是在玉米扬花期左右.\n[1] \n');
INSERT INTO `articles` VALUES (6, '番茄斑枯病', 0, 'title', '番茄斑枯病');
INSERT INTO `articles` VALUES (6, '番茄斑枯病', 0, 'summary', '番茄斑枯病又称鱼目斑病、斑点病、白星病，为番茄叶部常见病害。南北各地均有分布。各生育期都能发生，结果期间严重发病时会造成早期落叶，对产量影响很大。\n[1]');
INSERT INTO `articles` VALUES (6, '番茄斑枯病', 1, 'level-2', '简介：');
INSERT INTO `articles` VALUES (6, '番茄斑枯病', 2, 'para', '中文病名 ： 番茄斑枯病');
INSERT INTO `articles` VALUES (6, '番茄斑枯病', 3, 'para', '中文别名 ： 鱼目斑病、白星病、斑点病');
INSERT INTO `articles` VALUES (6, '番茄斑枯病', 4, 'para', '英文病名 ： Tomato septorial blight');
INSERT INTO `articles` VALUES (6, '番茄斑枯病', 5, 'para', '病原类别 ： 真菌');
INSERT INTO `articles` VALUES (6, '番茄斑枯病', 6, 'para', '病原，拉丁文名称及分类地位 ： 无性世代：番茄壳针孢菌(Septoria lycopersici Speg.)壳针孢属球壳孢目半知菌亚门真菌。');
INSERT INTO `articles` VALUES (6, '番茄斑枯病', 7, 'level-2', '形态特征 ：');
INSERT INTO `articles` VALUES (6, '番茄斑枯病', 8, 'para', '分生孢子器黑色扁球形，大小49～128 um×49～122um，初着生于寄主表皮下，后逐渐突破表皮而外露。分生孢子无色，丝状，有3～9个隔膜，大小45～90 um×2.3～2.8um。');
INSERT INTO `articles` VALUES (6, '番茄斑枯病', 9, 'para', '生理生化特征 ： 菌丝发育温度为15~28℃，最适温度为25℃左右；在温度为20℃或25℃时，病斑发展快且易产生分生孢子器，而在15℃时，分生孢子器形成慢。病菌的潜育期4～6天，10天左右即可形成分生孢子器。');
INSERT INTO `articles` VALUES (6, '番茄斑枯病', 10, 'para', '危害对象 ： 番茄、茄子、马铃薯以及茄科杂草。');
INSERT INTO `articles` VALUES (6, '番茄斑枯病', 11, 'para', '分布 ： 我国南北方大部分地区。');
INSERT INTO `articles` VALUES (6, '番茄斑枯病', 12, 'para', '侵染循环 ： 番茄斑枯病以菌丝体和分生孢子器在土中的病残体或种子上越冬，也可以在多年生的茄科杂草上越冬，翌年产生的分生孢子是病害的初侵染来源。病菌分生孢子借风、雨水及农事操作传播到寄主，并从寄主表皮上萌发后从气孔侵入，菌丝在细胞间蔓延，以分枝的吸器穿入寄主细胞内吸取养分。菌丝成熟后形成新的分生孢子器和分生孢子，进行再次侵染。');
INSERT INTO `articles` VALUES (6, '番茄斑枯病', 13, 'level-2', '发病条件 ：');
INSERT INTO `articles` VALUES (6, '番茄斑枯病', 14, 'para', '在菌源多和气候、栽培条件充分有利于发病时，易造成病害的流行。');
INSERT INTO `articles` VALUES (6, '番茄斑枯病', 15, 'para', '1 气候条件：病菌发育适温22～26℃，12℃以下或27.8℃以上发育不良。相对湿度在92%以上利于发病。温暖潮湿和阳光不足的阴天，有利于斑枯病的产生。');
INSERT INTO `articles` VALUES (6, '番茄斑枯病', 16, 'para', '2 栽培条件：种植过密、通风透光差、缺肥、连作等不良的栽培条件下，植株衰弱，抗病力降低时，易发病，在低洼地、排水不良病害易流行。');
INSERT INTO `articles` VALUES (6, '番茄斑枯病', 17, 'para', '危害部位 ： 叶柄、茎、花萼、果实');
INSERT INTO `articles` VALUES (6, '番茄斑枯病', 18, 'para', '危害症状 ： 叶片染病，先从下部老叶片开始，初期背出现水渍状小圆斑，后叶两面都出现圆形和近圆形病斑，病斑边缘深褐色，中央灰白色，凹陷，病部表面散生稀疏、隆起的小黑点。严重时，叶片布满病斑、渐枯黄、脱落，形成穿孔。茎上病斑椭圆形，褐色。果实上病斑褐色，圆形。');
INSERT INTO `articles` VALUES (6, '番茄斑枯病', 19, 'level-2', '防治方法 ：');
INSERT INTO `articles` VALUES (6, '番茄斑枯病', 20, 'para', '1.种子：从无病株上选留种子。如果种子带菌，可用50℃温汤浸种25分钟，晾干备用。');
INSERT INTO `articles` VALUES (6, '番茄斑枯病', 21, 'para', '2.轮作：重病地与非茄科作物实行3～4年轮作，最好与豆科或禾本科作物轮作。');
INSERT INTO `articles` VALUES (6, '番茄斑枯病', 22, 'para', '3.选用抗病品种 如浦红1号、广前4号、蜀早3号等。');
INSERT INTO `articles` VALUES (6, '番茄斑枯病', 23, 'para', '4. 育苗：育苗应在无病区建畦，或用无病的大田土换土育苗，防止苗期染病。');
INSERT INTO `articles` VALUES (6, '番茄斑枯病', 24, 'para', '5. 加强田间管理：选高燥易排、能灌的地块栽培。低洼或高畦地采用高畦或半高畦栽培。定植不宜过密，合理及时整枝、插架，以利通风透光，降低田间湿度。多施有机肥，施足基肥，增施磷钾肥。及时清洁田园，铲除杂草及病株残叶，减少菌源。');
INSERT INTO `articles` VALUES (6, '番茄斑枯病', 25, 'para', '6. 药剂防治：发病初期用50%多菌灵可湿性粉剂800～1000倍液；或70%甲基托布津可湿性粉剂1000倍液；或65%代森锌可湿性粉剂500倍液；或58%甲霜锰锌400倍液；或用70%代森锰锌500倍液，或用7%叶霉净粉尘剂1 kg / 亩，或65%福美锌可湿性粉剂500倍液，上述药剂之一，每7～10天喷一次，连喷2～3次即可。');
INSERT INTO `articles` VALUES (7, '花叶病毒病', 0, 'title', '花叶病毒病');
INSERT INTO `articles` VALUES (7, '花叶病毒病', 0, 'summary', '');
INSERT INTO `articles` VALUES (7, '花叶病毒病', 1, 'level-2', '症状');
INSERT INTO `articles` VALUES (7, '花叶病毒病', 2, 'para', '菊花染病后抗病品种表现为轻型花叶或不显症，在感病品种上，可形成明显的花叶症状或坏死斑，严重的产生褐色枯斑。');
INSERT INTO `articles` VALUES (7, '花叶病毒病', 3, 'level-2', '病原');
INSERT INTO `articles` VALUES (7, '花叶病毒病', 4, 'para', '病毒粒体线条状，长690×12(μm)，致死温度60一65℃，体外存活期1—6天，稀释限点100一1000倍，系统侵染的植物有菊、野菊、瓜叶菊、花环菊等。');
INSERT INTO `articles` VALUES (7, '花叶病毒病', 5, 'level-2', '发病条件');
INSERT INTO `articles` VALUES (7, '花叶病毒病', 6, 'para', '　　菊花B病毒由汁液、扦插条传播，传毒煤介主要有桃蚜(Myzuspersicae)、马铃薯蚜(Macrosiphumeu phorbiae)及其他多种蚜虫进行非持久性传毒。');
INSERT INTO `articles` VALUES (7, '花叶病毒病', 7, 'level-2', '防治方法');
INSERT INTO `articles` VALUES (7, '花叶病毒病', 8, 'para', '(1)染病菊花是带毒体，引种时要严格检疫，防止人为传播到无病区。');
INSERT INTO `articles` VALUES (7, '花叶病毒病', 9, 'para', '(2)防治 传毒蚜虫，喷洒50%抗蚜威可湿性粉剂2000倍液。(3)从无病株上采条作繁殖材料。有条件的采用茎尖组织培养进行脱毒，带毒的盆栽菊花可置于36℃条件下处理21—28天，能脱毒。生产上经过热处理的菊花，病毒已被钝化，可用来做繁殖材料。(4)在养护管理过程中，避免人为传毒。(5)必要时喷洒5%菌毒清可湿性粉剂400倍液或20%毒克星(盐酸吗啉胍·铜)可湿粉500一600倍液、0．5%抗毒剂1号水剂300倍液、20%病毒宁水溶性粉剂500倍液，隔7—10天1次，连防3次。采收前3天停止用药。');
INSERT INTO `articles` VALUES (8, '葡萄褐斑病', 0, 'title', '葡萄褐斑病');
INSERT INTO `articles` VALUES (8, '葡萄褐斑病', 0, 'summary', '又称斑点病、褐点病、叶斑病和角斑病等。褐斑病有大褐斑和小褐斑两种，主要为害中、下部叶片，病斑直径3-10毫米的为大褐斑病，其症状因种或品种不同而异。病斑小，直径2-3毫米的是小褐斑病，大小一致，叶片上现褐色小斑，中部颜色稍浅，潮湿时病斑背面生灰黑色霉层，严重时一张叶片上生有数十至上百个病斑致叶片枯黄早落。有时大、小褐斑病同时发生在一张叶片上，加速病叶枯黄脱落。');
INSERT INTO `articles` VALUES (8, '葡萄褐斑病', 1, 'level-2', '症状');
INSERT INTO `articles` VALUES (8, '葡萄褐斑病', 2, 'para', '褐斑病有两种：褐斑病和小褐斑病。褐斑病是由葡萄假尾孢菌侵染引起，主要为害叶片，侵染点发病初期呈淡褐色、不规则的角状斑点，病斑逐渐扩展，直径可达1厘 米，病斑由淡褐变褐，进而变赤褐色，周缘黄绿色，严重时数斑连结成大斑，边缘清晰，叶背面周边模糊，后期病部枯死，多雨或湿度大时发生灰褐色霉状物。有些品种病斑带有不明显的轮纹。小褐斑病为束梗尾孢菌寄生引起，侵染点发病出现黄绿色小圆斑点并逐渐扩展为2—3毫米的圆形病斑。病斑部逐渐枯死变褐进而茶褐，后期叶背面病斑生出黑色霉层。');
INSERT INTO `articles` VALUES (8, '葡萄褐斑病', 3, 'level-2', '病原物');
INSERT INTO `articles` VALUES (8, '葡萄褐斑病', 4, 'level-2', '发病因素');
INSERT INTO `articles` VALUES (8, '葡萄褐斑病', 5, 'para', '分生孢子萌发和菌丝体在寄主体内发展需要高湿和高温，故在高湿和高温条件下，病害发生严重。褐斑病一般在5、6月初发，7-9月为发病盛期。多雨年份发病较重。发病严重时可使叶片提早1～2个月脱落，严重影响树势和第二年的结果。');
INSERT INTO `articles` VALUES (8, '葡萄褐斑病', 6, 'level-2', '病害循环');
INSERT INTO `articles` VALUES (8, '葡萄褐斑病', 7, 'para', '病菌以菌丝体和分生孢子在落叶上越冬，至第二年初夏长出新的分生孢于梗，产生新的分生孢于新、旧分生孢子通过气流和雨水传播，引起初次侵染。分生孢子发芽后从叶背气孔侵入，发病通常自植株下部叶片开始，逐渐向上蔓延。病菌侵入寄主后，经过一段时期，于环境条件适宜时，产生第二批分生抱子，引起再次侵染，造成陆续发病。直至秋末，病菌又在落叶病组织内越冬。');
INSERT INTO `articles` VALUES (9, '叶斑病', 0, 'title', '叶斑病');
INSERT INTO `articles` VALUES (9, '叶斑病', 0, 'summary', '叶斑病是甜瓜重要病害之一，分布较广，各地都有发生，一般在春、秋两季发生较重。发病率10%～60%，严重地块或棚室达100%，可减产10%～30%，特别严重时可减产60%以上。此病还可为害黄瓜、冬瓜、丝瓜、苦瓜等蔬菜。');
INSERT INTO `articles` VALUES (9, '叶斑病', 1, 'level-2', '症状');
INSERT INTO `articles` VALUES (9, '叶斑病', 2, 'para', '①鸡冠花叶斑病（又称褐斑病），侵染叶片、叶柄和茎部。叶上病斑圆形，后扩大 \n\n\n\n\n鸡冠花叶斑病\n\n呈不规则状大病斑，并产生轮纹，病斑由红褐色变为黑褐色，中央灰褐色。茎和叶柄上病斑褐色、长条形。');
INSERT INTO `articles` VALUES (9, '叶斑病', 3, 'para', '②鱼尾葵叶斑病（亦称黑斑病）。叶片上产生黑褐色小圆斑，后扩大或病斑连片呈不规则大斑块，边缘略微隆起，叶两面散生小黑点。');
INSERT INTO `articles` VALUES (9, '叶斑病', 4, 'para', '③君子兰叶斑病（枯斑病）。叶上有椭圆型、长条形浅红褐色病斑，周围有退绿圈，后扩大呈不规则大斑块，病斑上产生黑点。');
INSERT INTO `articles` VALUES (9, '叶斑病', 5, 'level-2', '病原');
INSERT INTO `articles` VALUES (9, '叶斑病', 6, 'para', '属假单胞杆菌，丁香假单胞菌黄瓜致病变种细菌。病菌菌体短杆状，可链生，大小为0.7～0.9微米×1.4～2.0微米，极生1～5根鞭毛，有荚膜，无芽孢。革兰氏染色阴性，好气性。在肉汁胨琼脂培养基上菌落白色，近圆形，扁平，中央稍凸起，不透明，有同心环纹，边缘一圈薄而透明，菌落边缘有放射状细毛状物。');
INSERT INTO `articles` VALUES (9, '叶斑病', 7, 'level-2', '发病规律');
INSERT INTO `articles` VALUES (9, '叶斑病', 8, 'para', '叶斑病菌在病残体或随之到地表层越冬，翌年发病期随风、雨传播侵染寄主。鸡冠 花褐斑病夏秋（8-9月）病重，鱼尾葵叶斑病夏季高温病重，君子兰叶斑病春、秋发生， 但温室中四季均可发生。连作、过度密植、通风不良、湿度过大均有利于发病。');
INSERT INTO `articles` VALUES (9, '叶斑病', 9, 'level-2', '传播途径');
INSERT INTO `articles` VALUES (9, '叶斑病', 10, 'para', '经病原物侵害后主要在叶片上发生各种局部坏死性病斑的植物病害。可由真菌、细菌、线虫引起。病原物以真菌为主,如尾孢属(Cercospora)、长蠕孢属 (Helminthosporium)、壳针孢属(Septoria)、叶点霉(Phyllosticta)、链格孢属(Alternaria)等；此外,黄色单胞杆菌属(Xanthomonas)和假单胞杆菌属(Pseudomonas)的细菌也可引起叶斑病。有些病原物存在不同的生理小种。常见的叶斑病有由真菌引起的玉米大斑病、玉米小斑病、橡胶麻点病、花生褐斑病、花生黑斑病、甜菜褐斑病、向日葵褐斑病、苹果褐斑病等，由细菌引起的棉花角斑病、水稻白叶枯病、大豆细菌性叶斑病等。');
INSERT INTO `articles` VALUES (9, '叶斑病', 11, 'para', '叶斑病菌主要在植物病残组织和种子上越冬，成为下一代生长季的初侵染源。病菌多数靠气流、风雨，有时也靠昆虫传播。通常在生长季节不断侵染。叶斑病的流行要求雨量较大、降雨次数较多、温度适宜的气候条件,但也有对湿度要求不高的情况,如马铃薯早疫病。叶斑病造成局部坏死；仅少数可发展为系统侵染，如水稻白叶枯病的病菌侵入后在植株体内扩展，苗期症状不明显，至分蘖末期、孕穗初期呈现典型症状。');
INSERT INTO `articles` VALUES (9, '叶斑病', 12, 'level-2', '防治方法');
INSERT INTO `articles` VALUES (9, '叶斑病', 13, 'para', '①及时除去病组织，集中烧毁。');
INSERT INTO `articles` VALUES (9, '叶斑病', 14, 'para', '②轮作（温室内可换土）。');
INSERT INTO `articles` VALUES (9, '叶斑病', 15, 'para', '③不宜对植株喷浇。');
INSERT INTO `articles` VALUES (9, '叶斑病', 16, 'para', '④从发病初期开始喷药，防止病害扩展蔓延。 常用药剂有20%硅唑·咪鲜胺1000倍液，38%恶霜嘧铜菌酯800-1000倍液或4%氟硅唑1000倍液，50%托布津1000倍，70%代森锰500倍 、80%代森锰锌400-600倍，50%克菌丹500倍等。要注意药剂的交替使用，以免病菌产生抗药性。');
INSERT INTO `articles` VALUES (9, '叶斑病', 17, 'para', '预防方案');
INSERT INTO `articles` VALUES (9, '叶斑病', 18, 'para', '方案一：20%硅唑·咪鲜胺20ml或41%聚砹·嘧霉胺25ml，兑水15公斤，每7—10天1次');
INSERT INTO `articles` VALUES (9, '叶斑病', 19, 'para', '方案二：41%聚砹·嘧霉胺20ml+4%嘧啶核苷类抗菌素15ml兑水15公斤，每7—10天1次 治疗方案');
INSERT INTO `articles` VALUES (9, '叶斑病', 20, 'level-2', '生物防治');
INSERT INTO `articles` VALUES (9, '叶斑病', 21, 'para', '选用哈茨木霉菌300倍液，直接喷施于病部叶面，同时配合烟熏制剂进行烟熏可有效防治叶斑病，阴天可以施药，温度湿度对药效影响不大，同时该制剂属于生物制剂，对作物和蜜蜂等绝对安全！');
INSERT INTO `articles` VALUES (10, '番茄叶霉病', 0, 'title', '番茄叶霉病');
INSERT INTO `articles` VALUES (10, '番茄叶霉病', 0, 'summary', '番茄叶霉病在番茄的叶、茎、花、果实上，都会出现的症状，但是常见的是是发生在叶片上，初期在叶片背面出现一些退绿斑，后期变为灰色或黑紫色的不规则形霉层，叶片正面在相应的部位退绿变黄，严重时，叶片常出现干枯卷缩。从发病的顺序看，经常从植株下部向上蔓延、温度从9℃到34℃之间，病原都能生长发育，发育的最适温度是20～25℃。在最适温度且湿度较大（相对湿度在80%以上）时，仅需10天到半月就可普遍发病。');
INSERT INTO `articles` VALUES (10, '番茄叶霉病', 1, 'level-2', '为害症状');
INSERT INTO `articles` VALUES (10, '番茄叶霉病', 2, 'para', '主要危害叶片，严重时也危害茎、花和果实，叶片发病，初期叶片正面出现黄绿色、边缘不明显的斑点，叶背面出现灰白色霉层，后霉层变为淡褐至深褐色；湿度大时，叶片表面病斑也可长出霉层。病害常由下部叶片先发病，逐渐向上蔓延，发病严重时霉层布满叶背，叶片卷曲，整株叶片呈黄褐色干枯。嫩茎和果柄上也可产生相似的病斑，花器发病易脱落。果实发病，果蒂附近或');
INSERT INTO `articles` VALUES (10, '番茄叶霉病', 3, 'para', '\n\n\n\n\n山东济阳县崔寨番茄叶霉病田间症状\n\n');
INSERT INTO `articles` VALUES (10, '番茄叶霉病', 4, 'para', '果面上形成黑色圆形或不规则斑块，硬化凹陷，不能食用。');
INSERT INTO `articles` VALUES (10, '番茄叶霉病', 5, 'para', '\n\n\n\n\n番茄叶霉病\n\n');
INSERT INTO `articles` VALUES (10, '番茄叶霉病', 6, 'level-2', '病原特征');
INSERT INTO `articles` VALUES (10, '番茄叶霉病', 7, 'para', '\n\n\n\n\n番茄叶霉病菌\n\n');
INSERT INTO `articles` VALUES (10, '番茄叶霉病', 8, 'para', '\n\n\n\n\n叶霉病的病原物--黄枝孢\n\n');
INSERT INTO `articles` VALUES (10, '番茄叶霉病', 9, 'para', '病原为Fulvia fulva（Cooke）Cif.，属半知菌亚门褐孢霉属，异名为黄枝孢菌Cladosporium fulvumCooke。分生孢子梗成束从气孔伸出，稍有分枝，初无色，后呈褐色，有1~10个隔膜，大部分细胞上部偏向一侧膨大。其上产生分生孢子，产孢细胞单芽生或多芽生，合轴式延伸。分生孢子串生，孢子链通常分枝，分生孢子圆柱形或椭圆形，初无色，单孢，后变为褐色，中间长出一个隔膜，形成2个细胞，分生孢子大小为14~38umx5~9um。');
INSERT INTO `articles` VALUES (10, '番茄叶霉病', 10, 'level-2', '发病规律');
INSERT INTO `articles` VALUES (10, '番茄叶霉病', 11, 'para', '病菌以菌丝体或菌丝块在病残体内越冬，也可以分生孢子附着在种子表面或菌丝潜伏于种皮越冬。翌年条件适宜时，从病残体上越冬的菌丝体产生分生孢子，以气流传播引起初侵染，另外，播种带菌的种子也可引起初侵染。该病有多次再侵染，病菌萌发后，从寄主叶背面的气孔侵入，菌丝在细胞间蔓延，并产生吸器伸入细胞内吸取水分和养分，形成病斑。环境条件适宜时，病斑上又产生大量分生孢子，进行不断再侵染。病菌也可从萼片、花梗的气孔侵入，并能进入子房，潜伏在种皮上。');
INSERT INTO `articles` VALUES (10, '番茄叶霉病', 12, 'para', '番茄叶霉病菌的致病性分化最为繁杂，也最复杂，存在许多生理小种。这给抗病育种和抗病品种的使用带来很大困难。');
INSERT INTO `articles` VALUES (10, '番茄叶霉病', 13, 'level-2', '发生特点');
INSERT INTO `articles` VALUES (10, '番茄叶霉病', 14, 'para', '此病由真菌半知菌亚门的褐孢霉 Fulvia fulva (Cooke) Cif. 侵染所致。病菌主要以菌丝体或菌丝块在病株残体内越冬，也可以分生孢子附着在种子或以菌丝体在种皮内越冬。翌年环境条件适宜时，产生分生孢子，借气流传播，从叶背的气孔侵入，还可从萼片、花梗等部分侵入，并进入子房，潜伏在种皮上。');
INSERT INTO `articles` VALUES (10, '番茄叶霉病', 15, 'level-2', '发病条件');
INSERT INTO `articles` VALUES (10, '番茄叶霉病', 16, 'para', '病菌喜高温、高湿环境，发病最适气候条件为温度20～25℃，相对湿度95%以上。浙江及长江中下游地区主要发病盛期为春季3～7月和秋季9～11月。番茄的感病生育期是开花结果期。');
INSERT INTO `articles` VALUES (10, '番茄叶霉病', 17, 'para', '多年连作、排水不畅、通风不良、田间过于郁闭、空气湿度大的田块发病较重。年度间早春低温多雨、连续阴雨或梅雨多雨的年份发病重。秋季晚秋温度偏高、多雨的年份发病重。');
INSERT INTO `articles` VALUES (10, '番茄叶霉病', 18, 'level-2', '防治措施农业防治');
INSERT INTO `articles` VALUES (10, '番茄叶霉病', 19, 'para', '1、合理轮作：和非茄科作物进行三年以上轮作，以降低土壤中菌源基数。');
INSERT INTO `articles` VALUES (10, '番茄叶霉病', 20, 'para', '2、种子消毒：无病种子可减轻田间由种子带菌引起的初侵染。引进种子需要进行种子处理，采用温水浸种。利用种子与病菌耐热力的差异，选择既能杀死种子内外病菌，又不损伤种子生命力的温度进行消毒。对于温室栽培的番茄种子宜选择用55℃温水浸种30分钟，以清除种子内外的病菌，取出后在冷水中冷却，用高锰酸钾浸种30分钟，取出种子后用清水漂洗几次，最后晒干催芽播种。');
INSERT INTO `articles` VALUES (10, '番茄叶霉病', 21, 'para', '3、高温闷棚：选择晴天中午时间，采取两小时左右的30～33℃高温处理，然后及时通风降温，对病原菌有较好的控制作用。');
INSERT INTO `articles` VALUES (10, '番茄叶霉病', 22, 'para', '4、加强棚室管理：及时通风，适当控制浇水，浇水后及时通风降湿；采用双垄覆膜、膜下灌水的栽培方式，除可以增加土壤湿度外，还可以明显降低温室内空气湿度，从而抑制番茄叶霉病的发生与再侵染，并且地膜覆盖可有效地阻止土壤中病菌的传播。根据温室外天气情况，通过合理放风，尽可能降低温室内湿度和叶面结露时间，对病害有一定的控制效应。及时整枝打杈、植株下部的叶片尽可能的摘除，也可增加通风。实施配方施肥，避免氮肥过多，适当增加磷、钾肥。');
INSERT INTO `articles` VALUES (10, '番茄叶霉病', 23, 'para', '5、选用抗病品种，严把育苗关；市场上推广的品种中高抗叶霉病的有佳粉15、佳粉16、佳粉17、中杂7号、沈粉3号、佳红15等，可因地制宜，选用种植。');
INSERT INTO `articles` VALUES (10, '番茄叶霉病', 24, 'para', '6、每年更换一次棚室薄膜，使用无滴膜，经常清除膜上灰尘。定植密度不要过高，及时整枝打杈、绑蔓，植株坐果后适度摘除下部老叶，以利通风透光。露地番茄要早定植，深中耕，覆盖地膜或培土，促进植株生长。');
INSERT INTO `articles` VALUES (10, '番茄叶霉病', 25, 'para', '7、栽培前期注意提高棚室温度，后期加强通风，降低湿度。病势发展时，可选择晴天中午，密闭棚室使温度上升到36～38℃，保持2个小时可有效地抑制病情发展。');
INSERT INTO `articles` VALUES (10, '番茄叶霉病', 26, 'para', '\n\n\n\n');
INSERT INTO `articles` VALUES (11, '苹果黑星病', 0, 'title', '苹果黑星病');
INSERT INTO `articles` VALUES (11, '苹果黑星病', 0, 'summary', '苹果黑星病的病原菌是苹果黑星菌，属子囊菌亚门真菌。苹果黑星病主要危害叶片和果实。叶片上病斑近圆形或呈放射状，初期叶上生绿褐色霉层，稍后霉层渐变为褐色至黑色。是世界性苹果病害，欧洲、美国及日本等国危害严重，中国的北方多省市都有此病害。');
INSERT INTO `articles` VALUES (11, '苹果黑星病', 1, 'level-2', '病害症状');
INSERT INTO `articles` VALUES (11, '苹果黑星病', 2, 'para', '主要危害叶片和果实。叶片上病斑近圆形或呈放射状，初期叶上生绿褐色霉层，稍后霉层渐变为褐色至黑色。严重时病斑布满全叶，叶片枯焦，易早期脱落。叶柄、叶脉及果实等上的症状都与梨黑星病相似，特点是后期在病斑上均覆盖一层黑霉（即病菌的分生孢子梗和分生孢子）。\n\n\n\n\n苹果黑星病病叶\n\n');
INSERT INTO `articles` VALUES (11, '苹果黑星病', 3, 'level-2', '病原特性');
INSERT INTO `articles` VALUES (11, '苹果黑星病', 4, 'para', '苹果黑星菌，属子囊菌亚门真菌；无性世 代：Fusicladium dendriticum（wallr.）Fuck.称树状黑星孢，属半知菌亚门真菌。也有报道其无性世代为。Spilocaea pomiFr.属半知菌亚门真菌。菌丝初无色，后变为青褐色至红褐色，在培养基上灰色，分枝，有隔。分生孢子梗丛生，深褐色，大小50～60×4～6微米，屈膝状或结节状，短而直立，无隔或具1～2个隔膜。梗顶着生一个单细胞（少数双细胞）的分生孢子。分生孢子梭形或长卯圆形，深褐色，大小12～22×6～9微米。病菌在腐生阶段可形成假囊壳，球形或近球形，褐色至黑色，子囊平行排列于假囊壳基部，子囊长棍棒形或圆筒形，具短柄，内含8个子囊孢子。排列成两行。子囊孢子卵圆形，青褐至黄褐色，双细胞，上面的细胞较小而稍尖。\n[1] \n');
INSERT INTO `articles` VALUES (11, '苹果黑星病', 5, 'level-2', '发病特点');
INSERT INTO `articles` VALUES (11, '苹果黑星病', 6, 'para', '发病特点：苹果黑星病菌以菌丝体在枝溃疡和芽鳞内或子囊壳在落叶上越冬。子囊孢子到第二年春季成熟，田间分生孢子在6～7月份最多。病菌也可被蚜虫传播。寄主最易受害时期为花蕾开放与花瓣脱落期间，萼片上的病斑为以后侵染果实的最好菌源。因此，早春为病害发生的重要时期。在苹果感病期间，天气连绵多雨，适于病菌初次侵染。果实成熟期间被害，导致贮藏期间及运输中的损失。苹果各品种间抗病性存在差异，小苹果品种中，黄太平最感病，其次为黄海棠和花红，河南海棠、三叶海棠较为抗病；多花海棠是高抗品种。大苹果品种，国光和富士较易感病；其次为印度、红星、金冠、祝光、旭、黄魁等品种抗病力强。\n[1] \n');
INSERT INTO `articles` VALUES (11, '苹果黑星病', 7, 'level-2', '发生规律');
INSERT INTO `articles` VALUES (11, '苹果黑星病', 8, 'para', '苹果黑星菌的子囊壳大多在秋冬形成。在培养基上，20C和pH4.5～5.8时生长最适。实体形成后，在紫褐色的予实体层上产牛白霜状担孢子，担孢子陆续成熟，随风雨传播，通过伤口侵入，在木质部定植，然后沿导管上下蔓延。春、秋雨季是病菌侵入的有利时期。树体从感病到出现症状需1～2年时间。发病后，重病树1～2年死亡；轻病树可活十多年，部分病树还可自行恢复健康。该病的发生与果园地势、管理水平及品种密切相关。土壤粘重、排水不良、盐碱过重、树势衰弱的果园发病重；果园管理粗放。伤口不及时保护等均易导致病害发生。大树较幼树易感病。苹果各品种间感病性存在差异。\n[1] \n');
INSERT INTO `articles` VALUES (11, '苹果黑星病', 9, 'level-2', '防治措施');
INSERT INTO `articles` VALUES (11, '苹果黑星病', 10, 'para', '（1）加强栽培管理。增施有机肥，低洼积水地注意及时排水，改良土壤，以增强树势。');
INSERT INTO `articles` VALUES (11, '苹果黑星病', 11, 'para', '（2）清除初侵染源。挖除果园内重病树、病死树、根蘖苗，清除病根，锯除发病枝干，及时刮除病苗子实体集中烧毁或深埋。喷干枝清园，全树喷洒300倍液+有机硅。\n[1] \n');
INSERT INTO `articles` VALUES (11, '苹果黑星病', 12, 'level-2', '地理分布');
INSERT INTO `articles` VALUES (11, '苹果黑星病', 13, 'para', '世界性苹果病害，欧洲、美国及日本等国危害严重。中国黑龙江、吉林、辽宁、河南、新疆、甘肃、四川、云南及台湾等省局部地方有发现。');
INSERT INTO `articles` VALUES (12, '番茄白粉病', 0, 'title', '番茄白粉病');
INSERT INTO `articles` VALUES (12, '番茄白粉病', 0, 'summary', '番茄白粉病 鞑靼内丝白粉菌Leveillula taurica(Lev.)Arn.属子囊菌亚门真菌和番茄粉孢Oidium lycopersici Cooke et Mass.属于半知菌门真菌。寄主 番茄。危害 番茄的普通病害,病情较轻,对生产影响不大。个别棚室发病重,引起植株早衰死亡。分布 局部地区分布,多在棚室中发生。');
INSERT INTO `articles` VALUES (12, '番茄白粉病', 1, 'level-2', '症状特点');
INSERT INTO `articles` VALUES (12, '番茄白粉病', 2, 'para', '主要危害叶片。叶面初现白色霉点，散生，后逐渐扩大\n\n\n\n\n番茄白粉病\n\n成白色粉斑，并互相连合为大小不等的白粉斑，严重时整个叶面被白粉所覆盖，像被撒上一薄层面粉，故称白粉病。叶柄、茎部、果实等部位染病，病部表面也出现白粉状霉斑。白粉状物即为本病病征(分孢梗及分生孢子)。\n[1] \n');
INSERT INTO `articles` VALUES (12, '番茄白粉病', 3, 'level-2', '病原及发病特点');
INSERT INTO `articles` VALUES (12, '番茄白粉病', 4, 'para', '病原菌无性阶段为半知菌亚门的拟粉孢属〔Oidiopsistaurica(Lév.)Salm.〕；有性阶段为子囊菌亚门的内丝白粉菌属〔Leveillula taurica(Lév.)Arn.〕。在华南地区，病菌有性阶段不常见，主要以无性态分生孢子作为初侵与再侵接种体，依靠气流辗转传播危害，完成病害周年循环，无明显越冬现象。通常温暖潮湿的天气及环境有利于发展，尤其在温室或大棚保护地栽培，病害发生普通而较严重。病菌孢子耐旱力特强，在高温干燥天气亦可侵染致病。品种间抗性差异尚待调查。');
INSERT INTO `articles` VALUES (12, '番茄白粉病', 5, 'level-2', '防治方法');
INSERT INTO `articles` VALUES (12, '番茄白粉病', 6, 'para', '(1)注意选育抗病品种。');
INSERT INTO `articles` VALUES (12, '番茄白粉病', 7, 'para', '(2)保护地栽培宜加强温湿调控，主要用粉尘法或烟雾法防治；露地栽培于发病前或病害点片发生阶段及时连续喷药控病。');
INSERT INTO `articles` VALUES (13, '叶霉病', 0, 'title', '叶霉病');
INSERT INTO `articles` VALUES (13, '叶霉病', 0, 'summary', '叶霉病是属半知菌亚门真菌，其真菌定名为黄枝孢菌，主要危害叶片，严重时也可以为害茎、花、果实等。');
INSERT INTO `articles` VALUES (13, '叶霉病', 1, 'level-2', '概述');
INSERT INTO `articles` VALUES (13, '叶霉病', 2, 'para', '叶霉病又称。番茄叶霉病是温室番茄普遍发生、危害严重的一种病害，常造成叶片早枯而提早拉秧，与番茄霜霉病、番茄灰霉病并称，是番茄、黄瓜、葡萄、西瓜等经济作物需防治的主要病害。');
INSERT INTO `articles` VALUES (13, '叶霉病', 3, 'level-2', '病原及特征');
INSERT INTO `articles` VALUES (13, '叶霉病', 4, 'para', '叶霉病是属半知菌亚门真菌，其真菌定名为黄枝孢菌。叶霉病菌以附着在种子表面和侵入种皮内的菌丝及残存和附着在病残体、架材和土壤中的菌丝与孢子越冬。翌年春在适宜的温湿度条件下，产生新的分生孢子，孢子随风或弹射到叶片等部位侵入，一直延续到秋末。该病流行速度较快，在适合的条件下，从始发期到盛发期只需要10～15天左右的时间。相对湿度在80%以上，有利于孢子的形成及病斑扩展。相对湿度在90%以上病害易发生和流行，叶表面结露可促进病害发展。 　高温高湿有利于病害的发生，其中湿度是影响发病的重要因素。若温室内温度在20～25℃，相对湿度大于90%，发病可以从无到有，10多天蔓延全田。种植过密、通风透光不良或多雨，田间湿度大时均有利于病害发生。 　保护地种植密度过大，株间通风不良，浇水过多，湿度过大，叶面结露时间过长，都有利于病菌的侵染和扩展，能加重病害发生。一般来说，宁夏保护地番茄以春秋两季(3月～5月和9月～10月)发病较重，露地栽培的番茄则在多雨的7月～8月发病较重，露地受害状况比保护地要轻得多。番茄叶霉病是蔬菜病害中生理小种分化得最明显的一种病害。');
INSERT INTO `articles` VALUES (13, '叶霉病', 5, 'level-2', '危害症状');
INSERT INTO `articles` VALUES (13, '叶霉病', 6, 'para', '叶霉病主要危害叶片，严重时也可以危害茎、花、果实等。叶片发病初期，叶面出现椭圆形或不规则淡黄色褪绿病斑，叶背面初生白霉层，而后霉层变为灰褐色至黑褐色绒毛状，是病菌的分生孢子梗和分生孢子，条件适宜时，病斑正面也可长出黑霉，随病情扩展，病斑多从下部叶片开始逐渐向上蔓延，严重时可引起全叶干枯卷曲，植株呈黄褐色干枯状。果实染病后，果蒂部附近形成圆形黑色病斑，并且硬化稍凹陷，造成果实大量脱落。嫩茎及果柄上的症状与叶片相似。');
INSERT INTO `articles` VALUES (13, '叶霉病', 7, 'level-2', '防治措施');
INSERT INTO `articles` VALUES (13, '叶霉病', 8, 'para', '叶霉病比较难于防治,因此着重于预防更为重要。种子应选用无病株上采留的种子，如种子带菌可用52℃温水浸种30分钟。重病温室要与非茄科蔬菜进行2-3年轮作。，加强通风、透光，并适当增施磷、钾肥，提高植株抗病能力。');
INSERT INTO `articles` VALUES (13, '叶霉病', 9, 'level-3', '农业防治');
INSERT INTO `articles` VALUES (13, '叶霉病', 10, 'para', '（1）选用抗病品种，严把育苗关；');
INSERT INTO `articles` VALUES (13, '叶霉病', 11, 'para', '（2）利用无病种子：无病种子可减轻田间由种子带菌引起的初侵染。无病种子应从无病田或健康植株上留种。引进种子需要进行种子处理，采用温水浸种。利用种子与病菌耐热力的差异，选择既能杀死种子内外病菌，又不损伤种子生命力的温度进行消毒。对于温室栽培的番茄种子宜选择用55℃温水浸种30分钟，以清除种子内外的病菌，取出后在冷水中冷却，用高锰酸钾浸种30分钟，取出种子后用清水漂洗几次，最后晒干催芽播种；');
INSERT INTO `articles` VALUES (13, '叶霉病', 12, 'para', '（3）高温闷棚：选择晴天中午时间，密闭温室升温至30～33℃，并保持2个小时左右，然后及时通风降温，对病原有较好的控制作用；');
INSERT INTO `articles` VALUES (13, '叶霉病', 13, 'para', '（4）加强管理，降低湿度：采用双垄覆膜、膜下灌水的栽培方式，除可以增加土壤湿度外，还可以明显降低温室内空气湿度，从而抑制番茄叶霉病\n[1] \n的发生与再侵染，并且地膜覆盖可有效地阻止土壤中病菌的传播。根据温室外天气情况，通过合理放风，尽可能降低温室内湿度和叶面结露时间，对病害有一定的控制效应。及时整枝打杈、植株下部的叶片尽可能的摘除，可增加通风。另外，采用滴灌可降低空气内湿度，好于大水漫灌；');
INSERT INTO `articles` VALUES (13, '叶霉病', 14, 'level-3', '药剂防治');
INSERT INTO `articles` VALUES (13, '叶霉病', 15, 'para', '预防用药： 在预期发病时，用41%聚砹嘧霉胺(全新配方)25ml，进行植株全面均匀喷施，5—7天用药一次。 　 防治用药：');
INSERT INTO `articles` VALUES (13, '叶霉病', 16, 'para', '（1）叶霉病初发时，及时摘除病叶、病果及严重病枝，然后根据作物该时期并发病害情况，采用41%聚砹嘧霉胺(全新配方)25ml+嘧菌酯百菌清(50%百菌清,6%嘧菌酯)20ml，兑水15公斤，每5—7天用药1次；连用2—3次。');
INSERT INTO `articles` VALUES (13, '叶霉病', 17, 'para', '（2）发病较重时，清除中心病株、病叶等，及时采用中西医结合的防治方法，啶菌恶唑或恶霜嘧铜菌酯30ml+41%聚砹嘧霉胺(全新配方)25ml+嘧菌酯百菌清(50%百菌清,6%嘧菌酯)20ml兑水15公斤，3—5天用药1次。施药避开高温时间段，最佳施药温度为20℃—30℃。');
INSERT INTO `articles` VALUES (13, '叶霉病', 18, 'level-2', '注意事项');
INSERT INTO `articles` VALUES (13, '叶霉病', 19, 'para', '1、及时清除病株残体，病果、病叶、病枝');
INSERT INTO `articles` VALUES (13, '叶霉病', 20, 'para', '2、做好通风降湿，减少或避免叶面结露');
INSERT INTO `articles` VALUES (13, '叶霉病', 21, 'para', '3、提高植株自身的抗病力');
INSERT INTO `articles` VALUES (13, '叶霉病', 22, 'para', '4、大多数药剂须现配现用，不得与强酸、强碱性农药混用。');
INSERT INTO `articles` VALUES (13, '叶霉病', 23, 'para', '5、施药后4小时内降雨，需重新喷雾。');
INSERT INTO `articles` VALUES (13, '叶霉病', 24, 'para', '6、如有轻微沉淀析出属正常，不影响药效');
INSERT INTO `articles` VALUES (14, '斑点病', 0, 'title', '斑点病');
INSERT INTO `articles` VALUES (14, '斑点病', 0, 'summary', '斑点病：又名白星病、穿孔病、褐斑病、叶点霉斑等名称.在中国分布于吉林、辽宁、山西、河南、台湾、湖南、广西、云南等省、自治区。是一种为害轻微的次要植物病害。因常与蛙眼病、破烂叶斑病、赤星病等叶斑病混生，不易区分。');
INSERT INTO `articles` VALUES (14, '斑点病', 1, 'level-2', '简介');
INSERT INTO `articles` VALUES (14, '斑点病', 2, 'para', '斑点病：又名白星病、穿孔病、褐斑病、叶点霉斑等名称.在中国分布于吉林、辽宁、山西、河南、台湾、湖南、广西、云南等省、自治区。是一种为害轻微的次要植物病害。因常与蛙眼病、破烂叶斑病、赤星病等叶斑病混生，不易区分。');
INSERT INTO `articles` VALUES (14, '斑点病', 3, 'level-2', '症状');
INSERT INTO `articles` VALUES (14, '斑点病', 4, 'para', '主要危害叶片，也能为害嫩枝及果实。发病初叶片表面出现近圆形褐色水浸状小斑，以后扩大为椭圆形至不定形，呈灰褐至灰白色，周围具有水浸状淡绿色晕环，病斑大小为15－20毫米，严重时病斑汇合，使叶片局部干枯。后期病斑中部呈薄纸状，淡黄色或灰白色，易破碎，多发生在生育后期中下部叶片上。潮湿时斑面现小黑点，斑面常易破裂或穿孔。高温多雨季节，病斑迅速扩展为不整形大斑，其后焦枯脱落。');
INSERT INTO `articles` VALUES (14, '斑点病', 5, 'para', '徒长枝或内膛一年生枝容易感病。染病枝条皮孔突起，芽周变黑，凹陷坏死，边缘开裂。幼果感病多表现为黑点型和疮痂型，近成熟果实多为褐变型。');
INSERT INTO `articles` VALUES (14, '斑点病', 6, 'level-2', '发病条件');
INSERT INTO `articles` VALUES (14, '斑点病', 7, 'para', '该病为瓜灰星菌所致，病菌随遗落土中越冬，周年都有苦瓜种植地区越冬期不明显，可借雨水溅射传播，高温多湿的天气有利于本病的发生，连作和低洼郁蔽、或偏施氮肥发病较重。');
INSERT INTO `articles` VALUES (14, '斑点病', 8, 'level-2', '防治方法');
INSERT INTO `articles` VALUES (14, '斑点病', 9, 'para', '斑点病应采取以加强和改善栽培控病措施为主，配合杀菌农药保护的综防措施。 　　1、忌连作：因病残体是主要的初侵染源，故播前应彻底搞好清园工作以及避免连作。与水稻轮作一年效果较好。2、整地施肥：结合整地晒土起高畦，施足优质有机肥料，整平畦面以利灌排；避免单独或过量施速效氮肥，适当增施磷钾肥有增强抗性作用。3、喷药保护：发病初期可选喷：(1)70%甲基托布津可湿性粉剂1000倍液；20%氟硅唑咪鲜胺600-800倍液；(2)75%百菌清可湿性粉剂或高科56%嘧菌酯百菌清800-1000倍液；(3)30%氧氯化铜悬浮剂300-400倍液，隔7-8天喷一次，连续喷3-4次。');
INSERT INTO `articles` VALUES (15, '马铃薯早疫病', 0, 'title', '马铃薯早疫病');
INSERT INTO `articles` VALUES (15, '马铃薯早疫病', 0, 'summary', '马铃薯早疫病主要由链格孢属茄链格孢引起，可发生在叶片上，也可侵染块茎。叶片染病病斑黑褐色，圆形或近圆形，具同心轮纹，大小3～4mm。湿度大时，病斑上生出黑色霉层病征，即病原菌分生孢子梗和分生孢子。发病严重的叶片干枯脱落，田间植株成片枯黄。块茎染病产生暗褐色稍凹陷圆形或近圆形病斑，边缘分明，皮下呈浅褐色海绵状干腐。该病近年呈上升趋势，其为害有的地区不亚于晚疫病。');
INSERT INTO `articles` VALUES (15, '马铃薯早疫病', 1, 'level-2', '形态特征');
INSERT INTO `articles` VALUES (15, '马铃薯早疫病', 2, 'para', '菌丝丝状，有隔膜。分生孢子梗自气孔伸出，束生，每束1～5根，梗圆筒形或短杆状，暗褐色，具隔膜1～4个，大小30.6～104×4.3～9.19（um），直或较直，梗顶端着生分生孢子。分生孢子长卵形或倒棒形，淡黄色，大小85.6～146.5×11.7～22（um），纵隔1～9个，横隔7～13个，顶端长有较长的喙，无色，多数具1～3个横隔，大小6.3～74×3～7.4（um）。\n[1] \n');
INSERT INTO `articles` VALUES (15, '马铃薯早疫病', 3, 'level-2', '传播途径');
INSERT INTO `articles` VALUES (15, '马铃薯早疫病', 4, 'para', '以分生孢子或菌丝在病残体或带病薯块上越冬，翌年种薯发芽病菌即开始侵染。病苗出土后，其上产生的分生孢子借风、雨传播，进行多次再侵染使病害蔓延扩大。病菌易侵染老叶片，遇有小到中雨或连续阴雨或湿度高于70%，该病易发生和流行。\n[1] \n');
INSERT INTO `articles` VALUES (15, '马铃薯早疫病', 5, 'level-2', '发病条件');
INSERT INTO `articles` VALUES (15, '马铃薯早疫病', 6, 'para', '分生孢子萌发适温26～28℃，当叶上有结露或水滴，温度适宜，分生孢子经35～45分钟即萌发，从叶面气孔或穿透表皮侵入，潜育期2—3天。瘠薄地块及肥力不足田发病重。\n[1] \n');
INSERT INTO `articles` VALUES (15, '马铃薯早疫病', 7, 'level-2', '防治方法');
INSERT INTO `articles` VALUES (15, '马铃薯早疫病', 8, 'para', '（1）选用早熟耐病品种，适当提早收获。');
INSERT INTO `articles` VALUES (15, '马铃薯早疫病', 9, 'para', '（2）选择土壤肥沃的高燥田块种植，增施有机肥，推行配方施肥，提高寄主抗病力。');
INSERT INTO `articles` VALUES (15, '马铃薯早疫病', 10, 'level-2', '农业防治');
INSERT INTO `articles` VALUES (15, '马铃薯早疫病', 11, 'para', '（1）培育壮苗：要调节好苗床的温度和湿度，在苗子长到两叶一心时进行分苗，谨防苗子徒长。苗期喷施奥力克—霜贝尔500倍液，可防止苗期患病；');
INSERT INTO `articles` VALUES (15, '马铃薯早疫病', 12, 'para', '（2）轮作倒茬：番茄应实行与非茄科作物三年轮作制；');
INSERT INTO `articles` VALUES (15, '马铃薯早疫病', 13, 'para', '（3）加强田间管理：要实行高垄栽培，合理施肥，定植缓苗后要及时封垄，促进新根发生。温室内要控制好温度和湿度，加强通风透光管理。结果期要定期摘除下部病叶，深埋或烧毁，以减少传病的机会；');
INSERT INTO `articles` VALUES (16, '灰斑病', 0, 'title', '灰斑病');
INSERT INTO `articles` VALUES (16, '灰斑病', 0, 'summary', '大豆灰斑病又称褐斑病、斑点病或蛙眼病。大豆灰斑病是世界性病害，也是我国大豆主产区的重要病害，尤以东北三省危害严重。是一个真菌病害， 病原菌是 Cercospora sojina。\n[1]');
INSERT INTO `articles` VALUES (16, '灰斑病', 1, 'level-2', '危害及症状');
INSERT INTO `articles` VALUES (16, '灰斑病', 2, 'para', '\n\n\n\n\n灰斑病\n\n该病原为黑龙江省东部低洼易涝地区的主要病害，1963年统计，合江地区因灰斑病损失大豆达1000万千克，80年代以来，逐步向西扩大蔓延，现已成为全省性的大豆病害。近年来由于大豆重迎茬面积增加，使灰斑病愈来愈重。大豆灰斑病是我省大豆生产中常发生的间歇性流行病害。病害流行年份，造成大豆产量、品质严重损失，一般可减产5%—10%，严重时可减产30%—50%，百粒重下降2—3克，蛋白质和油份含量均不同程度降低。大豆灰斑病对大豆叶、茎、荚、籽实均能造成危害，以叶片和籽实最重。子叶上病斑圆形、半圆形或椭圆形，深褐色，略凹陷。叶片上病斑多为圆形、椭圆形或不规则形，病斑中央灰白色，周围红褐色，与健部分界清晰，这是区分灰斑病与其它叶部病害的主要特征。气候潮湿时病斑背面有密集的灰色霉层，即病菌的分生孢子梗和分生孢子，严重时一叶片上可生几十个病斑，使叶片提早脱落。茎、枝和叶柄上结荚后产生椭圆形或纺锤形病斑，中央褐色，边缘红褐色，后期中央灰色，边缘黑褐色，其上布满微小黑点。荚上病斑圆形或椭圆形，形状颜色同叶上病斑。种粒上病斑圆形至不规则形，中央灰色，边缘红褐色，形成蛙眼。');
INSERT INTO `articles` VALUES (16, '灰斑病', 3, 'para', '是一个真菌病害， 病原菌是 Cercospora sojina。大豆灰斑病菌生理分化现象十分明显，生理小种类型较多。东北春大豆区目前已报导有14个生理小种。其中以1号小种出现频率最高为50%，分布也最广，合江、牡丹江、松花江、嫩江、绥化等地区都以1号小种占优势，其次是7号小种出现频率为22%，黑河地区1号和7号小种出现频率相同。1号和7号两个小种是黑龙江省大豆产区的优势小种，10号小种出现频率为9%，占第三位。');
INSERT INTO `articles` VALUES (16, '灰斑病', 4, 'para', '大豆灰斑病与温湿度关系。灰斑病菌孢子萌发温度是基础，湿度是关键，孢子萌发最低温度为12℃，以21—26℃为最适，超过35℃萌发率明显降低。萌发的最低湿度为65%—75%，湿度越大萌发率越高。黑龙江省大豆灰斑病发病初期是7月初，8月下旬至9月初达到高峰，如果7月上旬到8月中旬雨量大，雨天多，相对湿度大，发病就重。');
INSERT INTO `articles` VALUES (16, '灰斑病', 5, 'level-2', '疾病防治');
INSERT INTO `articles` VALUES (16, '灰斑病', 6, 'para', '选种抗病品种，近年来黑龙江省审定推广的大豆品种都是抗或高抗灰斑病优势小种的新品种，但要注意在一个地区连续种植一个抗病品种之后，由于品种选推压力作用，会引起生理小种变化，而使抗病品种丧失抗性，因此要几个品种交替使用，延长品种的使用年限。');
INSERT INTO `articles` VALUES (16, '灰斑病', 7, 'level-2', '药剂防治');
INSERT INTO `articles` VALUES (16, '灰斑病', 8, 'para', '1、40%多菌灵胶悬剂，每亩100克，稀释成1000倍喷雾。2、50%多菌可湿性粉或70%甲基托布津，每亩100—150克兑水稀释成1000倍液。3、2.5%溴氰菊酯乳油，每亩40毫升与50%多菌灵可湿粉每亩100克混合，可兼防大豆、食心虫。药剂防治要抓住防治时机，田间一次施药的关键时期是始荚期至盛荚期。');
INSERT INTO `articles` VALUES (17, '葡萄轮斑病', 0, 'title', '葡萄轮斑病');
INSERT INTO `articles` VALUES (17, '葡萄轮斑病', 0, 'summary', '葡萄轮斑病主要危害叶片。发病初期在叶面上出现红褐色圆形或不规则形病斑，后扩大为圆形或近圆形，叶面具深浅相间的轮纹，湿度大时叶背面长有浅褐色霉层。');
INSERT INTO `articles` VALUES (17, '葡萄轮斑病', 1, 'level-2', '为害症状');
INSERT INTO `articles` VALUES (17, '葡萄轮斑病', 2, 'para', '轮斑病主要危害叶片。发病初期在叶面上出现红褐色圆形或不规则形病斑，后扩大为圆形或近圆形，叶面具深浅相间的轮纹，湿度大时叶背面长有浅褐色霉层。\n[1] \n');
INSERT INTO `articles` VALUES (17, '葡萄轮斑病', 3, 'level-2', '形态特征');
INSERT INTO `articles` VALUES (17, '葡萄轮斑病', 4, 'para', '病原Acrospermum viticola Ikata属子囊菌亚门真菌。子囊长圆筒形，无色，无侧丝。8个子囊孢子并列于子囊内。子囊孢子线状，无性阶段产生菌丝，形成淡黄褐色的分生孢子梗，分生孢子梗具隔膜1～3个，顶端生稍膨大轴细胞1个，其上轮生分生孢子，轴细胞延长又在其上再次轮生分生孢子，依此多次产孢。分生孢子椭圆形或圆筒形，具隔膜1～4个，淡黄色，大小7.5～16.3×2～6微米。\n[2] \n');
INSERT INTO `articles` VALUES (17, '葡萄轮斑病', 5, 'level-2', '发病条件');
INSERT INTO `articles` VALUES (17, '葡萄轮斑病', 6, 'para', '该病为真菌性病害，病原菌为葡萄生扁棒壳菌（Acrospermun viticola）病原菌以子囊壳在落叶上越冬，第2年夏天温度上升、湿度加大时散发出子囊孢子，经气流传播到叶片。病原菌从叶背气孔侵入，发病后产出分生孢子进行再侵染，该病危害美洲种葡萄。因发病期较晚，对当年产量影响较轻，危害严重时引起早期落叶，影响越冬和下一季葡萄长势与结果。高温高湿是该病发生和流行的重要条件，管理粗放、植株郁闭、通风透光差的葡萄园发病重。\n[1] \n');
INSERT INTO `articles` VALUES (17, '葡萄轮斑病', 7, 'level-2', '防治方法');
INSERT INTO `articles` VALUES (17, '葡萄轮斑病', 8, 'para', '（1）农业防治。认真清洁田园，加强田间管理；发病严重的葡萄园，宜尽快淘汰美洲种葡萄，改种欧亚杂交种。');
INSERT INTO `articles` VALUES (17, '葡萄轮斑病', 9, 'para', '（2）药剂防治。参见葡萄褐斑病。防治期可稍晚一些。\n[1] \n');
INSERT INTO `articles` VALUES (18, '葡萄黑腐病', 0, 'title', '葡萄黑腐病');
INSERT INTO `articles` VALUES (18, '葡萄黑腐病', 0, 'summary', '葡萄黑腐病是真菌病害，在东北、华北等地发生较多，一般为害不重，在长江以南地区，如遇连续高温高湿天气，则发病较重，如1991年遇到特大洪涝灾害后，不少园子均发生了程度不一的黑腐病。');
INSERT INTO `articles` VALUES (18, '葡萄黑腐病', 1, 'level-2', '病状');
INSERT INTO `articles` VALUES (18, '葡萄黑腐病', 2, 'para', '主要发生在果实、叶片、叶柄和新梢上。果实被害后发病初期产生紫褐色小斑点，逐渐扩大后，边缘褐色，中央灰白色，稍凹陷，发病果软烂，而后变为干缩僵果，有明显棱角，不易脱落，病果上生出许多黑色颗粒状小突起，即病菌的分生把子器或子囊壳。叶片发病时，初期产生红褐色小斑点，逐渐扩大成近圆形病斑，直径可达4—7厘米，中央灰白色，外缘褐色，边缘黑褐色，上面生出许多黑色小突起，排入成环状。新梢受害处生褐色椭圆形病斑，中央凹陷，其上生有黑色颗粒状小突起。黑腐病和房枯病，病菌形态上的主要区别——房枯病分生孢子比黑腐病的分生孢子狭而长，子囊孢子比黑腐病的大。');
INSERT INTO `articles` VALUES (18, '葡萄黑腐病', 3, 'level-2', '侵染及发病规律');
INSERT INTO `articles` VALUES (18, '葡萄黑腐病', 4, 'para', '黑腐病菌主要以子囊壳在僵果上过冬，也可以分生孢子过冬，夏季以子囊孢子借风雨传播，有适宜的水分和湿度即可萌发侵入。孢子发芽约需36—48小时，在22—24℃时萌发约需10—12小时。在果实上潜育期8—10天。分生孢子生活力很强。8—9月高温多雨和近成熟期发病严重。在南方，其消长规律同白腐病近似。');
INSERT INTO `articles` VALUES (18, '葡萄黑腐病', 5, 'level-2', '防治措施');
INSERT INTO `articles` VALUES (18, '葡萄黑腐病', 6, 'para', '(1)清除越冬病源。(2)及时排水，增施有机肥。(3)6—9月间可喷百菌清600倍液，50%多菌灵或托布津800倍液以及1:1:200倍波尔多液。在南方，在喷药防治上要抓住花前、花后和果实生长期三个关键时间，药剂以波尔多液为主。');
INSERT INTO `articles` VALUES (19, '黑星病', 0, 'title', '黑星病');
INSERT INTO `articles` VALUES (19, '黑星病', 0, 'summary', '黑星病是一种检疫性病害，除危害黄瓜外，还可危害西葫芦、甜瓜、南瓜等。我县近年来以温室大棚黄瓜发病较重，造成黄瓜减产。现将其病害发生规律及防治措施介绍如下。');
INSERT INTO `articles` VALUES (19, '黑星病', 1, 'level-2', '基本信息');
INSERT INTO `articles` VALUES (19, '黑星病', 2, 'para', '英文病名 ：Scab of cucurbits');
INSERT INTO `articles` VALUES (19, '黑星病', 3, 'para', '学名：Cladosporium cucumerinum Ell．&Arth．');
INSERT INTO `articles` VALUES (19, '黑星病', 4, 'para', '异名 ：Scolicotrichum melophthorum Prill．&Delacr．');
INSERT INTO `articles` VALUES (19, '黑星病', 5, 'para', '分类地位 ：丝孢纲（Hyphomycetes）丝孢目（Hyphomycetales）黑色菌科（Dematiaceae）枝孢属（Cladosporium）');
INSERT INTO `articles` VALUES (19, '黑星病', 6, 'level-2', '发生规律');
INSERT INTO `articles` VALUES (19, '黑星病', 7, 'para', '黄瓜黑星病是一种真菌性病害，该病菌主要以菌丝体随病残体在土壤中或者附着在架材上越冬，也可以分生抱子附着在种子表面或以菌丝在种皮内越冬。越冬后土壤中的菌丝，在适宜的条件下，产生出分生抱子借风雨在田间传播，成为初侵染源。分生抱子萌发后，长出芽管，主要从植物叶片、果实、茎蔓的表皮直接侵入，也可从气孔和伤口侵入。发病后又产生大量的分生抱子，靠气流、雨水、灌水或农事操作等传播，进行再侵染。');
INSERT INTO `articles` VALUES (19, '黑星病', 8, 'para', '\n\n\n\n\n黄瓜黑星病\n\n');
INSERT INTO `articles` VALUES (19, '黑星病', 9, 'level-2', '发病条件');
INSERT INTO `articles` VALUES (19, '黑星病', 10, 'para', '该病害发生要求低温高湿条件，最适温为17℃左右，相对湿度要求在90%以上。孢子萌发时，要求植株叶面结露有水膜或水滴存在，以日光温室黄瓜发病较重。种植密度大，光照少，通风不良，保护地大灌水，重茬地，肥料少等管理不当地发病重。');
INSERT INTO `articles` VALUES (19, '黑星病', 11, 'level-2', '症状表现');
INSERT INTO `articles` VALUES (19, '黑星病', 12, 'para', '幼苗期被害，子叶上出现近圆形黄白色的病斑，后发展为全叶干枯，植株停止生长或枯死；真叶被害，开始产生近圆形小斑点，呈淡黄色，后期易穿孔，穿孔后留下黑色边缘的星状孔；叶脉被害，组织坏死，引起病部周围叶组织扭皱。嫩茎被害，先呈现水渍状浅绿色的椭圆形或不规则的条斑，继而凹陷龟裂呈暗褐色，严重时病部腐烂。生长点被害，可在2－3天内烂掉，造成秃柱。卷须被害，多变褐色而腐烂。瓜条被害，病部开始出现凹陷，呈暗褐色的疮痴状，而且流出半透明的胶状物，而后变琥珀色，病组织停止生长，瓜条弯曲，瓜条病部不变软、不腐烂、湿度大时，病部产生黑色霉层。');
INSERT INTO `articles` VALUES (19, '黑星病', 13, 'level-2', '防治技术');
INSERT INTO `articles` VALUES (19, '黑星病', 14, 'para', '1．选用抗病品种 较抗品种有青杂1号。2号、丹东刺瓜、北抗选、中农11号、中农13号等抗病品种（感病品种多为津研系列和津春系列）。');
INSERT INTO `articles` VALUES (19, '黑星病', 15, 'para', '2．种子消毒 可用55℃温水浸种15分钟或用25%多菌灵300倍液，浸种l－2小时，洗净后催芽播种。每平主米苗床土用50%多菌灵8克处理土壤后播种。');
INSERT INTO `articles` VALUES (19, '黑星病', 16, 'para', '3、加强管理 （1）施足有机肥。基肥以有机肥为主，并增施磷钾肥，以提高植株抗病能力。（2）温度、湿度管理。棚室内防止出现低温高湿状态，白天气温保持在28－32℃，相对湿度60%，定植后至结瓜期控制浇水。（3）清除架材上的有病卷须，病田收获后，彻底清除病残体，并深埋或烧毁。');
INSERT INTO `articles` VALUES (19, '黑星病', 17, 'para', '4、药剂防治：在花前、花后各喷一次1：2：200倍式波尔多液或30%绿得保胶悬剂300-500倍液。对波尔多液及铜制剂敏感的品种可选用机油乳剂（蚧螨灵）：代森锰锌：水=10：1：500能有效地防治黑星病。此外还可选用50%甲基硫菌灵（甲基托布津）可湿性粉剂500-600倍液或50%苯菌灵可湿性粉剂1500倍液、20%氟硅唑咪鲜胺1000倍液、75%百菌清可湿性粉剂750倍液或56%嘧菌酯百菌清800倍液、25%多菌灵可湿性粉剂250倍液、50%凯克星可湿性粉剂500-600倍液、25%氧环宁乳油1000倍液、65%代森锌可湿性粉剂600倍液。为增加药液粘着性，减少雨水冲刷，可加相当于药液量1/3000-1/4000的皮胶或500公斤药液中加松香皂0.5公斤，或0.1-0.2%的\"6501\"展着剂。喷药注意均匀，使果面和叶片正反面都能着药。近有报道：在发病初期喷20%浩瀚高科氟硅唑咪鲜胺800-1000倍液，防治梨黑星病效果突出。能保水，绿化环境。');
INSERT INTO `articles` VALUES (20, '早疫病', 0, 'title', '早疫病');
INSERT INTO `articles` VALUES (20, '早疫病', 0, 'summary', '属真菌病害，苗期、成株期均可发病，苗期发病，幼苗的茎基部生暗褐色病斑，稍陷，有轮纹。成株期发病一般从下部叶片向上部发展。初期叶片呈水渍状暗绿色病斑，扩大后呈圆形或不规则轮纹斑，边缘具有浅绿色或黄色晕环。中部具同心轮纹，潮湿时病部长出黑色霉层·主要症状是病部有（同心）轮纹。');
INSERT INTO `articles` VALUES (20, '早疫病', 1, 'level-2', '发生特点');
INSERT INTO `articles` VALUES (20, '早疫病', 2, 'para', '病菌主要以菌丝体和分生孢子在病残体和种子上越冬，通过气流、灌溉水以及农事操作从气孔、伤口或表皮直接侵入传播，病菌生长适温26～28℃，高温高湿发病重。');
INSERT INTO `articles` VALUES (20, '早疫病', 3, 'level-2', '防治方法');
INSERT INTO `articles` VALUES (20, '早疫病', 4, 'para', '农业防治');
INSERT INTO `articles` VALUES (20, '早疫病', 5, 'para', '合理轮作、密植，选用抗病品种。');
INSERT INTO `articles` VALUES (20, '早疫病', 6, 'para', '（1）培育壮苗：要调节好苗床的温度和湿度，在苗子长到两叶一心时进行分苗，谨防苗子徒长。苗期喷施奥力克—霜贝尔500倍液，可防止苗期患病；');
INSERT INTO `articles` VALUES (20, '早疫病', 7, 'para', '（2）轮作倒茬：番茄应实行与非茄科作物三年轮作制；');
INSERT INTO `articles` VALUES (20, '早疫病', 8, 'para', '（3）加强田间管理：要实行高垄栽培，合理施肥，定植缓苗后要及时封垄，促进新根发生。温室内要控制好温度和湿度，加强通风透光管理。结果期要定期摘除下部病叶，深埋或烧毁，以减少传病的机会；');
INSERT INTO `articles` VALUES (21, '草莓叶枯病', 0, 'title', '草莓叶枯病');
INSERT INTO `articles` VALUES (21, '草莓叶枯病', 0, 'summary', '草莓叶枯病，主要为害叶片，是叶部的主要病害之一，叶柄、花萼。果梗也可染病。叶枯病属低温、高湿性病害，多在春秋季发病，初发病时，叶面上产生紫褐色无光泽小斑点，以后逐渐扩大成不规则形病斑。');
INSERT INTO `articles` VALUES (21, '草莓叶枯病', 1, 'level-2', '为害症状');
INSERT INTO `articles` VALUES (21, '草莓叶枯病', 2, 'para', '主要为害叶片，是叶部的主要病害之一，叶柄、花萼。果梗也可染病。叶枯病属低温、高湿性病害，多在春秋季发病，初发病时，叶面上产生紫褐色无光泽小斑点，以后逐渐扩大成不规则形病斑。病斑多沿主侧叶脉分布，发病重时整个叶面布满病斑，发病后期全叶黄褐色至暗褐色，直至枯死。叶柄或果柄发病后，病斑呈黑褐色，微凹陷，脆而易折。');
INSERT INTO `articles` VALUES (21, '草莓叶枯病', 3, 'level-2', '发病规律');
INSERT INTO `articles` VALUES (21, '草莓叶枯病', 4, 'para', '叶枯病病源为风梨草莓褐斑病菌，以子囊壳或分生孢子在病组织上越冬，春季释放出子囊壳或分生孢子，借风力传播。秋季和早春雨露较多的天气有利于浸染发病，一般健壮苗发病轻，弱苗发病重。');
INSERT INTO `articles` VALUES (21, '草莓叶枯病', 5, 'level-2', '植保要领');
INSERT INTO `articles` VALUES (21, '草莓叶枯病', 6, 'para', '1、发病期，及时清除病株残体，病果、病叶、病枝等。');
INSERT INTO `articles` VALUES (21, '草莓叶枯病', 7, 'para', '2、拉秧后彻底清除病残落叶及残体。');
INSERT INTO `articles` VALUES (21, '草莓叶枯病', 8, 'para', '3、对保护地、田间做好通风降湿，保护地减少或避免叶面结露。');
INSERT INTO `articles` VALUES (21, '草莓叶枯病', 9, 'para', '4、不偏施氮肥，增施磷、钾肥，培育壮苗，以提高植株自身的抗病力。适量灌水，阴雨天或下午不宜浇水，预防冻害。\n[1] \n');
INSERT INTO `articles` VALUES (22, '疮痂病', 0, 'title', '疮痂病');
INSERT INTO `articles` VALUES (22, '疮痂病', 0, 'summary', '疮痂病：为害嫩叶、嫩枝、幼果。受害叶片开始呈现油浸状斑点，后变蜡黄色，病斑扩展，并向一面隆起成圆锥形的瘤粒突起。如病斑聚集，叶会变成扭曲畸形，果也会变成畸形果，落叶落果严重。借风雨和昆虫传播，气温16～24℃易发病，易发生期3～5月。');
INSERT INTO `articles` VALUES (22, '疮痂病', 1, 'level-2', '简介');
INSERT INTO `articles` VALUES (22, '疮痂病', 2, 'para', '主要由真菌中痂囊腔菌属(Elsinoe)和痂圆孢属(S-phaceloma) 引起的植物病害。 细菌中黄色单胞杆\n\n\n\n\n疮痂病\n\n菌属(Xanthomonas)和放线菌中的链霉菌属 (Streptomyces)也能引起疮痂病。受害部位形成粗糙、稍隆起、疮痂状或木栓化、圆形或椭圆形的病斑，中央开裂或下陷。以茎荚、瓜果、块根、块茎受害后的症状最为明显。严重时病斑连成片、果实畸形、肉质根茎发育不良。叶片受害造成歪扭，幼株致死。常见的有柑橘疮痂病、葡萄黑痘病，其他还有大豆黑痘病、核果类疮痂病、马铃薯粉痂病等。病菌在病残组织内或枝条中越冬，地上部分的病菌通过风雨传播，地下部分的病菌靠土壤中水分的移动及地下生物的活动传播，带菌种子或患病种薯可远距离传播。一般通过清除病残组织、选用抗病品种、轮作、增施有机肥料、生长期喷药等措施防治。防治方法：①加强管理，多施钾肥，使抽出的新梢整齐而迅速成熟，搞好冬季清园和修剪，以提高树体抗病能力。②药剂保护：在春季和初夏，雨水多和气温不很高，早上雾浓露水重时发病严重，要喷药剂保护嫩叶幼果。防治溃疡病和炭疽病的药剂均可选用。');
INSERT INTO `articles` VALUES (22, '疮痂病', 3, 'level-2', '柚');
INSERT INTO `articles` VALUES (22, '疮痂病', 4, 'level-3', '概述');
INSERT INTO `articles` VALUES (22, '疮痂病', 5, 'para', '本病是柚类的主要病害，主要为害春梢、晚秋梢、冬梢的嫩叶，花和幼果。发病严重时引起大量落叶、落花、落果，被害果实畸形，品质变劣，商品等级低。此病在中国长江流域两岸省市的低温地区发病较严重。');
INSERT INTO `articles` VALUES (22, '疮痂病', 6, 'level-3', '病原');
INSERT INTO `articles` VALUES (22, '疮痂病', 7, 'para', '疮痂病的病原为半知菌亚门的一种真菌。');
INSERT INTO `articles` VALUES (22, '疮痂病', 8, 'level-3', '病状');
INSERT INTO `articles` VALUES (22, '疮痂病', 9, 'para', '受害叶片初期出现水渍状圆形小斑点，后变成蜡黄色。病斑随叶片的生长而扩大，并逐渐木栓化，向叶片一面隆起呈圆锥状疮痂，另一面则向内凹陷，病斑多的叶片扭曲畸形，严重的引起落叶；幼果受害\n\n\n\n\n疮痂病\n\n初期产生褐色斑点，逐渐扩大并转为黄褐色、圆锥形、木栓化的瘤状突起，形成许多散生或群生的瘤突，引起果实发育不良、畸形，造成早期落果，后期果实品质变劣。');
INSERT INTO `articles` VALUES (22, '疮痂病', 10, 'level-3', '发病规律');
INSERT INTO `articles` VALUES (22, '疮痂病', 11, 'para', '病菌在病部组织内越冬，发育最适温度为16～23℃，当春季阴雨潮湿天气气温在15℃以上时，产生分生孢子，通过风、雨、昆虫传播本病。春季空气湿度是决定发病严重与否的主要因素。春梢及晚秋梢抽吐期如遇阴雨连绵、早晨雾重则此病流行，夏梢期由于气温高极少发病。');
INSERT INTO `articles` VALUES (22, '疮痂病', 12, 'level-3', '防治方法');
INSERT INTO `articles` VALUES (22, '疮痂病', 13, 'para', '(1)注意苗木选择：新建果园应选用无病苗木，避免在病区运苗和取接穗。');
INSERT INTO `articles` VALUES (22, '疮痂病', 14, 'para', '(2)加强栽培管理：做好冬季清园修剪工作，剪除的病枝、落叶要集中烧毁。疏删过密枝条，提高树冠内部通风透光条件，降低树冠内空气湿度。');
INSERT INTO `articles` VALUES (22, '疮痂病', 15, 'para', '(3)喷药防治：本病仅侵染幼嫩组织，因此，必须做好春梢、晚秋梢及幼果期的喷药保护工作。第一次喷药在春芽长2毫米时，第二次在谢花期，晚秋梢期喷药视天气而定。可供选用的农药有：受侵染前喷0.3～0.5%倍量式波尔多液，或30%氧氯化铜500～600倍液，或75%百菌清可湿性粉剂500倍液，或50%退菌特可湿性粉剂500倍液预防保护。已浸染的可喷50%托布津可湿性粉剂600～800倍液等内吸杀菌剂防治。');
INSERT INTO `articles` VALUES (22, '疮痂病', 16, 'level-2', '辣椒');
INSERT INTO `articles` VALUES (22, '疮痂病', 17, 'level-3', '症状');
INSERT INTO `articles` VALUES (22, '疮痂病', 18, 'para', '发病时出现不规则形水浸状小斑点，在湿度较大时发生严重，严重时可导致落叶落果。');
INSERT INTO `articles` VALUES (22, '疮痂病', 19, 'level-3', '防治方法');
INSERT INTO `articles` VALUES (22, '疮痂病', 20, 'para', '用百菌杀+康发+绿得快喷雾，4天一次，连喷两次。');
INSERT INTO `articles` VALUES (22, '疮痂病', 21, 'level-2', '马铃薯');
INSERT INTO `articles` VALUES (22, '疮痂病', 22, 'level-3', '概述');
INSERT INTO `articles` VALUES (22, '疮痂病', 23, 'para', '症状马铃薯块茎表面先产生褐色小点，扩大后形成褐色圆形或不规则形大斑块。因产生大量木\n\n\n\n\n疮痂病\n\n栓化细胞致表面粗糙，后期中央稍凹陷或凸起虽疮痂状硬斑块。病斑仅限于皮部，不深入薯内，别于粉痂病。');
INSERT INTO `articles` VALUES (22, '疮痂病', 24, 'level-3', '病原');
INSERT INTO `articles` VALUES (22, '疮痂病', 25, 'para', 'Streptomycesscabies(Thaxter)Waks.etHenvici称疮痂链霉菌，属放线菌。菌体丝状，有分枝，极细，尖端常呈螺旋状，连续分割生成大量孢子。孢子圆筒形，大小1.2—1.5×0.8—1.0(μm)。');
INSERT INTO `articles` VALUES (22, '疮痂病', 26, 'para', '传播途径和发病条件');
INSERT INTO `articles` VALUES (22, '疮痂病', 27, 'para', '病菌在土壤中腐生或在病薯上越冬。块茎生长的早期表皮木栓化之前，病菌从皮孔或伤口侵入后染病，当块茎表面木栓化后，侵入则较困难。病薯长出的植株极易发病，健薯播入带菌土壤中也能发病。适合该病发生的温度为25—30℃，中性或微碱性砂壤土发病重，pH5.2以下很少发病。品种间抗病性有差异，白色薄皮品种易感病，褐色厚皮品种较抗病。');
INSERT INTO `articles` VALUES (22, '疮痂病', 28, 'level-3', '防治方法');
INSERT INTO `articles` VALUES (22, '疮痂病', 29, 'para', '(1)选用无病种薯，一定不要从病区调种。播前用40%福尔马林120倍液浸种4分钟。');
INSERT INTO `articles` VALUES (22, '疮痂病', 30, 'para', '(2)多施有机肥或绿肥，可抑制发病。');
INSERT INTO `articles` VALUES (22, '疮痂病', 31, 'para', '(3)与葫芦科、豆科、百合科蔬菜进行5年以上轮作。');
INSERT INTO `articles` VALUES (22, '疮痂病', 32, 'para', '(4)选择保水好的菜地种植，结薯期遇干旱应及时浇水。');
INSERT INTO `articles` VALUES (22, '疮痂病', 33, 'level-2', '番茄');
INSERT INTO `articles` VALUES (22, '疮痂病', 34, 'level-3', '症状');
INSERT INTO `articles` VALUES (22, '疮痂病', 35, 'para', '主要危害茎、叶和果实。病叶早期在叶背出现水浸状小斑，逐渐扩展近圆形或连结成不规则形黄褐色病斑，粗糙不平，病斑周围有褪绿晕圈，后期干枯质脆。茎部先出现水浸状褪绿斑点，后上下扩展呈长椭圆形，中央稍凹陷的黑褐色病斑；病果表面出现水浸状褪绿斑点，逐渐扩展，初期有油浸亮光，后呈黄褐色或黑褐色木栓化、直径0.2-0.5厘米大小近圆形粗糙枯死斑，有的相互连结成不规则形大斑块，果柄与果实连接处受害时，易落果。');
INSERT INTO `articles` VALUES (22, '疮痂病', 36, 'level-3', '发生规律');
INSERT INTO `articles` VALUES (22, '疮痂病', 37, 'para', '细菌引起的病害。病菌随病残体在田间或附着种子上越冬，第二年借风雨、昆虫传播到叶、茎或果实上，从伤口或气孔侵入危害。高温、高湿、阴雨天发病重，管理粗放，虫害重或暴风雨造成伤口多，易发病。');
INSERT INTO `articles` VALUES (22, '疮痂病', 38, 'level-3', '防治措施');
INSERT INTO `articles` VALUES (22, '疮痂病', 39, 'para', '（1）建立无病种子田，确保种子不带菌是杜绝病害传播的根本措施；种子用1%次氯酸钠溶液云大-120500倍液，浸种20-30分钟，再用清水冲洗干净后催芽播种。');
INSERT INTO `articles` VALUES (22, '疮痂病', 40, 'para', '（2）初发病时用“天达2116”800倍液“天达诺杀”1000倍液、77%多宁可湿性粉剂600倍液、70%可杀得101可湿性粉剂800倍液、新植霉素4000倍液、50%琥胶肥酸铜可湿性粉剂500倍液喷雾，每隔7-10天喷1次，连喷3次。');
INSERT INTO `articles` VALUES (22, '疮痂病', 41, 'level-2', '花生');
INSERT INTO `articles` VALUES (22, '疮痂病', 42, 'level-3', '症状');
INSERT INTO `articles` VALUES (22, '疮痂病', 43, 'para', '主要为害叶片、叶柄及茎部，病菌在病残体上越冬，翌春借风、雨传播进行初侵染和再侵染，叶片染病初叶两面产生圆形至不规则形小斑点，边缘稍隆起，中间凹陷，叶面上病斑黄褐色，叶背面为淡红褐色，具褐色边缘。叶柄、茎部染病初生卵圆形隆起的稍大病斑，多数病斑融合时，引起叶柄及茎扭曲，上端枯死。');
INSERT INTO `articles` VALUES (22, '疮痂病', 44, 'level-3', '病原菌形态特征、传播途径和发病条件');
INSERT INTO `articles` VALUES (22, '疮痂病', 45, 'para', '花生痂圆孢菌，属半知菌亚门真菌。分生孢子盘褐色或黑色，垫状或盘状。分生孢子梗单根，密集在盘上形成一橄榄色绒状层。分生孢子椭圆形至纺锤形，单胞无色，大小5～20×3～4(&micro;m)。病菌在病残体上越冬，翌春借风、雨传播进行初侵染和再侵染。');
INSERT INTO `articles` VALUES (22, '疮痂病', 46, 'level-3', '防治方法');
INSERT INTO `articles` VALUES (22, '疮痂病', 47, 'para', '花生疮痂病防治率较高，通过有效的防治一般不会造成明显的产量损失。');
INSERT INTO `articles` VALUES (22, '疮痂病', 48, 'para', '1、选用抗病品种。');
INSERT INTO `articles` VALUES (22, '疮痂病', 49, 'para', '2、轮作倒茬：与禾本科作物进行3年以上轮作。');
INSERT INTO `articles` VALUES (22, '疮痂病', 50, 'para', '2、加强田间管理：增施磷钾肥，控制氮肥供给量，在花生下针期以前应及时喷施矮壮素等化控药剂，抑制主梢疯长，促进花芽分化；在开花前期、幼果期、果实膨大期喷施地果壮蒂灵使地下果营养输导管变粗，提高地果膨大活力，增加花生的产量。');
INSERT INTO `articles` VALUES (22, '疮痂病', 51, 'para', '3、药剂防治：用2%春雷霉素500倍、或30%爱苗乳油4000倍+天达2116壮苗灵600倍+3000倍天达有机硅混合喷雾，每7—10天1次，连续喷施2次，不但能控制疮痂病的危害，还能兼治后期的叶斑病，爱苗乳油还能控制花生的徒长。');
INSERT INTO `articles` VALUES (23, '晚疫病', 0, 'title', '晚疫病');
INSERT INTO `articles` VALUES (23, '晚疫病', 0, 'summary', '');
INSERT INTO `articles` VALUES (23, '晚疫病', 1, 'level-2', '病原');
INSERT INTO `articles` VALUES (23, '晚疫病', 2, 'para', '中文学名：致病疫霉（Phytophthora infestans）');
INSERT INTO `articles` VALUES (23, '晚疫病', 3, 'para', '界：真菌界');
INSERT INTO `articles` VALUES (23, '晚疫病', 4, 'para', '门：真菌门(Eumycophyta) ');
INSERT INTO `articles` VALUES (23, '晚疫病', 5, 'para', '亚门：鞭毛菌亚门(Mastigomycotina) ');
INSERT INTO `articles` VALUES (23, '晚疫病', 6, 'para', '纲：卵菌纲(Oomycetes) ');
INSERT INTO `articles` VALUES (23, '晚疫病', 7, 'para', '目：霜霉目(Peronosporales) ');
INSERT INTO `articles` VALUES (23, '晚疫病', 8, 'para', '科：腐霉科(Pythiaceae) ');
INSERT INTO `articles` VALUES (23, '晚疫病', 9, 'para', '属：疫霉属(Phytophthora)');
INSERT INTO `articles` VALUES (23, '晚疫病', 10, 'level-2', '症状识别');
INSERT INTO `articles` VALUES (23, '晚疫病', 11, 'para', '属真菌病害，幼苗、成株均可发病，可为害叶、茎、果，但以成株期的叶片和青果受害较重。叶片染病多从下部叶片开始，形成暗绿色水渍状边缘不明显的病斑，扩大后呈褐色，湿度大时，叶背病键交界处出现白霉，干燥时病部干枯，脆而易破。 ');
INSERT INTO `articles` VALUES (23, '晚疫病', 12, 'para', '整个生育期均可发病，主要危害叶和果实，也可侵染茎部。幼苗受害，近叶柄处呈黑褐色腐烂，蔓延至茎，造成幼苗萎蔫倒伏。叶片上病斑多从叶尖或叶缘开始发生，形状不规则，呈暗绿色水渍状，后逐渐变成褐色，边缘不明显，病斑上无轮纹。茎部病斑呈暗褐色，形状不规则，稍凹陷，边缘有明显的白色霉状物。果实上病斑多发生在绿果的一侧，边缘不明显，常以云纹状向外扩展，初为暗褐色油渍状病斑，后渐变为暗褐色至棕色，病斑占果面的三分之一，被害部分深达果肉内部，果实质地硬实而不软腐，潮湿时，患病部可长出白色霉状物');
INSERT INTO `articles` VALUES (23, '晚疫病', 13, 'level-2', '发生特点');
INSERT INTO `articles` VALUES (23, '晚疫病', 14, 'para', '低温、潮湿是该病发生的主要条件，温度在18～22℃，相对湿度在95～100%时易流行。20～23℃时菌丝生长最快，借气流、雨水传播，偏氮，底肥不足，连阴雨，光照不足，通风不良，浇水过多，密度过大利于发病。是一种多次重复侵染的流行性病害。');
INSERT INTO `articles` VALUES (23, '晚疫病', 15, 'level-2', '侵染途径');
INSERT INTO `articles` VALUES (23, '晚疫病', 16, 'para', '病菌以菌丝体在带病的马铃薯块茎内越冬，也可在温室番茄上危害并越冬。在土壤中病残体不能存活，初次侵染来自田间马铃薯或温室番茄上。患病的植株在病斑上产生大量的孢子囊，借气流、雨水、流水进行再次侵染。');
INSERT INTO `articles` VALUES (23, '晚疫病', 17, 'level-2', '防治方法');
INSERT INTO `articles` VALUES (23, '晚疫病', 18, 'para', '一、农业防治\n[1] \n');
INSERT INTO `articles` VALUES (23, '晚疫病', 19, 'para', '1、轮作换茬：防止连作，应与十字花科蔬菜实行3年以上轮作，避免和马铃薯相邻种植；');
INSERT INTO `articles` VALUES (23, '晚疫病', 20, 'para', '2、培育无病壮苗：病菌主要在土壤或病残体中越冬，因此，育苗土必须严格选用没有种植过茄科作物的土壤，提倡用营养钵、营养袋、穴盘等培育无病壮苗；');
INSERT INTO `articles` VALUES (23, '晚疫病', 21, 'para', '3、加强田间管理：施足基肥，实行配方施肥，避免偏施氮肥，增施磷、钾肥。定植后要及时防除杂草，根据不同品种结果习性，合理整枝、摘心、打杈，减少养分消耗，促进主茎的生长；');
INSERT INTO `articles` VALUES (23, '晚疫病', 22, 'para', '4、合理密植：根据不同品种生育期长短、结果习性，采用不同的密植方式，如：双秆整枝的每亩，栽2000株左右，单秆整枝的每亩栽2500～3500株，合理密植，可改善田间通风透光条件，降低田间湿度，减轻病害的发生；');
INSERT INTO `articles` VALUES (23, '晚疫病', 23, 'para', '二、药剂防治');
INSERT INTO `articles` VALUES (23, '晚疫病', 24, 'para', '预防用药：');
INSERT INTO `articles` VALUES (23, '晚疫病', 25, 'para', '在预期发病时，采用嘧啶核苷类抗菌素500倍液喷施加嘧菌酯百菌清800倍液，每7—10天1次。');
INSERT INTO `articles` VALUES (23, '晚疫病', 26, 'para', '治疗用药：');
INSERT INTO `articles` VALUES (23, '晚疫病', 27, 'para', '1、发病初期，及时摘除病叶、病果及严重病枝，然后根据作物该时期并发病害情况，40%乙磷锰锌可湿性粉剂300倍液加嘧啶核苷类抗菌素500倍液喷施加嘧菌酯百菌清800倍液， 5—7天用药1次，连用2-3次。');
INSERT INTO `articles` VALUES (23, '晚疫病', 28, 'para', '2.发病较重时，清除中心病株、病叶等，及时采用中西医结合的防治方法。');
INSERT INTO `articles` VALUES (23, '晚疫病', 29, 'level-2', '发病条件');
INSERT INTO `articles` VALUES (23, '晚疫病', 30, 'para', '病菌发生的最适温度是18℃～22℃，病菌孢子囊大量形成，需有95%以上的相对湿度。当田间温度在10℃～24℃、相对湿度在75%以上时，病害均可发生。阴雨天湿度大、露水重、早晨及夜晚多雾的情况下易发病。保护地栽培比露地栽培易发病。栽培密度过大(如：番茄中、晚熟品种每亩超过3500棵，早熟品种每亩超过4000棵)，通风不良都有利于该病的发生。');
INSERT INTO `articles` VALUES (24, '番茄疮痂病', 0, 'title', '番茄疮痂病');
INSERT INTO `articles` VALUES (24, '番茄疮痂病', 0, 'summary', 'bacterial spot');
INSERT INTO `articles` VALUES (24, '番茄疮痂病', 1, 'level-2', '症状');
INSERT INTO `articles` VALUES (24, '番茄疮痂病', 2, 'para', '主要危害茎、叶和果实。病叶早期在叶背出现水浸状小斑，逐渐扩展近圆形或连结成不规则形黄褐色病斑，粗糙不平，病斑周围有褪绿晕圈，后期干枯质脆。茎部先出现水浸状褪绿斑点，后上下扩展呈长椭圆形，中央稍凹陷的黑褐色病斑；病果表面出现水浸状褪绿斑点，逐渐扩展，初期有油浸亮光，后呈黄褐色或黑褐色木栓化、直径0.2-0.5厘米大小近圆形粗糙枯死斑，有的相互连结成不规则形大斑块，果柄与果实连接处受害时，易落果。');
INSERT INTO `articles` VALUES (24, '番茄疮痂病', 3, 'level-2', '发生规律');
INSERT INTO `articles` VALUES (24, '番茄疮痂病', 4, 'para', '细菌引起的病害。病菌随病残体在田间或附着种子上越冬，第二年借风雨、昆虫传播到叶、茎或果实上，从伤口或气孔侵入危害。高温、高湿、阴雨天发病重，管理粗放，虫害重或暴风雨造成伤口多，易发病。');
INSERT INTO `articles` VALUES (24, '番茄疮痂病', 5, 'level-2', '防治措施');
INSERT INTO `articles` VALUES (24, '番茄疮痂病', 6, 'para', '（1）建立无病种子田，确保种子不带菌是杜绝病害传播的根本措施；种子用1%次氯酸钠溶液+云大-120 500倍液,浸种20-30分钟，再用清水冲洗干净后催芽播种。');
INSERT INTO `articles` VALUES (24, '番茄疮痂病', 7, 'para', '（2）初发病时用“天达2116”800倍液+“天达诺杀”1000倍液、77%多宁可湿性粉剂600倍液、70%可杀得101可湿性粉剂800倍液、新植霉素4000倍液、50%琥胶肥酸铜可湿性粉剂500倍液喷雾，每隔7-10天喷1次，连喷3次。');
INSERT INTO `articles` VALUES (25, '番茄早疫病', 0, 'title', '番茄早疫病');
INSERT INTO `articles` VALUES (25, '番茄早疫病', 0, 'summary', '番茄早疫病在我国东北、华北、华东、西南等地区都有发生，其最主要特征是不论发生在果实、叶片或主茎上的病斑，都有明显的轮纹，所以又被称作轮纹病。果实病斑常在果蒂附近，茎部病斑常在分杈处，叶部病斑发生在叶肉上。');
INSERT INTO `articles` VALUES (25, '番茄早疫病', 1, 'level-2', '简介');
INSERT INTO `articles` VALUES (25, '番茄早疫病', 2, 'para', '番茄早疫病（Tomato Early Blight）又称为“轮纹病”，各地普遍发生，是危害番茄的重要病害之一\n[1] \n。一直以来，一些地区由于推广抗病毒病而不抗早疫病的番茄品种，导致早疫病严重发生。此病原寄主范围广泛，除危害番茄外，还可危害茄子、辣椒等等茄科蔬菜作物和马铃薯。');
INSERT INTO `articles` VALUES (25, '番茄早疫病', 3, 'level-2', '危害症状');
INSERT INTO `articles` VALUES (25, '番茄早疫病', 4, 'para', '主要危害叶片，也可危害幼苗、茎和果实。幼苗染病，在茎基部产\n\n\n\n\n番茄早疫病危害症状(2张)\n\n\n\n\n\n\n生暗褐色病斑，稍凹陷有轮纹\n[2] \n。成株期叶片被害，多从植株下部叶片向上发展，初呈水浸状暗绿色病斑，扩大后呈圆形或不规则形的轮纹斑，边缘多具浅绿色或黄色的晕环，中部呈同心轮纹，潮湿时病斑上长出黑色霉层（分生孢子及分生孢子梗），严重时叶片脱落；茎部染病，病斑多在分枝处及叶柄基部，呈褐色至深褐色不规则圆形或椭圆形病斑，凹陷，具同心轮纹，有时龟裂，严重时造成断枝。青果染病，多始于花萼附近，初为椭圆形或不规则形褐色或黑色斑，凹陷，后期果实开裂，病部较硬，密生黑色霉层。叶柄、果柄染病，病斑灰褐色，长椭圆形，稍凹陷。');
INSERT INTO `articles` VALUES (25, '番茄早疫病', 5, 'level-2', '病原及侵染');
INSERT INTO `articles` VALUES (25, '番茄早疫病', 6, 'para', '早疫病是由茄链格孢菌侵染所致，在真菌分类中，属于半知菌亚门链格孢属。除发生在番茄上外，还可侵染马铃薯、茄子、辣椒等作物，其主要侵染体是分生孢子。这种棒状的分生孢子晕暗褐色，通过气流、微风、雨水溅流，传染到寄主上，通过气孔、伤口或者从表皮直接侵入。在体内繁殖多量的菌丝，然后产生孢子梗，进而产生分生孢子进行传播。一季作物收获后，病原以形成的菌丝体和分生孢子随病残组织落入土壤中进行越冬。有的分生孢子可残留在种皮上，随种子一起越冬。分生孢子比较顽固，通常条件下可存活1－1.5年。同时产生的活体菌丝可在1-45℃的广泛温度\n\n\n\n范围中生长，在26－28℃时，生长最快。侵入寄主后， 2－3天就可形成病斑。形成病斑后3－4天，在病斑上就可形成大量的分生孢子。由此而进行多次重复再侵染。在发病的各种条件中，主要条件是温度和湿度。从总的情况看，温度偏高、湿度偏大有利于发病。28－30℃时。分生孢子在水滴中35-45分钟的暂短时间内就可萌芽。初夏季节，如果多雨、多雾，分生孢子就形成的快而且多，病害就很易流行。在浙江地区一般是5月中、下旬为盛发期，在山东地区一般是6月上、中旬为盛发期。除去温、湿度条件外，发病与寄主生育期夫系也很密切。当植株进入1－3穗果膨大期时，在下部和中下部较老的叶片上开始发病，并发展迅速，然后随着叶片的向上逐渐老化而向上扩展，大量病斑和病原都存在于下部、中下部和中部植株上。当然，肥力差、管理粗放的地块发病更重。另外土质粘重者比土质砂性强的地块发病重。');
INSERT INTO `articles` VALUES (25, '番茄早疫病', 7, 'level-2', '发病规律');
INSERT INTO `articles` VALUES (25, '番茄早疫病', 8, 'para', '病菌在土壤或种子上越冬，借风雨传播，从气孔、皮孔、伤口或表皮侵入，引起发病，病菌可在田间进行多次再侵染，结果盛期发病严重。在气温20～ 25℃，相对湿度80%以上或阴雨天气，病害易流行。重茬地、低洼地、瘠薄地、浇水过多或通风不良地块发病较重。');
INSERT INTO `articles` VALUES (25, '番茄早疫病', 9, 'level-2', '防治技术');
INSERT INTO `articles` VALUES (25, '番茄早疫病', 10, 'para', '1.品种的选择');
INSERT INTO `articles` VALUES (25, '番茄早疫病', 11, 'para', '选择抗病品种，迪丽雅、欧缇丽、凯旋158等品种较抗病，在重病区可选用。一般早熟品种、窄叶品种发病偏轻，高棵、大秧、大叶品种发病偏重。');
INSERT INTO `articles` VALUES (25, '番茄早疫病', 12, 'para', '2.注意轮作');
INSERT INTO `articles` VALUES (25, '番茄早疫病', 13, 'para', '鉴于病原能有一年以上的存活期，所以要注意轮作。一般是与非茄科作物进行两年以上的轮作。在选择育苗床时，也要引起足够的重视。');
INSERT INTO `articles` VALUES (25, '番茄早疫病', 14, 'para', '3.种子的处理');
INSERT INTO `articles` VALUES (25, '番茄早疫病', 15, 'para', '在注意从无病地块、无病植株上选留种子的基础上，对采后的种子除结合其他病害的预防，用70℃干热处理72小时法进行处理外（注意采后对种子给予一定的后熟转化期），在播前可用52℃温水、自然降温处理30分钟，然后冷水浸种催芽。');
INSERT INTO `articles` VALUES (25, '番茄早疫病', 16, 'para', '4.药剂防治');
INSERT INTO `articles` VALUES (25, '番茄早疫病', 17, 'para', '（一）农业防治');
INSERT INTO `articles` VALUES (25, '番茄早疫病', 18, 'para', '（1）培育壮苗：要调节好苗床的温度和湿度，在苗子长到两叶一心时进行分苗，谨防苗子徒长。可防止苗期患病；');
INSERT INTO `articles` VALUES (25, '番茄早疫病', 19, 'para', '（2）轮作倒茬：番茄应实行与非茄科作物三年轮作制；');
INSERT INTO `articles` VALUES (25, '番茄早疫病', 20, 'para', '（3）加强田间管理：要实行高垄栽培，合理施肥，定植缓苗后要及时封垄，促进新根发生。温室内要控制好温度和湿度，加强通风透光管理。结果期要定期摘除下部病叶，深埋或烧毁，以减少传病的机会；');
INSERT INTO `articles` VALUES (25, '番茄早疫病', 21, 'para', '（二）药剂防治');
INSERT INTO `articles` VALUES (25, '番茄早疫病', 22, 'para', '以防为主\n[3] \n，从苗期始每隔7-10天喷药，带药定植。效果显著。');
INSERT INTO `articles` VALUES (25, '番茄早疫病', 23, 'para', '治疗用药：');
INSERT INTO `articles` VALUES (25, '番茄早疫病', 24, 'para', '1、发病初期，及时摘除病叶、病果及严重病枝，开始喷施杀菌农药，为防止产生抗药性提高防效，提倡轮换交替或复配使用。每7天喷1次，连喷2～3次。');
INSERT INTO `articles` VALUES (25, '番茄早疫病', 25, 'para', '2.发病较重时，清除中心病株、病叶等，及时采用中西医结合的防治方法。');
INSERT INTO `articles` VALUES (25, '番茄早疫病', 26, 'para', '3、加强管理。 施足腐熟的有机底肥，合理密植。棚室栽培时注意通风透光，降低湿度。露地栽培时，注意雨后及时排水。与非茄科作物实行2～3年轮作。早期及时摘除病叶、病果，带出田外集中销毁。番茄拉秧后及时清除田间残余植株、落花、落果，结合翻耕土地，搞好田间卫生。\n[4] \n');
INSERT INTO `articles` VALUES (25, '番茄早疫病', 27, 'para', '4、大棚内要注意保温和通风，每次洒水后一定要通风，以降低棚内空气湿度，尤其是早春定植时昼夜温差大，相对湿度高，易结露利于此病的发生和蔓延，故调整好棚内的水、火、气有机配合是防止早疫病的有效手段。\n[5] \n');
INSERT INTO `articles` VALUES (26, '番茄花叶病毒病', 0, 'title', '番茄花叶病毒病');
INSERT INTO `articles` VALUES (26, '番茄花叶病毒病', 0, 'summary', '花叶病毒病一种是在叶片上发生轻微的花叶或斑驳，植株生长发育正常，株高与健株无异，对产量影响甚微；另一种是在叶片上发生浓、淡相间的斑驳，随后新生叶片变小，叶脉变紫，叶片细长狭窄，扭曲畸形。茎顶叶片生长停滞，植株矮小，下部叶片多呈卷筒状，病株花芽分化能力弱，大量落花、落蕾，已坐果的，果形细小，果面多呈花脸状。');
INSERT INTO `articles` VALUES (26, '番茄花叶病毒病', 1, 'level-2', '危害症状');
INSERT INTO `articles` VALUES (26, '番茄花叶病毒病', 2, 'para', '（1）花叶：在叶片上发生轻微的花叶或绿色深浅不匀的斑驳，植株生长发育正常，株高与健株无异，叶片不变小，不畸形，植株不矮化，对产量影响不大。');
INSERT INTO `articles` VALUES (26, '番茄花叶病毒病', 3, 'para', '（2）在叶片上发生浓、淡相间的斑驳，随后新生叶片变小，叶脉变紫，叶片细长狭窄，扭曲畸形。茎顶叶片生长停滞，植株矮小，下部叶片多呈卷筒状，花芽分化能力减退，大量落花落蕾，已坐果的，果形细小，果小质劣呈花脸状。对产量影响很大，病株比健株减产10%～30%。');
INSERT INTO `articles` VALUES (26, '番茄花叶病毒病', 4, 'level-2', '病原形态特征');
INSERT INTO `articles` VALUES (26, '番茄花叶病毒病', 5, 'para', 'T烟草普通花叶病毒（obacco mosaic virus.），简称TMV，属+ssRNA目、（未定）科、烟草花叶病毒属病毒。病毒粒体杆状，大小300×18(nm)。钝化温度90～93℃经10分钟，稀释限点10-4～10-7，体外保毒期72～96小时。在无菌条件下致病力达数年，在干燥病组织内存活50年以上。该病毒有不同株系，我国主要有普通株系、番茄株系、黄斑株系和珠斑等4个株系，因致病力差异及与其它病毒的复合侵染而造成症状的多样性。');
INSERT INTO `articles` VALUES (26, '番茄花叶病毒病', 6, 'level-2', '侵染循环');
INSERT INTO `articles` VALUES (26, '番茄花叶病毒病', 7, 'para', '病原能在活的植物体内越冬，也能在土壤中或土壤内的病残体上越冬，还可在种子上沾附的果肉里越冬。未充分腐熟的带毒肥料也是初侵染源。适宜发病温度为20～25℃。\n\n\n\n\n番茄花叶病毒病\n\n');
INSERT INTO `articles` VALUES (26, '番茄花叶病毒病', 8, 'para', '该病主要由汁液传播，蚜虫不传病毒，在植株生长期间，主要通过各种农事操作，进行接触传染。病、健株相互摩擦，增加汁液传染机会；种植过密，增加摩擦，病害加重；田间的蝗虫、烟青虫等咀嚼式口器的昆虫也可传播TMV病毒。与茄科植物连作地块，特别是番茄连作，病害会明显加重。土壤黏重、肥力不足，排水不畅，造成植株生长不良，抗性降低，施用未腐熟或带毒有机肥，栽培在有病毒的土壤都可加重病毒传染。土壤板结，气候干旱，田间线虫 危害较重的地块发病重。');
INSERT INTO `articles` VALUES (26, '番茄花叶病毒病', 9, 'level-2', '发病因素');
INSERT INTO `articles` VALUES (26, '番茄花叶病毒病', 10, 'para', '（1）环境：花叶病的发生发展与气温有密切关系。适宜发病温度为20～25℃。');
INSERT INTO `articles` VALUES (26, '番茄花叶病毒病', 11, 'para', '（2）栽培：花叶病和条纹病主要由汁液传播，故一切粗放的农事操作，都会增加病毒的传播机会，病、健株相互摩擦，增加汁液传染机会;种植过密，增加摩擦，病害加重。与茄科植物连作地块，特别是番茄连作，病害会明显加重。土壤黏重、肥力不足，排水不畅，造成植株生长不良，往往加重病害发生。土壤中缺少钙、钾、磷等元素，花叶病发生重，增施钙、钾、磷肥能增强植株抗病力，减轻花叶病的发生。黏重且含腐殖质多的土壤保毒期长，会使病害发生严重。');
INSERT INTO `articles` VALUES (26, '番茄花叶病毒病', 12, 'level-2', '防治方法');
INSERT INTO `articles` VALUES (26, '番茄花叶病毒病', 13, 'para', '农业防治');
INSERT INTO `articles` VALUES (26, '番茄花叶病毒病', 14, 'para', '（1）采收完后及时清除病残体，在远离菜地、水源的地方处烧毁或挖坑深埋。育苗前，苗床彻底清除枯枝残叶和杂草;定植前深翻土壤，促使病残体腐烂;对病田里用过的工具、架材要进行消毒处理。');
INSERT INTO `articles` VALUES (26, '番茄花叶病毒病', 15, 'para', '（2）选用排灌方便的田块，开好排水沟，降低地下水位，达到雨停无积水；大雨过后及时清理沟系，防止湿气滞留，降低田间湿度，这是防病的重要措施。');
INSERT INTO `articles` VALUES (26, '番茄花叶病毒病', 16, 'para', '（3）施用酵素菌沤制的堆肥或腐熟的有机肥，不用带菌肥料，有机肥中不得含有植物病残体；采用配方施肥技术，适当增施磷钾肥，加强田间管理，培育壮苗，增强植株抗病力，有利于减轻病害。');
INSERT INTO `articles` VALUES (26, '番茄花叶病毒病', 17, 'para', '（4）高温干旱时应科学浇水，以提高田间湿度，减轻蚜虫、灰飞虱危害与传播其它病毒，也可在番茄行间采取铺草保湿等措施，也有利于减轻病害。严禁连续灌水和大水漫灌。');
INSERT INTO `articles` VALUES (26, '番茄花叶病毒病', 18, 'para', '（5）因地制宜，合理选用抗病品种。不要和烟草相邻种植。');
INSERT INTO `articles` VALUES (26, '番茄花叶病毒病', 19, 'para', '（6）打杈、整枝、绑蔓时先健株后病株，接触过病株的手要用肥皂水消毒，防止通过农事操作再次传播。');
INSERT INTO `articles` VALUES (27, '黑腐病', 0, 'title', '黑腐病');
INSERT INTO `articles` VALUES (27, '黑腐病', 0, 'summary', '多肉的黑腐病，是由“尖孢镰刀菌”这种真菌引起的（不同植物的黑腐病致病真菌不一定一样），尖孢镰刀菌是一类既可侵染植物又可在土壤内生存的兼性寄生真菌。和其它植物病原菌一样，存在着种下分化，可侵染许多植物寄主。');
INSERT INTO `articles` VALUES (27, '黑腐病', 1, 'level-2', '症状');
INSERT INTO `articles` VALUES (27, '黑腐病', 2, 'para', '主要为害叶片。病斑从下部叶片逐渐扩展至上部叶片。叶片表面有部分黄变，其背面出现灰白色轮廓不分明的病斑，密生灰白色霉层。不久之后，叶表面也生成霉层，最后，叶片卷曲干枯，导致坐果不良，果实生长不良，早期着色。 温室、大棚栽培时常发病。在拱棚栽培中，主要发生在覆盖过程中，但湿度大时，去掉塑料薄膜之后也仍继续蔓延。');
INSERT INTO `articles` VALUES (27, '黑腐病', 3, 'level-2', '病原');
INSERT INTO `articles` VALUES (27, '黑腐病', 4, 'para', '叶霉病菌除了附着在温室、大棚、小拱棚等的玻璃、塑料薄膜、支架、支柱等表面生存之外，也残留在病叶上形成传染源。此外，亦可经种子传染。 病斑上生成的分生孢子被风吹至叶片 上，萌发出芽管后其菌丝从气孔处侵入，约2周后发病，在叶背面生成众多的分生孢子。这些分生孢子被风吹散，逐渐蔓延。叶霉病菌喜好20—25℃的温度和95%以上的湿度。');
INSERT INTO `articles` VALUES (27, '黑腐病', 5, 'level-2', '防治方法');
INSERT INTO `articles` VALUES (27, '黑腐病', 6, 'para', '换气不充分的早春、晚秋及冬季的温室和大棚栽培的番茄都可以发病。密植及灌水过多易发病，生长衰竭，尤其是收获期脱肥时，发病率很高。作为预防对策，要栽培抗性品种，注意换气，避免过度灌水和密植。');
INSERT INTO `articles` VALUES (27, '黑腐病', 7, 'level-2', '注意事项');
INSERT INTO `articles` VALUES (27, '黑腐病', 8, 'para', '1. 及时对整株萎蔫、根系病变较重的植株应剪断其根茎，并对病穴进行重点杀菌处理。');
INSERT INTO `articles` VALUES (27, '黑腐病', 9, 'para', '2．用药3天内遇大雨，应于雨后1天再次使用一次。及时铲除病株并进行药剂杀菌处理。');
INSERT INTO `articles` VALUES (27, '黑腐病', 10, 'para', '3、雨季病区烟田深挖排水沟，及时排水，并合理布局排水沟渠，避免病区水流向健区，造成交叉感染。');
INSERT INTO `articles` VALUES (27, '黑腐病', 11, 'para', '4、灌药时要将整个植株根系充分灌透。');
INSERT INTO `articles` VALUES (28, '褐斑病', 0, 'title', '褐斑病');
INSERT INTO `articles` VALUES (28, '褐斑病', 0, 'summary', '褐斑病(又称立枯丝核疫病 brown spot)，主要是由立枯丝核菌引起的一种真菌病害。广泛分布于世界各地，可以侵染所有草坪草，其中尤以冷季型草坪禾草受害最重，造成草坪植株死亡，使草坪形成大面积秃斑，极大地破坏草坪景观。');
INSERT INTO `articles` VALUES (28, '褐斑病', 1, 'level-2', '病原');
INSERT INTO `articles` VALUES (28, '褐斑病', 2, 'para', '褐斑病的病原为尾孢菌Cercospora insulana Sacc，分生孢子梗淡褐色，束生。分生孢子无色，鞭形，具3-7个隔膜。大小30-983.4-4微米。本菌生长发育最适温度27-30℃，37℃以上或5℃以下停止发育，致死温度为45℃，10分钟，分生孢子萌发最适温度26-31℃，最适相对湿度98%-100%，以水滴状最好。');
INSERT INTO `articles` VALUES (28, '褐斑病', 3, 'para', '褐斑病的病原Cerospora ipomoeoeae Wint.属真菌半知菌亚门，丝孢目，尾孢属，番薯尾孢。分生孢子梗多根束生，暗褐色，25-200微米。分生孢子针形，无色，基部平切，20-200毫米。');
INSERT INTO `articles` VALUES (28, '褐斑病', 4, 'para', '病原物(pathogen)');
INSERT INTO `articles` VALUES (28, '褐斑病', 5, 'para', '1.大褐斑病 Phaeoisariopsis vitis (Lev.) Sawada.,异名：Cercospora viticala (Ces.) Sacc．分生孢子梗常10~30梗集结成束状，直立, 暗褐色，单个分生孢子梗大小92～225x2.8～4微米>有1～6个隔膜。老熟的分生孢子梗先端常有1～2个孢痕。分生孢子着生于分生孢子梗顶端，长棍棒状，微弯曲，基部稍膨大，上部渐狭小，有0～9个隔膜，褐色至暗褐色，大小12～64x3.2—6.8微米。');
INSERT INTO `articles` VALUES (28, '褐斑病', 6, 'para', '2.小褐斑病 Cercospora roseleri (Caff.) Sace. 分生孢子梗较短，松散不集结成束，淡褐色。分生孢于长柱形，直或稍弯，有3～5个分隔，棕色。\n\n\n\n\n褐斑病\n\n');
INSERT INTO `articles` VALUES (28, '褐斑病', 7, 'level-2', '症状识别');
INSERT INTO `articles` VALUES (28, '褐斑病', 8, 'para', '真菌性病害，下部叶片开始发病，逐渐向上部蔓延，初期为圆形或椭圆形，紫褐色，后期为\n\n\n\n\n褐斑病\n\n黑色，直径为5-10mm，界线分明，严重时病斑可连成片，使叶片枯黄脱落，影响开花。');
INSERT INTO `articles` VALUES (28, '褐斑病', 9, 'para', '该病全年都可发生，但以高温高湿的多雨炎热夏季为害最重。单株受害叶片、叶鞘、茎秆或根部，出现梭形、长条形、不规则形病斑，病斑内部青灰色水浸状，边缘红褐色，以后病斑变成黑褐色，腐烂死亡。草坪上开始发病时，常出现大小不等的近圆形枯草斑。条件适合时，病害发展很快，枯草斑直径可从几厘米扩展到一二米。由于病斑中心的病株可以恢复，结果使枯草斑多呈“蛙眼”状，即中央绿色，边缘为枯黄色环带。在清晨有露水或高湿时，枯草圈外缘(与枯草圈交界处)出现由萎蔫的新病株组成的暗绿色至黑褐色的浸润圈，即“烟圈”(由病菌的菌丝形成)。这种现象只是在叶片很湿或空气湿度很高时才可能出现。在病鞘、茎基部还可看到由菌丝聚集形成的初为白色后变成黑褐色的菌核，易脱落。在修剪较高的多年生黑麦草、草地早熟禾、高羊茅草坪上，通常没有烟圈。有经验的草坪管理人员，在病害出现之前12至24小时能闻到一种霉味，有时一直到发病后。若病株散生于草坪中，就无明显枯草斑。该病还可在冷凉的春季和秋季引致黄斑症状(也称为冷季或冬季型褐斑)。结缕草受害以健康草株中镶嵌着枯死株的环状斑为典型症状。褐斑病的症状表现很复杂，常受草种类型、品种组合、气象条件及病原菌的株系、立地环境和养护管理水平等方面影响。');
INSERT INTO `articles` VALUES (28, '褐斑病', 10, 'level-2', '发病特点');
INSERT INTO `articles` VALUES (28, '褐斑病', 11, 'para', '病原以菌丝体或分生孢子器在枯叶或土壤里越冬，错助风雨传播夏初开始发生，秋季、危害严重，高温高湿、光照不足、通风不良、连作等均有利于病害发生。');
INSERT INTO `articles` VALUES (28, '褐斑病', 12, 'level-2', '发病规律');
INSERT INTO `articles` VALUES (28, '褐斑病', 13, 'para', '病菌以茵核或在植物残体上的菌丝渡过不良环境条件。菌核有很强的耐高低温能力，侵染、\n\n\n\n\n褐斑病\n\n发病适温为21℃—32℃。由于丝核菌寄生能力较弱，对于处于良好生长环境中的禾草，只能造成轻微发病。只有当冷季型禾草生长于不利的高温条件、抗病性下降时，才有利于病害的发展，因此，发病盛期主要在夏季。当气温升至大约30℃，同时空气湿度很高(降雨、有露、吐水或潮湿天气等)，且夜间温度高于20℃时、造成病害猖獗。另外，枯草层较厚的老草菌源量大，发病重。低洼潮湿、排水不良、田间郁闭、气候温度高，偏施氮肥，植株旺长，组织柔嫩，冻害，灌水不当等因素都极有利于病害的流行。');
INSERT INTO `articles` VALUES (28, '褐斑病', 14, 'level-2', '防治方法');
INSERT INTO `articles` VALUES (28, '褐斑病', 15, 'para', '在高温高湿天气来临之前或其间，要少施或不施氮肥，保持一定量的磷、钾肥，避免串灌和漫灌，特别要避免傍晚灌水。在草坪出现枯斑时，应在早晨尽早去掉吐水(或露水)，有助于减轻病情。夏季剪草不要过低(一般在5-6厘米)，过密草坪要适当打孔、梳草，枯草和修剪后的残草要及时清除，应种植抗(耐)病品种，要进行及时有效的化学防治。');
INSERT INTO `articles` VALUES (28, '褐斑病', 16, 'level-2', '一品红');
INSERT INTO `articles` VALUES (28, '褐斑病', 17, 'level-3', '症状');
INSERT INTO `articles` VALUES (28, '褐斑病', 18, 'para', '为害叶片。初为黄褐色小斑点，有时1片叶上有数十个病斑点。小斑点逐渐扩大为直径1-2\n\n\n\n\n褐斑病\n\n厘米病斑，内部淡褐色而周边略呈角型浓淡有差别的同心轮纹病斑。病重时病斑直径可达2-3厘米。病健交接暗灰色部分发生灰白色绵毛状霉。');
INSERT INTO `articles` VALUES (28, '褐斑病', 19, 'level-3', '病原');
INSERT INTO `articles` VALUES (28, '褐斑病', 20, 'para', 'Corynes pora cassiicola (Cooke) Lindau 称棒孢菌，病菌孢子多生于叶面，背面也可产生，但较少。分生孢子梗多数单生，细长，不分枝，具1-7个隔膜，浅褐至黄褐色。分生孢子顶生，具厚壁，分生孢子基部膨大，倒棍棒形或圆柱形，直立或弯曲。');
INSERT INTO `articles` VALUES (28, '褐斑病', 21, 'level-3', '传染途径');
INSERT INTO `articles` VALUES (28, '褐斑病', 22, 'para', '1、 以分生孢子丛或菌丝体在土中的病残体上越冬，菌丝或孢子在病残体上可存活6个月。 2、 病菌还可产生厚垣孢子及菌核，渡过不良环境。翌年产出分生孢子借气流或雨水飞溅传播，进行初侵染。 3、 病部新生的孢子，进行再侵染。在生长季节，再侵染多次发生，逐渐蔓延。 4、 病菌侵入后潜育期一般6-7天，高湿或通风透气不良易发病。温差大有利于发病。');
INSERT INTO `articles` VALUES (28, '褐斑病', 23, 'level-3', '防治方法');
INSERT INTO `articles` VALUES (28, '褐斑病', 24, 'para', '1、 彻底清除病残体，减少初侵染源。');
INSERT INTO `articles` VALUES (28, '褐斑病', 25, 'para', '2、 搞好温室内温湿度管理，注意放风排湿，改善通风透气性能。');
INSERT INTO `articles` VALUES (28, '褐斑病', 26, 'level-2', '郁金香');
INSERT INTO `articles` VALUES (28, '褐斑病', 27, 'level-3', '症状');
INSERT INTO `articles` VALUES (28, '褐斑病', 28, 'para', '受侵染的叶芽发育不良，呈畸型卷曲状，嫩芽受害后长起来的叶即变卷曲。如环境潮湿，病组织上会产生大量灰霉状分生孢子，殃及邻近健株而使叶片感病，因此鳞茎的生长受到极大影响。 花受害后，开始出现白色或浅黄褐色病斑，随即迅速扩大而枯落，或变为褐色而干枯。花梗上也会出现环带状，上面有时发生分生孢子层。该病发生的每个阶段都极易感染花朵。病鳞茎外壳上生有菌核，或者在外鳞片上出现圆形、椭圆形凹斑，中心灰黄色，边沿褐色，里边包含有一个至数个菌核。');
INSERT INTO `articles` VALUES (28, '褐斑病', 29, 'level-3', '病原');
INSERT INTO `articles` VALUES (28, '褐斑病', 30, 'para', '郁金香葡萄孢[Botrytis tulipae Lind.]属半知菌亚门，常见于郁金香栽培地区，雨季更加严重。它地分生孢子梗上生无色或灰色的分生孢子，菌核黑色，直径1～2毫米，表面平坦，圆或椭圆形。 传染途径： 菌丝和菌核残留在腐烂的鳞茎和土壤中越冬。病鳞茎种植后，受害枯死幼芽上产生大量分生孢子，是地上部病害的侵染来源。');
INSERT INTO `articles` VALUES (28, '褐斑病', 31, 'level-3', '发病条件');
INSERT INTO `articles` VALUES (28, '褐斑病', 32, 'para', '病菌在5℃和湿度达90%～100%时即能产生分生孢子。春季低温、雨天多湿时发病重。');
INSERT INTO `articles` VALUES (28, '褐斑病', 33, 'level-3', '防治方法');
INSERT INTO `articles` VALUES (28, '褐斑病', 34, 'para', '1、栽种前去除病鳞茎，并实行轮栽。');
INSERT INTO `articles` VALUES (28, '褐斑病', 35, 'para', '2、一旦发现有呈该病症状的芽或花苞，立即除去，以控制病害发展。');
INSERT INTO `articles` VALUES (28, '褐斑病', 36, 'para', '3、栽种前将鳞茎放2%福尔马林液中浸泡30分种，晾干栽种，以减少残留病菌。');
INSERT INTO `articles` VALUES (28, '褐斑病', 37, 'para', '4、生长期可喷洒50%扑海因1000倍液或50%速克灵2000倍液或50%多霉灵1000倍液。');
INSERT INTO `articles` VALUES (28, '褐斑病', 38, 'level-2', '桂花');
INSERT INTO `articles` VALUES (28, '褐斑病', 39, 'level-3', '发病规律');
INSERT INTO `articles` VALUES (28, '褐斑病', 40, 'para', '病菌以菌丝块在病叶和病落叶上越冬，次年在温、湿度适宜时即侵染发病，并产生分生孢子，然后再侵染发病。病菌以气流和水滴传播。此病在4～10月均有发生，以多雨季节和年份发病严重，并以7～8月病害蔓延最快。老叶发病娇嫩叶为重，生长衰弱和当年移栽的植株容易发病。桂花不同品种对褐斑病的抗菌力互有差别，丹桂类抗病力强于金桂和银桂类品种。');
INSERT INTO `articles` VALUES (28, '褐斑病', 41, 'level-3', '病原');
INSERT INTO `articles` VALUES (28, '褐斑病', 42, 'para', '隶属半知菌亚门、丝孢纲、丝孢目的木犀生尾孢。子座近圆形，直径12～24μm，褐色。分生孢子梗不分枝，0～1分隔，无膝状节，淡榄褐色，顶端近无色，（3～3.5）μm×（8～30）μm，通常12～32根束生。分生孢子近无色至淡榄褐色，倒棒形，隔膜1～9个，直立至微弯曲，基部倒圆锥形，顶端略钝，（2.5～4）μm×（10～62）μm。');
INSERT INTO `articles` VALUES (28, '褐斑病', 43, 'level-3', '防治方法');
INSERT INTO `articles` VALUES (28, '褐斑病', 44, 'para', '1、苗木出圃时，清除病叶，喷洒高锰酸钾1 000倍液消毒。');
INSERT INTO `articles` VALUES (28, '褐斑病', 45, 'para', '2、 选择无病株做繁殖母株。');
INSERT INTO `articles` VALUES (28, '褐斑病', 46, 'para', '3、 加强栽培管理，结合树冠整形，剪除弱病枝，调整枝叶疏密度，增强树势。');
INSERT INTO `articles` VALUES (28, '褐斑病', 47, 'para', '4、 生长季节发病初期整株树体喷药保护，如70%可杀得300～500倍液，阿米西达1 000～1 500倍液或 50%多菌灵500～600倍液。');
INSERT INTO `articles` VALUES (28, '褐斑病', 48, 'level-2', '凤仙花');
INSERT INTO `articles` VALUES (28, '褐斑病', 49, 'level-3', '症状');
INSERT INTO `articles` VALUES (28, '褐斑病', 50, 'para', '凤仙花褐斑病又称凤仙花叶斑病，在我国南北各地均有发生。病害主要发生在叶片上。叶面病斑初为浅黄褐色小点，后扩展成圆形或椭圆形，以后中央变成淡褐色，边缘褐色，具有不明显的轮纹。严重感病的叶片上，病斑连片，导致叶片变褐枯黄，直至植株死亡。');
INSERT INTO `articles` VALUES (28, '褐斑病', 51, 'level-3', '病原');
INSERT INTO `articles` VALUES (28, '褐斑病', 52, 'para', '为真菌尾孢属的一种Cercospora fukushiana (Matsuura)。');
INSERT INTO `articles` VALUES (28, '褐斑病', 53, 'level-3', '传染途径');
INSERT INTO `articles` VALUES (28, '褐斑病', 54, 'para', '病菌在凤仙花病残体及土壤植物碎片上越冬。翌年当环境条件适宜时，病菌借风雨飞散传播。高温多雨的季节，易于发病。');
INSERT INTO `articles` VALUES (28, '褐斑病', 55, 'level-3', '防治方法');
INSERT INTO `articles` VALUES (28, '褐斑病', 56, 'para', '1、 凤仙花喜肥沃的砂质壤土，不耐涝。因此，种植以砂质壤土为宜，以利排水；盆栽凤仙花，雨后应及时倒盆。秋末应将病叶、病株集中销毁，减少来年传染源。');
INSERT INTO `articles` VALUES (28, '褐斑病', 57, 'para', '2、 发病初期用25%多菌灵可湿性粉剂300-600倍液，或50%甲基托布津1000倍液，或75%百菌清1000倍液防治。');
INSERT INTO `articles` VALUES (28, '褐斑病', 58, 'level-2', '牵牛花');
INSERT INTO `articles` VALUES (28, '褐斑病', 59, 'level-3', '症状');
INSERT INTO `articles` VALUES (28, '褐斑病', 60, 'para', '叶上病斑散生于叶脉间及叶缘，近圆形至多角形，直径2-6毫米，褐色，或中央暗灰色，边缘深褐色，病斑表面生淡黑色霉层，严重时病斑布满全叶，叶片早枯。');
INSERT INTO `articles` VALUES (28, '褐斑病', 61, 'level-3', '病原');
INSERT INTO `articles` VALUES (28, '褐斑病', 62, 'para', 'Cerospora ipomoeoeae Wint.属真菌半知菌亚门，丝孢目，尾孢属，番薯尾孢。分生孢子梗多根束生，暗褐色，25-200微米。分生孢子针形，无色，基部平切，20-200毫米。');
INSERT INTO `articles` VALUES (28, '褐斑病', 63, 'level-3', '防治方法');
INSERT INTO `articles` VALUES (28, '褐斑病', 64, 'para', '1、适当密植，避免浇水过量；喷施植宝素7500倍液，促使植株早生快发。');
INSERT INTO `articles` VALUES (28, '褐斑病', 65, 'para', '2、喷洒75%百菌清可湿性粉剂1000倍液加70%甲基硫菌可湿性粉剂1000倍液，或40%多.悬乳剂600们倍液、50%速克灵可湿性粉剂2000倍液，隔7-10天1次，连续防治2-3次。');
INSERT INTO `articles` VALUES (28, '褐斑病', 66, 'level-2', '翠菊');
INSERT INTO `articles` VALUES (28, '褐斑病', 67, 'level-3', '症状');
INSERT INTO `articles` VALUES (28, '褐斑病', 68, 'para', '该病是翠菊的一种常见叶部病害，严重时，叶片枯黄，导致全株枯萎。 初期在叶片在出现近圆形大小不一的紫褐色病斑，后变成黑色或黑褐色。后期病斑中心转浅灰色，再现细小黑点。叶上病斑较多时，全叶变黄干枯。一般从下部叶片开始，顺次向上枯死。');
INSERT INTO `articles` VALUES (28, '褐斑病', 69, 'level-3', '病原');
INSERT INTO `articles` VALUES (28, '褐斑病', 70, 'para', '为壳针孢属的一种真菌Septoria chryrsan-themella Sacc.');
INSERT INTO `articles` VALUES (28, '褐斑病', 71, 'level-3', '传染途径');
INSERT INTO `articles` VALUES (28, '褐斑病', 72, 'para', '病菌以菌丝体和分生孢子器在病株残体上越冬，翌年风雨传播。北方7～8月高温多雨，发病严重。植株植过密时，易于发病。');
INSERT INTO `articles` VALUES (28, '褐斑病', 73, 'level-3', '防治方法');
INSERT INTO `articles` VALUES (28, '褐斑病', 74, 'para', '1、选择排水良好的地段种植。种植密度要适当。选择抗病品种。发现病叶立即摘除。秋末，收集病落叶和病残体集中毁。');
INSERT INTO `articles` VALUES (28, '褐斑病', 75, 'para', '2、喷施50%多菌灵可湿性粉剂1000倍液，或80%代森锌可湿性粉剂500～700倍液，或1%波尔多液，或755百菌清500倍液防治。每隔7～10天喷施1次。');
INSERT INTO `articles` VALUES (29, '苹果灰斑病', 0, 'title', '苹果灰斑病');
INSERT INTO `articles` VALUES (29, '苹果灰斑病', 0, 'summary', '苹果灰斑病全国各苹果产区都有发生。主要为，也可为害枝条、嫩梢及果实，可引起苹果树早期落叶。引起苹果灰斑病的有2种：');
INSERT INTO `articles` VALUES (29, '苹果灰斑病', 1, 'level-2', '症状');
INSERT INTO `articles` VALUES (29, '苹果灰斑病', 2, 'para', '梨叶点霉主要为害叶片。也可为害枝条、嫩梢及果实。叶片受害，初期产生近圆形黄褐色、边缘清晰的病斑，以后病斑变灰色。高温多雨季节病斑迅速扩大成不规则形，多个病斑密集或互相联合使叶片呈焦枯状。，病斑中散生多个小黑点。');
INSERT INTO `articles` VALUES (29, '苹果灰斑病', 3, 'para', '叶生棒盘孢为害叶片，病斑近圆形至圆形，少数不规则形，灰色至褐色，边缘不整齐，上生小黑点。');
INSERT INTO `articles` VALUES (29, '苹果灰斑病', 4, 'level-2', '病原');
INSERT INTO `articles` VALUES (29, '苹果灰斑病', 5, 'para', '引起苹果灰斑病的病原物有2种：梨叶点霉（Phyllosticta pirina Sacc.）和叶生棒盘孢（Coryneum foliicolum）。');
INSERT INTO `articles` VALUES (29, '苹果灰斑病', 6, 'para', '梨叶点霉（Phyllosticta pirina Sacc.）属于半知菌亚门腔孢纲球壳孢目。病部的小黑点为该菌的分生孢子器，埋生于表皮下，球形或扁球形，直径96～168微米，上端有一孔口，深褐色。分生孢子无色、单胞，卵圆形或椭圆形，大小为3．4～6．9×2．4～4．5微米，一般为6．2×3.2微米。\n[1] \n');
INSERT INTO `articles` VALUES (29, '苹果灰斑病', 7, 'para', '叶生棒盘孢（Coryneum foliicolum），属腔孢纲黑盘孢目，黑盘孢科棒盘孢属，分生孢子盘散生，黑色，直径80～112微米。分生孢子梗线性，无色。分生孢子椭圆形至长椭圆形、近梭形，榄褐色，直，3个隔膜，基部略尖，顶端钝圆，12～18微米×4.5～6微米。');
INSERT INTO `articles` VALUES (29, '苹果灰斑病', 8, 'para', '。次年春季环境条件适宜时，产生分生孢子随风、雨传播。北方果区5月中、下旬开始发病，7～8月为发病盛期。一般在秋季发病较多，为害也较重。该病的发生、流行与气候、品种密切相关。高温、高湿、降雨多而早的年份发病早且重。国光品种易感病。');
INSERT INTO `articles` VALUES (29, '苹果灰斑病', 9, 'level-2', '防治方法');
INSERT INTO `articles` VALUES (29, '苹果灰斑病', 10, 'para', '1、标本兼治：');
INSERT INTO `articles` VALUES (29, '苹果灰斑病', 11, 'para', '疑难病害的防治仅靠见症治症，特别是仅靠杀菌是不可行的，往往已经造成严重的损失，只有复壮树（株）体，增强抗逆性，解决病菌侵染途径，在生理健壮的基础上辅之以杀菌，才能达到标本兼治的效果。');
INSERT INTO `articles` VALUES (29, '苹果灰斑病', 12, 'para', '2：早防重治　　菌源一旦入园难于根除，真菌病源能够远距离传播，生理性病害（裂果及日灼）一旦形成在树势较弱免疫力低下的情况下难以根除。所以要加强日常管理从极早杀菌和复壮树体入手，解决侵染菌源的数量和侵染条件（伤口、气孔）问题。在发病条件来临（具备）前、在症状初显时，采用得力的方案尽最大可能杀灭病菌解除病患，起到早防无病、病轻，重治是速效、省钱、省工的效果。　　3：中西结合');
INSERT INTO `articles` VALUES (29, '苹果灰斑病', 13, 'para', '中（草）药制剂具有多种成份复方而成、调理生长、提高作物免疫力、杀菌广谱、持效期长、由内而外防病治病的优势，单一成份的常规用药具有速效（抗药性较大）的特性，中西结合、复配使用有利于优势互补，在重症治疗时保障效果。');
INSERT INTO `articles` VALUES (29, '苹果灰斑病', 14, 'para', '4：营养复壮');
INSERT INTO `articles` VALUES (29, '苹果灰斑病', 15, 'para', ' 常规生产规程中所采用的化学药，大多具有抑制生长甚至伤害作物的特征，其作用机理只能通过杀菌来达到防病治病的效果，不具有营养复壮的作用。而靓果安是药食同源、药肥双效，营养复壮，有利于展叶，叶绿体创造的叶绿素多，创造的有机质多，树（株）体必然健壮。');
INSERT INTO `articles` VALUES (29, '苹果灰斑病', 16, 'para', '果实套袋后，要及时进行喷药防治。在6月中旬至8月期间，要视降雨和田间发生情况，每间隔15天左右喷药防治一次。果园病害尚未发生前，要喷洒好保护性杀菌剂，病害发生后应配合使用内吸性杀菌剂。保护性杀菌剂可选用1：2.5～3：200倍波尔多液，或50%代森锰锌可湿性粉剂500～600倍液、或80%大生M－45可湿性粉剂600－800倍液等；内吸性杀菌剂可选用25%戊唑醇可湿性粉剂2000～2500倍液、或10%苯醚甲环唑水分散颗粒剂1500～2500倍液、或70%甲基硫菌灵可湿性粉剂800～1000倍液、或50%多菌灵可湿性粉剂800～1000倍液。喷药要周到细致，树冠内外、上下和叶片的正反两面都要均匀着药\n[1] \n。');
INSERT INTO `articles` VALUES (30, '辣椒疮痂病', 0, 'title', '辣椒疮痂病');
INSERT INTO `articles` VALUES (30, '辣椒疮痂病', 0, 'summary', '辣椒疮痂病又名细菌性斑点病，主要危害叶片、茎蔓、果实；叶片染病后初期出现许多圆形或不规则状的黑绿色至黄褐色斑点，有时出现轮纹，叶背面稍隆起，水泡状，正面稍有内凹；茎蔓染病后病斑呈不规则条斑或斑块；果实染病后出现圆形或长圆形墨绿色病斑，直径0.5厘米左右，边缘略隆起，表面粗糙，引起烂果。');
INSERT INTO `articles` VALUES (30, '辣椒疮痂病', 1, 'level-2', '病原');
INSERT INTO `articles` VALUES (30, '辣椒疮痂病', 2, 'para', 'Xanthomonas campestris pv．Vesicatoria称野油菜黄单胞辣椒斑点病致病型。属细菌，菌体杆状，两端钝圆，具极生单鞭毛，能游动(图2-23)。菌体排列链状，有荚膜，革兰氏阴性，好气。');
INSERT INTO `articles` VALUES (30, '辣椒疮痂病', 3, 'level-2', '发生规律');
INSERT INTO `articles` VALUES (30, '辣椒疮痂病', 4, 'para', '病原细菌主要在种子表面越冬，也可随病残体在田间越冬。旺长期易发生，病菌从叶片上的气孔侵入，潜育期3～5天；在潮湿情况下，病斑上产生的灰白色菌脓借雨水飞溅及昆虫作近距离传播。发病适温27～30℃，高温高湿条件时病害发生严重，多发生于7～8月份，尤其在暴风雨过后，容易形成发病高峰。高湿持续时间长，叶面结露对该病发生和流行至关重要。');
INSERT INTO `articles` VALUES (30, '辣椒疮痂病', 5, 'level-2', '防治措施');
INSERT INTO `articles` VALUES (30, '辣椒疮痂病', 6, 'para', '1、合理轮作，露地辣椒可与葱蒜、水稻或大豆实行2--3年轮作；应选用排水良好的沙壤土，移栽前大田应浇足底水，施足底肥，并对地表喷施消毒药剂加新高脂膜对土壤进行消毒处理。');
INSERT INTO `articles` VALUES (30, '辣椒疮痂病', 7, 'para', '2、播种前可用55°C温水加新高脂膜浸种15分钟后移入冷水中冷却，后催芽播种。加强苗期管理，适期定植，促早发根，合理密植，移栽后应喷施新高脂膜防止地上水分不蒸发，苗体水分不蒸腾，缩短缓苗期，使辣椒茁壮成长。');
INSERT INTO `articles` VALUES (30, '辣椒疮痂病', 8, 'para', '3、加强田间管理，应及时深翻土壤，加强松土、浇水、追肥，促进根系发育，提高植株抗病力，并注意氮、磷、钾肥的合理搭配；同时在辣椒生长期喷施辣椒壮蒂灵提高授粉质量，果蒂增粗，防止落叶、落花、落果，使辣椒着色早、辣味香浓。');
INSERT INTO `articles` VALUES (31, '玉米叶斑病', 0, 'title', '玉米叶斑病');
INSERT INTO `articles` VALUES (31, '玉米叶斑病', 0, 'summary', '主要为害叶片和苞叶。病斑不规则、透光，中央灰白色，边缘褐色，上生黑色小点，即病原菌的子囊座。分布在吉林、辽宁、河南等省。');
INSERT INTO `articles` VALUES (31, '玉米叶斑病', 1, 'level-2', '为害症状');
INSERT INTO `articles` VALUES (31, '玉米叶斑病', 2, 'para', '主要为害叶片和苞叶。病斑不规则、透光，中央灰白色，边缘褐色\n\n\n\n\n玉米叶斑病\n[1]\n\n\n，上生黑色小点，即病原菌的子囊座。\n[2] \n');
INSERT INTO `articles` VALUES (31, '玉米叶斑病', 3, 'level-2', '形态特征');
INSERT INTO `articles` VALUES (31, '玉米叶斑病', 4, 'para', '病菌子囊座生在叶片两面，散生或聚生，初埋生，具圆形孔口露在表皮外。子囊矩圆形，近棒形至囊形，大小35～55×7.5～8.5微米。子囊孢子矩圆形，无色，具隔膜1个，隔膜处稍缢缩，细胞中具油滴1～2个，大小14～16×3.5～4微米。\n[2] \n');
INSERT INTO `articles` VALUES (31, '玉米叶斑病', 5, 'level-2', '传播途径');
INSERT INTO `articles` VALUES (31, '玉米叶斑病', 6, 'para', '病菌在病残体上越冬，翌年春季形成子囊孢子，进行初侵染。\n[2] \n');
INSERT INTO `articles` VALUES (31, '玉米叶斑病', 7, 'level-2', '发病条件');
INSERT INTO `articles` VALUES (31, '玉米叶斑病', 8, 'para', '冷湿条件易发病。\n[2] \n');
INSERT INTO `articles` VALUES (31, '玉米叶斑病', 9, 'level-2', '防治方法');
INSERT INTO `articles` VALUES (31, '玉米叶斑病', 10, 'para', '(1)及时收集病残体烧毁。');
INSERT INTO `articles` VALUES (31, '玉米叶斑病', 11, 'para', '(2)结合防治玉米其它叶斑病，及早喷洒75%百菌清可湿性粉剂1000倍液加70%甲基硫菌灵可湿性粉剂1000倍液或75%百菌清可湿性粉剂1000倍液加70%代森锰锌可湿性粉剂1000倍液、40%多·硫悬浮剂500倍液、50%复方硫菌灵可湿性粉剂800倍液，隔10天左右1次，连续防治1～2次。');
INSERT INTO `articles` VALUES (31, '玉米叶斑病', 12, 'para', '\n[2-3] \n');
INSERT INTO `articles` VALUES (32, '斑枯病', 0, 'title', '斑枯病');
INSERT INTO `articles` VALUES (32, '斑枯病', 0, 'summary', '属半知菌亚门，病原菌有两种，分别为杨生小球壳菌和杨小球壳菌。主要危害毛白杨及部分黑杨品系。分布于华北、西北及豫、苏等地区。');
INSERT INTO `articles` VALUES (32, '斑枯病', 1, 'level-2', '危害症状');
INSERT INTO `articles` VALUES (32, '斑枯病', 2, 'para', '病菌主要浸染叶片。其病斑特点因寄主和病菌种类的不同而有区别。一般最初在叶正面出现褐色圆形小斑，后渐扩大为多角形大斑，为灰白色或浅褐色，边缘深褐色，斑内散生或轮生许多小黑点，为病菌的分生孢子器。叶背面有毛的叶片，病斑不明显，在叶背面无毛的叶片上，背面也有病斑和小黑点。一个病叶上可生数十个小斑，互相连接后，叶片变黄，干枯脱落。斑枯病 -   发生规律    病菌以分生孢子器在病落叶内越冬，翌年4〜5月放出分生孢子，借风传播，浸染幼叶，有再浸染发生。海河平原多在6月下旬开始发病，发病规律为自下而上，先从下部叶片发病，逐渐向上蔓延，7〜9月为盛期，9月病叶开始脱落。幼树发病较晚，10月开始落叶。夏秋多雨，高温，幼林郁闭过早，有利于病害蔓延。　　斑枯病 -   防治方法    1.选择抗病品系造林。　　2.后清扫烧毁或深埋。　　3.8月份的发病初、中期喷洒1：2：200倍波尔多液或65%代森锌400~500倍液。每半月喷洒一次，共喷2〜3次即可控制病情。');
INSERT INTO `articles` VALUES (32, '斑枯病', 3, 'level-2', '发生规律');
INSERT INTO `articles` VALUES (32, '斑枯病', 4, 'para', '郁闭过早，有利于病害蔓延。');
INSERT INTO `articles` VALUES (32, '斑枯病', 5, 'level-2', '防治方法');
INSERT INTO `articles` VALUES (32, '斑枯病', 6, 'para', '1.选择抗病品系造林。　　2.后清扫烧毁或深埋。　　3.8月份的发病初、中期喷洒1：2：200倍波尔多液或65%代森锌400~500倍液。每半月喷洒一次，共喷2〜3次即可控制病情。');
INSERT INTO `articles` VALUES (33, '锈病', 0, 'title', '锈病');
INSERT INTO `articles` VALUES (33, '锈病', 0, 'summary', '由4,00 0种以上真菌引致的几千种重要经济植物和杂草的病害。');
INSERT INTO `articles` VALUES (33, '锈病', 1, 'level-2', '简介');
INSERT INTO `articles` VALUES (33, '锈病', 2, 'para', '由真菌中的锈菌寄生引起的一类植物病害。危害植物的叶、茎和果实。锈菌一般只引起局部侵染，受害部位可因孢子积集而产生不同颜色的小疱点或疱状、杯状、毛状物，有的还可在枝干上引起肿瘤、粗皮、丛枝、曲枝等症状，或造成落叶、焦梢、生长不良等。严重时孢子堆密集成片，植株因体内水分大量蒸发而迅速枯死。');
INSERT INTO `articles` VALUES (33, '锈病', 3, 'para', '锈病分布广且危害性大，多见于禾谷类作物、豆科植物和梨等。不少作物的锈病是世界性的，有些有大区流行的特点,产量损失常以万吨计。中国在20世纪60～70年代，小麦叶锈病(Puccinia reconditavar.tritici)曾大面积流行;小麦条锈病(P.striiformis)等也常有为害。\n\n\n\n\n小麦锈病\n\n');
INSERT INTO `articles` VALUES (33, '锈病', 4, 'level-2', '生物特性');
INSERT INTO `articles` VALUES (33, '锈病', 5, 'para', '锈菌具有形态上的多型性、生理上的专化性和变异性等特点，并有转主寄生、夏孢子远距离传播等现象，其生活史在真菌中是最为复杂的。锈菌的多型性指锈菌有多种不同类型的孢子。典型的产生5种，即性孢子、锈孢子、夏孢子、冬孢子和担孢子。由它们引起植物受害部产生的小疱点,有的为黄色至铁锈色(夏孢子堆)，有的为黑色（冬孢子堆）,有的为白色或黄色(性孢子器)，有的则为黄色的疱状、杯状或毛状物（锈孢子器）。这5种孢子发生在同一寄主植物上的称同宗寄主,如菜豆锈病菌。5种孢子发生在分类地位极不相近的两种植物上才能完成生活史的称转主寄主，如小麦秆锈病菌夏孢子和冬孢子发生在小麦上，性孢子和锈孢子发生在小檗上。不典型的锈菌生活史内缺少一二种类型的孢子，但也有同宗寄主与转主寄主之别，如梨锈病菌无夏孢子，冬孢子发生在桧柏上，性孢子及锈孢子发生在梨上。');
INSERT INTO `articles` VALUES (33, '锈病', 6, 'para', '锈菌的专化性和变异性表现在同一种锈菌对不同属的植物有不同的致病性，并可因此而分为不同的专化型。如为害禾谷类作物的禾柄锈菌,至少有9个专化型；为害小麦的是其中一个专化型。各种专化型又根据对同一种作物不同品种的致病性，分为不同的生理小种。为害小麦的禾柄锈菌已经鉴定出300多个生理小种。');
INSERT INTO `articles` VALUES (33, '锈病', 7, 'level-2', '传播规律');
INSERT INTO `articles` VALUES (33, '锈病', 8, 'para', '有些锈菌的夏孢子可借气流远距离传播，这在锈病循环上有重要作用。通常锈菌以冬孢子越冬，初次侵染来源是担孢子或夏孢子，而后以锈孢子或夏孢子在作物生长期间靠气流传播继续为害。在亚热带和热带，不少锈菌不产生冬孢子，靠夏孢子或由病菌直接在自生苗或冬季作物上越冬。在有些地区，转主寄主在病害循环中不起作用，如中国东北的阿穆耳小檗展叶期的气候不适宜小麦秆锈病菌冬孢子的萌发，而夏孢子虽在当地不能越冬，都可由东南沿海地区借气流长距离从南向北吹来成为初次侵染源。此外，有些锈菌侵染循环中的主要问题不是越冬而是越夏，如小麦条锈病菌不耐高温，须在高寒地区的自生麦苗和不同生育期的麦株上越夏。');
INSERT INTO `articles` VALUES (33, '锈病', 9, 'para', '锈病防治：从花后开始，约十几天喷一次150至200倍液高脂膜（乳剂）。');
INSERT INTO `articles` VALUES (34, '叶枯病', 0, 'title', '叶枯病');
INSERT INTO `articles` VALUES (34, '叶枯病', 0, 'summary', '叶枯病多从叶缘、叶尖侵染发生，病斑由小到大不规则状，红褐色至灰褐色，病斑连片成大枯斑，干枯面积达叶片的1/3-1/2，病斑边缘有一较病斑深的带；病健界限明显。常见于植物之中，该病在7-10月份均可发生。植株下部叶片发病重。高温多湿、通风不良均有利于病害的发生。植株生长势弱的发病较严重。');
INSERT INTO `articles` VALUES (34, '叶枯病', 1, 'level-2', '症状');
INSERT INTO `articles` VALUES (34, '叶枯病', 2, 'para', '后期在病斑上产生一些黑色小粒点。如桂花叶枯病（亦称枯斑病）、腊梅叶枯病、翠菊叶枯病（亦称斑枯病）。病叶初期先变黄，黄色部分逐渐变褐色坏死。由局部扩展到整个叶脉，呈现褐色至红褐色的叶缘病斑，病斑边缘波状，颜色较深。病键交界明显，其外缘有时还有宽窄不等的黄色浅带，随后，病斑逐渐向叶基部延伸，直至整个叶片变为褐色至灰褐色。随后在病叶背面或正面出现黑色绒毛状物或黑色小点。\n\n\n\n\n叶枯病\n\n');
INSERT INTO `articles` VALUES (34, '叶枯病', 3, 'level-2', '病原');
INSERT INTO `articles` VALUES (34, '叶枯病', 4, 'para', '该病的病原菌有三种：属于半知菌亚门的Alternaria alternata；属于子囊菌亚门的Glomerella cingulata；属于半知菌亚门的Pestalotia ginkgo病原菌以菌丝体与孢子在病落叶等处越冬。');
INSERT INTO `articles` VALUES (34, '叶枯病', 5, 'level-2', '发病规律');
INSERT INTO `articles` VALUES (34, '叶枯病', 6, 'para', '叶枯病在病叶上越冬，翌年在温度适宜时，病菌的孢子借风、雨传播到寄主植物上发生侵染。该病在7-10月份均可发生。植株下部叶片发病重。高温多湿、通风不良均有利于病害的发生。植株生长势弱的发病较严重。');
INSERT INTO `articles` VALUES (34, '叶枯病', 7, 'level-2', '防治方法');
INSERT INTO `articles` VALUES (34, '叶枯病', 8, 'para', '①秋季彻底清除病落叶，并集中烧毁，减少翌年的侵染来源。 　　②加强栽培管理，控制病害的发生。栽植地要排水良好，土壤肥沃，增施有机肥料 　　及磷、钾肥。控制栽植密度，使其通风透光，降低叶面湿度，减少侵染机会。改喷浇为 　　滴灌或流水浇灌，减少病菌的传播。 　　③生长季节在发病严重的区域，从6月下旬发病初期到10月间，每隔10天左右喷1次 　　药，连喷几次可有效的予以防治。常用药剂有1：1：100倍的波尔多液、50%托布津500- 　　800倍液、50%多菌灵可湿性粉剂1000倍（或40%胶悬剂600-800倍）、50%苯莱特1000-15 　　00倍、65%代森锌500倍液等，可供选用或交替使用。');
INSERT INTO `articles` VALUES (35, '番茄黄化曲叶病毒病', 0, 'title', '番茄黄化曲叶病毒病');
INSERT INTO `articles` VALUES (35, '番茄黄化曲叶病毒病', 0, 'summary', '番茄黄化曲叶病毒病为中国番茄黄化曲叶病毒(Tomato yellow leaf curl virus，TYLCV)，属于双生病毒科。');
INSERT INTO `articles` VALUES (35, '番茄黄化曲叶病毒病', 1, 'level-2', '病原');
INSERT INTO `articles` VALUES (35, '番茄黄化曲叶病毒病', 2, 'para', '该病毒为中国番茄黄化曲叶病毒(Tomato yellow leaf curl virus，TYLCV)，属于双生病毒科（Geminiviridae）\n\n\n\n\n发病症状图片(4张)\n\n\n\n\n\n\n');
INSERT INTO `articles` VALUES (35, '番茄黄化曲叶病毒病', 3, 'para', '菜豆金色花叶病毒属（Begomovirus）病毒，因该属病毒在自然条件下只能由烟粉虱（Bemisia tabaci）以持久方式传播，又被称为粉虱传双生病毒，是一类具有孪生颗粒形态的植物DNA 病毒，广泛分布于热带和亚热带地区，在烟草、番茄、南瓜、木薯、棉花等重要经济作物上造成毁灭性危害。');
INSERT INTO `articles` VALUES (35, '番茄黄化曲叶病毒病', 4, 'level-2', '症状');
INSERT INTO `articles` VALUES (35, '番茄黄化曲叶病毒病', 5, 'para', '染病番茄植株矮化，生长缓慢或停滞，顶部叶片常稍褪绿发黄、变小，叶片边缘上卷，叶片增厚，叶质变硬，叶背面叶脉常显紫色。生长发育早期染病植株严重矮缩，无法正常开花结果；生长发育后期染病植株仅上部叶和新芽表现症状，结果数减少，果实变小，成熟期果实着色不均匀(红不透)，基本失去商品价值。');
INSERT INTO `articles` VALUES (35, '番茄黄化曲叶病毒病', 6, 'level-2', '发病特点');
INSERT INTO `articles` VALUES (35, '番茄黄化曲叶病毒病', 7, 'para', '田间操作如定植、整枝、打杈、绑蔓等通过磨擦将病株毒源传给健侏；蚜虫的迁飞和危害也是重要传毒途径。一般低温时，病毒病不表现症状或症状很轻，随气温升高，一般在20℃左右即表现花叶和蕨叶症状。');
INSERT INTO `articles` VALUES (35, '番茄黄化曲叶病毒病', 8, 'level-2', '防治措施');
INSERT INTO `articles` VALUES (35, '番茄黄化曲叶病毒病', 9, 'para', '1.培育无病无虫苗是关键。该病对番茄植株侵害越早，发病率越高，所以预防要从育苗期抓起，做到早防早控，力争少发病或不发病。苗床周围杂草要除干净，苗床土壤要进行消毒处理，以减少病源。苗床用黄化曲叶病毒灵B灌根剂3000倍液喷后整地，并使用40～60目防虫网覆盖。在苗期2-3片叶开始5天一次连续喷施3次黄化曲叶病毒疫苗预防。并用黄化曲叶病毒灵B2000倍液在分苗时和定植前灌苗床2次 。');
INSERT INTO `articles` VALUES (35, '番茄黄化曲叶病毒病', 10, 'para', '2.农业措施。定植时用黄化曲叶病毒灵B2000-3000倍液浇穴水，缓苗后用黄化曲叶病毒灵A1袋1桶水3-4天一次喷施。连喷4次。适当控制氮肥用量和保持田间湿润。施肥灌水做到少量多次，做到不旱不涝，适时放风，避免棚内高温，调节好田间温湿度，增施有机肥，促进植株生长健壮，提高植株的抗病能力，1-2穗果时可再喷施1-2次番茄黄化曲叶病毒灵，及时清除田间杂草和残枝落叶，以减少虫源。大棚风口用40～60目防虫网隔离，配合田间吊黄板预防烟粉虱。');
INSERT INTO `articles` VALUES (35, '番茄黄化曲叶病毒病', 11, 'para', '3、治疗：如前期没有预防感染上了病毒，请立即用黄化曲叶病毒灵B2000-3000倍液灌根，3-4天喷1次黄化曲叶病毒灵A或黄化曲叶病毒疫苗，连喷4-5次。如不想灌根可用黄化曲叶病毒灵B冲施，亩用1000-1500毫升。在治疗期间停止用生长素及控旺药物及普通病毒药物。');
INSERT INTO `articles` VALUES (35, '番茄黄化曲叶病毒病', 12, 'para', '一般浇水后的3～4天用药最好，1～2次用药可控制病害扩散，3～4次用药初发病株可恢复，对生长点已经停止生长的就没有治疗意义了。如果已经发病必须灌根和喷施同时进行，效果快，恢复好。');
INSERT INTO `articles` VALUES (35, '番茄黄化曲叶病毒病', 13, 'para', '治疗效果：1、苗期只接种疫苗3次，抗病效果可达90%以上。');
INSERT INTO `articles` VALUES (35, '番茄黄化曲叶病毒病', 14, 'para', '2、苗期接种疫苗+定植灌根+缓苗后喷施黄化曲叶病毒灵4次，抗病效果可达96%以上。');
INSERT INTO `articles` VALUES (35, '番茄黄化曲叶病毒病', 15, 'para', '3、苗期接种疫苗+定植灌根+缓苗后喷施黄化曲叶病毒灵4次+1-2穗果加强喷2次黄化曲叶病毒灵，抗病效果可达99%以上。');
INSERT INTO `articles` VALUES (35, '番茄黄化曲叶病毒病', 16, 'para', '治疗黄化曲叶病毒病应该注意的问题：');
INSERT INTO `articles` VALUES (35, '番茄黄化曲叶病毒病', 17, 'para', '1、 灌根效果比冲施效果好，灌根剂1瓶500毫升灌2000-2500株最好，用注射器抽灌根剂6-7毫升兑30斤水灌30株，冲施亩用2-3瓶。');
INSERT INTO `articles` VALUES (35, '番茄黄化曲叶病毒病', 18, 'para', '2、 喷施黄化曲叶病毒灵或疫苗最好不要在高温时进行，早晚施药效果最佳，配药时最好2次稀释，上下要喷均匀，一般开花前亩用30-45斤水，开花后1亩45-60斤水，1-2穗果时60-75斤水，2-3穗果时90-120斤水，3穗果以上时120-150斤水。1袋药配30斤水。和农用有机硅助剂配合使用效果好。');
INSERT INTO `articles` VALUES (35, '番茄黄化曲叶病毒病', 19, 'para', '3、 边治边方生的原因：（一）可能和其它含激素的叶面肥或普通抗病毒病的药剂混用或交替使用了，喷花时激素浓度过大。（二）可能是药量小，喷施不均匀。（三）没有按时用药。');
INSERT INTO `articles` VALUES (35, '番茄黄化曲叶病毒病', 20, 'para', '4、 黄化曲叶病毒病的治疗效果主要取决于正确的使用方法及亩用量，药量小及喷施不均匀达不到满意的治疗效果。');
INSERT INTO `articles` VALUES (35, '番茄黄化曲叶病毒病', 21, 'level-2', '防治方法');
INSERT INTO `articles` VALUES (35, '番茄黄化曲叶病毒病', 22, 'para', '预防方案：');
INSERT INTO `articles` VALUES (35, '番茄黄化曲叶病毒病', 23, 'para', '一、培育无病无虫苗是关键。该病对番茄植株侵害越早，发病率越高，所以预防要从育苗期抓起，做到早防早控，力争少发病或不发病。苗床周围杂草要除干净，苗床土壤要进行消毒处理，以减少病源。');
INSERT INTO `articles` VALUES (35, '番茄黄化曲叶病毒病', 24, 'para', '二、重防烟粉虱。番茄黄化曲叶病毒病由烟粉虱传播。烟粉虱在田间有迁飞性。应加强整枝打权和化学防治等田间统一管理，减少相临田块之间的烟粉虱迁飞。');
INSERT INTO `articles` VALUES (35, '番茄黄化曲叶病毒病', 25, 'para', '三、农业措施。在栽培上适当控制氮肥用量和保持田间湿润。施肥灌水做到少量多次，做到不旱不涝，适时放风，避免棚内高温，调节好田间温湿度，增施有机肥，促进植株生长健壮，提高植株的抗病能力，及时清除田间杂草和残枝落叶，以减少虫源。注意田间管理防治接触传染，在绑蔓、整枝、打杈、粘花和摘果等操作时，应先处理健株，后处理病株，注意手和工具要用肥皂水充分擦洗，减少人为的传播，发现病株及时清除，减少病毒源。');
INSERT INTO `articles` VALUES (35, '番茄黄化曲叶病毒病', 26, 'para', '四、实行轮作换茬。发病严重地块要与茄科以外的其他作物实行三年以上的轮作。如换茬种植黄瓜、豆角、葡萄等，避免间套作和连作，减少和避免番茄病毒病土壤和残留物的传毒，减轻病毒病的发生；育苗地和栽植棚地应彻底清除带毒杂草，减少病毒病的毒源。');
INSERT INTO `articles` VALUES (36, '马铃薯晚疫病', 0, 'title', '马铃薯晚疫病');
INSERT INTO `articles` VALUES (36, '马铃薯晚疫病', 0, 'summary', '马铃薯晚疫病（Potato Late Blight）由致病疫霉引起，导致马铃薯茎叶死亡和块茎腐烂的一种毁灭性卵菌病害。叶片染病先在叶尖或叶缘生水浸状绿褐色斑点，病斑周围具浅绿色晕圈，湿度大时病斑迅速扩大，呈褐色，并产生一圈白霉，即孢囊梗和孢子囊，尤以叶背最为明显；干燥时病斑变褐干枯，质脆易裂，不见白霉，且扩展速度减慢。茎部或叶柄染病现褐色条斑。发病严重的叶片萎垂、卷缩，终致全株黑腐，全田一片枯焦，散发出腐败气味。块茎染病初生褐色或紫褐色大块病斑，稍凹陷，病部皮下薯肉亦呈褐色，慢慢向四周扩大或烂掉。');
INSERT INTO `articles` VALUES (36, '马铃薯晚疫病', 1, 'level-2', '形态特征');
INSERT INTO `articles` VALUES (36, '马铃薯晚疫病', 2, 'para', '\n\n\n\n\n马铃薯晚疫病\n\nPhytophthora infestans (Mont.)de Bary称致病疫霉属鞭毛茵亚门真菌。孢囊梗分枝，每隔一段着生孢子囊处具膨大的节。孢子囊柠檬形，大小2～38×12～23（um），一端具乳突，另端有小柄，易脱落，在水中释放出5～9个肾形游动孢子。游动孢子具鞭毛2根，失去鞭毛后变成休止孢子，萌发出芽管，又生穿透钉侵入到寄主体内。菌丝生长适温20～23℃，孢子囊形成适温19～22℃，10～13℃形成游动孢子，温度高于24℃，孢子囊多直接萌发，孢子囊形成要求相对湿度高。');
INSERT INTO `articles` VALUES (36, '马铃薯晚疫病', 3, 'para', '\n\n\n\n\n马铃薯晚疫病(2张)\n\n\n\n\n\n\n');
INSERT INTO `articles` VALUES (36, '马铃薯晚疫病', 4, 'level-2', '发病条件');
INSERT INTO `articles` VALUES (36, '马铃薯晚疫病', 5, 'para', '病菌喜日暖夜凉高湿条件，相对湿度95%以上、18～22℃条件下，有利于孢子囊的形成，冷凉（10～13℃，保持1～2小时）又有水滴存在，有利于孢子囊萌发产生游动孢子，温暖（24～25℃，持续5～8小时）有水滴存在，利于孢子囊直接产出芽管。因此多雨年份，空气潮湿或温暖多雾条件下发病重。种植感病品种，植株又处于开花阶段，只要出现白天22℃左右，相对湿度高于95%持续8小时以上，夜间10～13℃，叶上有水滴持续11～14小时的高湿条件，本病即可发生，发病后10～14天病害蔓延全田或引起大流行。');
INSERT INTO `articles` VALUES (36, '马铃薯晚疫病', 6, 'level-2', '传播途径');
INSERT INTO `articles` VALUES (36, '马铃薯晚疫病', 7, 'para', '病菌主要以菌丝体在薯块中越冬。播种带菌薯块，导致不发芽或发芽后出土即死去，有的出土后成为中心病株，病部产生孢子囊借气流传播进行再侵染，形成发病中心，致该病由点到面，迅速蔓延扩大。病叶上的孢子囊还可随雨水或灌溉水渗入土中侵染薯块，形成病薯，成为翌年主要侵染源。\n[1] \n');
INSERT INTO `articles` VALUES (36, '马铃薯晚疫病', 8, 'level-2', '病害分布');
INSERT INTO `articles` VALUES (36, '马铃薯晚疫病', 9, 'para', '世界各地马铃薯产区都有发生，流行年一般减产30%。19世纪40年代爱尔兰马铃薯大量死亡，减产一半，使\n\n\n\n\n发病症状图片(2张)\n\n\n\n\n\n\n100多万人饿死，200万人移居海外。当时对马铃薯死亡的原因有各种推测，1842年冯·马蒂尤斯（vonMartius）首先认为是病菌引起，1857年斯皮尔许奈德（Speerschneider）证明叶上霉菌能引起块茎腐烂。1861～1863年德巴利（deBary）确定了叶上病斑和块茎腐烂都是由一种真菌引起并给鉴定了病原菌。在中国马铃薯产地都有发生，西南地区较为严重，东北、华北与西北多雨潮湿的年份为害较重，如1950年大流行年，这些地区损失30%～50%。以后的10年内又有5年是流行年。 \n[1] \n');
INSERT INTO `articles` VALUES (36, '马铃薯晚疫病', 10, 'level-2', '防治方法');
INSERT INTO `articles` VALUES (36, '马铃薯晚疫病', 11, 'level-3', '选用无病种薯');
INSERT INTO `articles` VALUES (36, '马铃薯晚疫病', 12, 'para', '减少初侵染源。做到秋收入窖、冬藏查窖、出窖、切块、春化等过程中，每次都要严格剔除病薯，有条件的要建立无病留种地，进行无病留种。');
INSERT INTO `articles` VALUES (36, '马铃薯晚疫病', 13, 'level-3', '加强栽培管理');
INSERT INTO `articles` VALUES (36, '马铃薯晚疫病', 14, 'para', '适期早播，选土质疏松、排水良好的田块栽植，降低田间湿度，促进植株健壮生长，增强抗病力。');
INSERT INTO `articles` VALUES (36, '马铃薯晚疫病', 15, 'level-3', '轮作换茬');
INSERT INTO `articles` VALUES (36, '马铃薯晚疫病', 16, 'para', '防止连作，防止与茄科作物连作，或临近种植。应与十字花科蔬菜实行3年以上轮作，避免和马铃薯相邻种植；');
INSERT INTO `articles` VALUES (36, '马铃薯晚疫病', 17, 'level-3', '加强田间管理');
INSERT INTO `articles` VALUES (36, '马铃薯晚疫病', 18, 'para', '施足基肥，实行配方施肥，避免偏施氮肥，增施磷、钾肥。定植后要及时防除杂草，根据不同品种结果习性，合理整枝、摘心、打杈，减少养分消耗，促进主茎的生长；');
INSERT INTO `articles` VALUES (36, '马铃薯晚疫病', 19, 'level-3', '合理密植');
INSERT INTO `articles` VALUES (36, '马铃薯晚疫病', 20, 'para', '根据不同品种生育期长短、结果习性，采用不同的密植方式，如：双秆整枝的每亩，栽2000株左右，单秆整枝的每亩栽2500～3500株，合理密植，可改善田间通风透光条件，降低田间湿度，减轻病害的发生；');
INSERT INTO `articles` VALUES (36, '马铃薯晚疫病', 21, 'level-3', '施行种薯消毒');
INSERT INTO `articles` VALUES (36, '马铃薯晚疫病', 22, 'para', '①切种薯块的工具必须用75%酒精或200倍漂白粉、0.1%—0.2%高锰酸钾溶液浸10分钟。②可用58%甲霜灵·锰锌可湿性粉剂50克加70%甲基硫菌灵可湿性粉剂100克加72%农用链霉素20—30克，与10—15克滑石粉充分搅拌，均匀拌在100公斤略干的种薯块上，不仅可防治晚疫病，还可兼治茎基腐病、早疫病、立枯丝核菌病、黑胫病、软腐病和环腐病。③可用72.2%普力克(霜霉威)水剂600倍液或72%霜脲·锰锌（克露、奔路）可湿性粉剂600倍液，浸泡种薯10—15分钟后晾干播种。');
INSERT INTO `articles` VALUES (36, '马铃薯晚疫病', 23, 'level-3', '发病初期药剂防治');
INSERT INTO `articles` VALUES (36, '马铃薯晚疫病', 24, 'para', '可选用药剂有①80%代森锰锌（大生m—45）600—800倍液。②58%甲霜灵·锰锌500倍液。③64%杀毒矾500倍液。④60%琥·乙磷铝500倍液。⑤50%甲霜铜700—800倍液。⑥50%氟啶胺（福帅得）2000—2500倍液。⑦10%氰霜唑（科佳）2000—2500倍液。以上药剂连续打2—3次，每次间隔7—10天。\n[1] \n');
